/* View: LEVERANDOROMS */
CREATE VIEW LEVERANDOROMS(
    V509INDEX,
    V509INDEXUNIKT,
    NAVN,
    AFDELING_ID,
    MDR,
    AAR,
    ANTAL,
    SALGSPRIS,
    MOMS,
    KOSTPRIS,
    RABAT,
    RABATMOMS,
    DAEKNING,
    SALGUMOMS,
    LANTAL,
    LSALGSPRIS,
    LKOSTPRIS,
    LMOMS,
    KANTAL,
    KSALGSPRIS,
    KMOMS,
    KKOSTPRIS,
    DG)
AS
SELECT
    LEVERANDOERER.V509INDEX,
    LEVERANDOERER.V509INDEXUNIKT,
    LEVERANDOR_OMSETNING.NAVN,
    LEVERANDOR_OMSETNING.AFDELING_ID,
    LEVERANDOR_OMSETNING.MDR,
    LEVERANDOR_OMSETNING.AAR,
    SUM(LEVERANDOR_OMSETNING.ANTAL) AS ANTAL,
    SUM(LEVERANDOR_OMSETNING.SALGSPRIS) AS SALGSPRIS,
    SUM(LEVERANDOR_OMSETNING.MOMS) AS MOMS,
    SUM(LEVERANDOR_OMSETNING.KOSTPRIS) AS KOSTPRIS,
    SUM(LEVERANDOR_OMSETNING.RABAT) AS RABAT,
    SUM(LEVERANDOR_OMSETNING.RABATMOMS) AS RABATMOMS,
    SUM(LEVERANDOR_OMSETNING.SALGSPRIS - LEVERANDOR_OMSETNING.MOMS - LEVERANDOR_OMSETNING.KOSTPRIS),
    SUM(LEVERANDOR_OMSETNING.SALGSPRIS - LEVERANDOR_OMSETNING.MOMS),
    SUM(LEVERANDOR_OMSETNING.LANTAL),
    SUM(LEVERANDOR_OMSETNING.LSALGSPRIS),
    SUM(LEVERANDOR_OMSETNING.LKOSTPRIS),
    SUM(LEVERANDOR_OMSETNING.LMOMS),
    SUM(LEVERANDOR_OMSETNING.KANTAL),
    SUM(LEVERANDOR_OMSETNING.KSALGSPRIS),
    SUM(LEVERANDOR_OMSETNING.KMOMS),
    SUM(LEVERANDOR_OMSETNING.KKOSTPRIS),
    (SELECT MAX(DB) FROM BEREGNDG(SUM(LEVERANDOR_OMSETNING.SALGSPRIS),SUM(LEVERANDOR_OMSETNING.KOSTPRIS),SUM(LEVERANDOR_OMSETNING.MOMS))) AS TT
    FROM  LEVERANDOERER LEFT JOIN LEVERANDOR_OMSETNING ON LEVERANDOR_OMSETNING.NAVN = LEVERANDOERER.NAVN
    GROUP BY
        LEVERANDOERER.V509INDEX,
        LEVERANDOERER.V509INDEXUNIKT,
        LEVERANDOR_OMSETNING.NAVN,
        LEVERANDOR_OMSETNING.AFDELING_ID,
        LEVERANDOR_OMSETNING.MDR,
        LEVERANDOR_OMSETNING.AAR
;



