/* View: V_ELEKTRONISKEGAVEKORT_POST */
CREATE VIEW V_ELEKTRONISKEGAVEKORT_POST(
    GAVEKORTSNUMMER,
    GD_DATE,
    KOEB,
    SALG,
    GD_STATUS,
    FORBRUGAFDELING,
    FORBRUGMASKINE,
    OPRETAFDELING,
    OPRETMASKINE,
    GV_CREATED,
    GV_MODIFIED,
    GV_CLOSED,
    FORBRUGAFDNAVN,
    FORBRUGAFDINFO,
    FORBRUGAFDNAVNINFO,
    OPRETAFDNAVN,
    OPRETAFDINFO,
    OPRETAFDNAVNINFO,
    BONNR)
AS
Select 
  GIFTCARD.GIFTCARD_ID, 
  GIFTCARD_DETAIL.GCD_DATE, 
  CASE WHEN (GIFTCARD_DETAIL.GCD_STATUS = 500) THEN GIFTCARD_DETAIL.GCD_VALUE*-1 ELSE 0 END, 
  CASE WHEN (GIFTCARD_DETAIL.GCD_STATUS = 500) THEN 0 ELSE GIFTCARD_DETAIL.GCD_VALUE END, 
  GIFTCARD_DETAIL.GCD_STATUS, 
  CASE WHEN (GIFTCARD_DETAIL.GCD_STATUS = 500) THEN DEPARTMENT.DEPARTMENT_ID ELSE GIFTCARD_DETAIL.AFDNR END, 
  CASE WHEN (GIFTCARD_DETAIL.GCD_STATUS = 500) THEN MACHINES.MACHINE_ID ELSE GIFTCARD_DETAIL.MASKINE END, 
  DEPARTMENT.DEPARTMENT_ID, 
  MACHINES.MACHINE_ID, 
  GIFTCARD.GC_CREATED, 
  GIFTCARD.GC_MODIFIED, 
  GIFTCARD.GC_CLOSED, 
A1.NAVN as ForbrugAfdNavn, 
  A1.AFDINFO as ForbrugAfdInfo, 
  A1.NAVN || ' (' || A1.AFDINFO || ')' as ForbrugAfdNavnInfo, 
  A2.NAVN as OpretAfdNavn, 
  A2.AFDINFO as OpretAfdInfo, 
  A2.NAVN || ' (' || A2.AFDINFO || ')' as OpretAfdNavnInfo, 
  GIFTCARD_DETAIL.GCD_POSNUMBER 
from GIFTCARD 
  inner join GIFTCARD_DETAIL on (GIFTCARD_DETAIL.GIFTCARD_ID = GIFTCARD.ID) 
  inner join MACHINES on (GIFTCARD.MACHINE_ID = MACHINES.ID) 
  inner join DEPARTMENT on (MACHINES.DEPARMENT_ID = DEPARTMENT.ID) 
  LEFT JOIN AFDELING A1 ON A1.AFDELINGSNUMMER = DEPARTMENT.DEPARTMENT_ID 
  LEFT JOIN AFDELING A2 ON A2.AFDELINGSNUMMER = GIFTCARD_DETAIL.AFDNR
;



