      SUM(VAREGRUPPER_DETAIL.LAGERKR) AS LAGERKR,
      SUM(LAGERKR_MOMS) AS LAGERKR_MOMS,
      SUM(VARERGRUPPER_OMSETNING.ANTAL) AS ANTAL,
      SUM(VARERGRUPPER_OMSETNING.SALGSPRIS) AS SALGSPRIS,
      SUM(VARERGRUPPER_OMSETNING.MOMS) AS MOMS,
      SUM(VARERGRUPPER_OMSETNING.KOSTPRIS) AS KOSTPRIS,
      SUM(VARERGRUPPER_OMSETNING.RABAT) AS RABAT,
      SUM(VARERGRUPPER_OMSETNING.RABATMOMS) AS RABATMOMS,
      SUM(VARERGRUPPER_OMSETNING.SALGSPRIS - VARERGRUPPER_OMSETNING.MOMS - VARERGRUPPER_OMSETNING.KOSTPRIS),
      SUM(VARERGRUPPER_OMSETNING.SALGSPRIS - VARERGRUPPER_OMSETNING.MOMS)
FROM
  VAREGRUPPER_DETAIL LEFT OUTER JOIN
        VARERGRUPPER_OMSETNING ON
          VAREGRUPPER_DETAIL.AFDELING_ID = VARERGRUPPER_OMSETNING.AFDELING_ID AND
          VAREGRUPPER_DETAIL.NAVN = VARERGRUPPER_OMSETNING.NAVN
          left OUTER JOIN
            VAREGRUPPER ON VARERGRUPPER_OMSETNING.NAVN = VAREGRUPPER.NAVN

GROUP BY
  VAREGRUPPER.V509INDEX,
  VAREGRUPPER.V509INDEXUNIKT,
  VAREGRUPPER.HOVEDGRUPPE,
  VAREGRUPPER.GRUPPEREF,
  VARERGRUPPER_OMSETNING.NAVN,
  VARERGRUPPER_OMSETNING.AFDELING_ID,
  VARERGRUPPER_OMSETNING.MDR,
  VARERGRUPPER_OMSETNING.AAR
;



