/* Trigger: USER_LOGIN */
CREATE OR ALTER TRIGGER USER_LOGIN
ACTIVE ON CONNECT POSITION 10
AS 
declare variable lAfdnr Varchar(30); 
declare variable lMaskine Varchar(30); 
declare variable OvfUser varchar(30); 
declare variable lProgram varchar(10); 
declare variable lUser varchar(10); 
begin 
  /*Initiate variables*/ 
  lAfdnr = ''; 
  lMaskine = ''; 
  lProgram = ''; 
  lUser = ''; 
 
  /*Set current user*/ 
  OvfUser  = User; 
  if (CHAR_LENGTH(OvfUser)>10) then 
  begin 
    /*Length of username is 10 or more*/ 
    if ((SUBSTRING(OvfUser from 1 for 10)='OCCKONTOR_') or (SUBSTRING(OvfUser from 1 for 10)='OCCFINANS_')) then 
    begin 
      /*User is either EasyPOS Kontor or EasyPOS Finans*/ 
      /*Set program*/ 
      if (SUBSTRING(OvfUser from 1 for 10)='OCCKONTOR_') then 
      begin 
        lProgram = 'EPK'; 
      end 
      else 
      begin 
        lProgram = 'EPF'; 
      end 
      /*Set current EasyPOS user*/ 
      lUser      = SUBSTRING(OvfUser from 11 for (CHAR_LENGTH(OvfUser))); 
    end 
 
    if (SUBSTRING(OvfUser from 1 for 8)='OCCSALG_') then 
    begin 
      /*Username belongs to EasyPOS Salg*/ 
      /*Set program*/ 
      lProgram = 'EPS'; 
      /*Set current EasyPOS User*/ 
      lUser    = SUBSTRING(OvfUser from 9 for (CHAR_LENGTH(OvfUser))); 
    end 
  end 
 
 
  if ((lProgram<>'') AND (lUser<>'')) then 
  begin 
    /*A usedr and a program has been set*/ 
    lAfdNr    = ''; 
    lMaskine  = ''; 
 
    /*Henter afdeling og maskine */ 
    Select NAVN, AFDELING_ID from UnderAfdeling where Navn=:lUser Into :lMaskine, :lAfdNr; 
 
    /*Laver post til program og maskine, hvis den ikke findes */ 
    if (not exists (select ID from UserCount where Program=:lProgram and Afdeling_ID=:lAfdNr and UAfd=:lMaskine)) then 
    begin 
      Insert Into UserCount (Afdeling_ID, UAfd, Bruger, Forbindelse, Program) 
        Values (:lAfdNr, :lMaskine, User, Current_Connection, :lProgram); 
    end 
 
    Update UserCount set 
      LoginDato = 'NOW', 
      Aktiv = 1 
    Where 
      Afdeling_ID = :lAfdNr and 
      UAfd = :lMaskine and 
      Program = :lProgram; 
  end 
end^

