            New.PROVISION_VAL      = New.PROVISION / lRappValuta * lStdVal;
            New.PROV_TOTALT_Val    = New.PROV_TOTALT / lRappValuta * lStdVal;
            New.PROV_TOTALTMOMS_Val= New.PROV_TOTALTMOMS / lRappValuta * lStdVal;
        end
        else
        begin
            New.Salgspris_Val      = New.Salgspris;
            New.Moms_Val           = New.Moms;
            New.Kostpris_Val       = New.Kostpris;
            New.Rabat_Val          = New.Rabat;
            New.RabatMoms_Val      = New.RabatMoms;
            New.kSALGSPRIS_VAL     = New.kSALGSPRIS;
