/* Trigger: KUNDETELLER_SALGSTAL2 */
CREATE OR ALTER TRIGGER KUNDETELLER_SALGSTAL2 FOR KUNDETELLER
ACTIVE BEFORE UPDATE POSITION 55
AS
declare variable lStk numeric(18,2); 
declare variable lKpr numeric(18,2); 
declare variable lSpr numeric(18,2); 
declare variable lMoms numeric(18,2); 
declare variable lRabat numeric(18,2); 
declare variable lRabatMoms numeric(18,2);
declare variable lAntalEksp Integer; 
declare variable lDato1 varchar(20); 
declare variable lDato2 varchar(20); 
begin
  if ((NEW.AAR<>OLD.AAR) OR
      (NEW.DOER<>OLD.DOER) OR
      (NEW.MDR<>OLD.MDR) OR
      (NEW.DAG<>OLD.DAG) OR
      (NEW.TID<>OLD.TID) OR
      (NEW.TELLER<>OLD.TELLER) OR
      (NEW.TELLER2<>OLD.TELLER2)) then
  begin  
    NEW.STK = 0; 
    NEW.Kpr = 0; 
    NEW.Spr = 0; 
    NEW.Moms = 0; 
    NEW.Rabat = 0; 
    NEW.Rabat_Moms = 0; 
    if ((NEW.AAR>1980) and (NEW.AAR<2080)) then 
    begin 
      if ((NEW.MDR>=1) and (NEW.MDR<=12)) then 
      begin 
        if ((NEW.DAG>=1) and (NEW.DAG<=31)) then 
        begin 
          if ((NEW.TID>=0) and (NEW.TID<=23)) then 
          begin 
            lDato1 = NEW.AAR; 
            if ((NEW.MDR>=1) and (NEW.MDR<=9)) then 
            begin 
              lDato1 = lDato1 || '-0' || NEW.MDR; 
            end 
            else 
            begin 
              lDato1 = lDato1 || '-' || NEW.MDR; 
            end 
   
            if ((NEW.DAG>=1) and (NEW.DAG<=9)) then 
            begin 
              lDato1 = lDato1 || '-0' || NEW.DAG; 
            end 
            else 
            begin 
              lDato1 = lDato1 || '-' || NEW.DAG; 
            end 
   
            if ((NEW.TID>=0) and (NEW.TID<=9)) then 
            begin 
              lDato2 = lDato1 || ' 0' || NEW.TID || ':59:59'; 
              lDato1 = lDato1 || ' 0' || NEW.TID || ':00:00'; 
            end 
            else 
            begin 
              lDato2 = lDato1 || ' ' || NEW.TID || ':59:59'; 
              lDato1 = lDato1 || ' ' || NEW.TID || ':00:00'; 
            end 
   
            SELECT 
              SUM(TRANSAKTIONER.SALGSTK), 
              SUM(TRANSAKTIONER.KOSTPR), 
              SUM(TRANSAKTIONER.SALGKR), 
              SUM(TRANSAKTIONER.MOMSKR), 
              SUM(TRANSAKTIONER.RABAT), 
              SUM(TRANSAKTIONER.RABAT_MOMS), 
              count(distinct BonNr) 
            FROM 
              TRANSAKTIONER 
            WHERE 
              EXTRACT(YEAR FROM TRANSAKTIONER.DATO)=NEW.AAR 
              AND EXTRACT(MONTH FROM TRANSAKTIONER.DATO)=NEW.MDR 
              AND EXTRACT(DAY FROM TRANSAKTIONER.DATO)=NEW.DAG 
              AND EXTRACT(HOUR FROM TRANSAKTIONER.DATO)=NEW.TID 
              And (TRANSAKTIONER.DATO>=:lDato1) and (TRANSAKTIONER.DATO<=:lDato2) 
              AND TRANSAKTIONER.AFDELING_ID=NEW.AFDELING_ID 
              AND TRANSAKTIONER.ART IN (0,1) 
            INTO 
              :lStk, 
              :lKpr, 
              :lSpr, 
              :lMoms, 
              :lRabat,    
              :lRabatMoms,
              :lAntalEksp; 
   
            if (lStk is null) then 
            begin 
              NEW.STK = 0; 
            end 
            else 
            begin 
              NEW.STK = lStk; 
            end 
            if (lKpr is null) then 
            begin 
              NEW.Kpr = 0; 
            end 
            else 
            begin 
              NEW.Kpr = lKpr; 
            end 
            if (lSpr is null) then 
            begin 
              NEW.Spr = 0; 
            end 
            else 
            begin 
              NEW.Spr = lSpr; 
            end 
            if (lMoms is null) then 
            begin 
              NEW.Moms = 0; 
            end 
            else 
            begin 
              NEW.Moms = lMoms; 
            end 
            if (lRabat is null) then 
            begin 
              NEW.Rabat = 0; 
            end 
            else 
            begin 
              NEW.Rabat = lRabat; 
            end 
            if (lRabatMoms is null) then 
            begin 
              NEW.Rabat_Moms = 0; 
            end 
            else 
            begin 
              NEW.Rabat_Moms = lRabatMoms; 
            end 
            if (lAntalEksp is null) then 
            begin 
              NEW.AntalEksp = 0; 
            end 
            else 
            begin 
              NEW.AntalEksp = lAntalEksp; 
            end 
            NEW.TaltOP = NEW.TaltOP + 1; 
          end 
        end 
      end 
    end
  end 
end^

