/* Trigger: POSTERINGER_VAL */
CREATE OR ALTER TRIGGER POSTERINGER_VAL FOR POSTERINGER
INACTIVE BEFORE INSERT OR UPDATE POSITION 10
AS 
declare variable lStdValuta VarChar(10); 
declare variable lRapporteringsValuta VarChar(10); 
declare variable lStdVal numeric (18,2); 
declare variable lRappValuta numeric (18,2); 
begin 
    lRapporteringsValuta = "DKR"; 
    lStdValuta           = "DKR"; 
    lStdVal              = 100; 
    lRappValuta          = 100; 
    Select StdValuta, RapporteringsValuta from Stamdata_Prg_ext Where Afdeling_ID=New.Afdeling into :lStdValuta, :lRapporteringsValuta; 
 
    if (lStdValuta<>lRapporteringsValuta) then 
    begin 
        SELECT  VALUTALINIER.KURS FROM VALUTALINIER WHERE VALUTALINIER.VALUTA_TEKST=:lStdValuta into :lStdVal; 
        SELECT  VALUTALINIER.KURS FROM VALUTALINIER WHERE VALUTALINIER.VALUTA_TEKST=:lRapporteringsValuta into :lRappValuta; 
 
        if (lRappValuta<>0) then 
        begin 
            New.BELOB_VAL          = New.BELOB / lRappValuta * lStdVal; 
            New.VALUTA_VAL         = New.VALUTA / lRappValuta * lStdVal; 
        end 
        else    
        begin 
            New.BELOB_VAL          = 0; 
            New.VALUTA_VAL         = 0; 
        end 
    end 
    else 
    begin 
            New.BELOB_VAL          = 0; 
            New.VALUTA_VAL         = 0; 
    end 
end^

