/* Trigger: STOR_OMSETNING_VALUTA */
CREATE OR ALTER TRIGGER STOR_OMSETNING_VALUTA FOR STOR_OMSETNING
ACTIVE BEFORE INSERT OR UPDATE POSITION 10
AS
declare variable lStdValuta VarChar(10);
declare variable lRapporteringsValuta VarChar(10);
declare variable lStdVal numeric (18,2);
declare variable lRappValuta numeric (18,2);
begin
    /* Henter std. og rapp. valutaer */
    lRapporteringsValuta = 'DKR';
    lStdValuta           = 'DKR';
    lStdVal              = 100;
    lRappValuta          = 100;
    Select StdValuta, RapporteringsValuta from Stamdata_Prg_ext Where Afdeling_ID=New.Afdeling_ID into :lStdValuta, :lRapporteringsValuta;
    
    if (lStdValuta<>lRapporteringsValuta) then
    begin
        /*Er de forskellige hentes kurserne*/      
        SELECT  VALUTALINIER.KURS FROM VALUTALINIER WHERE VALUTALINIER.VALUTA_TEKST=:lStdValuta into :lStdVal;
        SELECT  VALUTALINIER.KURS FROM VALUTALINIER WHERE VALUTALINIER.VALUTA_TEKST=:lRapporteringsValuta into :lRappValuta;
            
        if (lRappValuta<>0) then
        begin
            /*Hvis rapp. kursen er forskellig fra 0 */
            
            /* Divider med rapp. valuta. og gang med std. valuta */
            New.LSALGSPRIS_VAL     = New.LSALGSPRIS / lRappValuta * lStdVal;
            New.lKostPris_Val      = New.lKostPris / lRappValuta * lStdVal;
            New.lMoms_Val          = New.lMoms / lRappValuta * lStdVal;
            New.Salgspris_Val      = New.Salgspris / lRappValuta * lStdVal;
            New.Kostpris_Val       = New.Kostpris / lRappValuta *  lStdVal;
            New.Rabat_Val          = New.Rabat / lRappValuta * lStdVal;
            New.RabatMoms_Val      = New.RabatMoms / lRappValuta * lStdVal;
            New.Moms_Val           = New.Moms / lRappValuta * lStdVal;
            New.kSALGSPRIS_VAL     = New.kSALGSPRIS / lRappValuta * lStdVal;
            New.kKostPris_Val      = New.kKostPris / lRappValuta * lStdVal;
            New.kMoms_Val          = New.kMoms / lRappValuta * lStdVal;
        end
        else
        begin
            New.LSALGSPRIS_VAL     = New.LSALGSPRIS;
            New.lKostPris_Val      = New.lKostPris;
            New.lMoms_Val          = New.lMoms;
            New.Salgspris_Val      = New.Salgspris;
            New.Kostpris_Val       = New.Kostpris;
            New.Rabat_Val          = New.Rabat;
            New.RabatMoms_Val      = New.RabatMoms;
            New.Moms_Val           = New.Moms;
            New.kSALGSPRIS_VAL     = New.kSALGSPRIS;
            New.kKostPris_Val      = New.kKostPris;
            New.kMoms_Val          = New.kMoms;
        end
    end
    else
    begin
        New.LSALGSPRIS_VAL     = New.LSALGSPRIS;
        New.lKostPris_Val      = New.lKostPris;
        New.lMoms_Val          = New.lMoms;
        New.Salgspris_Val      = New.Salgspris;
        New.Kostpris_Val       = New.Kostpris;
        New.Rabat_Val          = New.Rabat;
        New.RabatMoms_Val      = New.RabatMoms;
