/* Trigger: TRANSAKTIONER_EASYPOSEVENT */
CREATE OR ALTER TRIGGER TRANSAKTIONER_EASYPOSEVENT FOR TRANSAKTIONER
ACTIVE AFTER INSERT POSITION 100
AS  
DECLARE VARIABLE lStartingBalance NUMERIC(15,2);  
BEGIN  
  /* This trigger will automatically insert records into EasyPOS_Events table */  
  /* */  
  IF (NEW.ART = 150) THEN 
  BEGIN  
    /* Settlement (kasseopgorelse) has been made succesfully*/  
    INSERT INTO easypos_events (  
        department,  
        machine,  
        clerk,  
        eventid,  
        eventtype,  
        transid,  
        empid,  
        eventdateandtime,  
        eventtext,  
        receiptnumber) 
    VALUES (  
        NEW.afdeling_id,  
        NEW.uafd_navn,  
        NEW.ekspedient,  
        '13005',
        'OPEN_DRAWER', 
        NEW.transid,  
        NEW.ekspedient,  
        NEW.dato,  
        'Cash drawer was manually made on machine ' || NEW.uafd_navn || ' in department ' || NEW.afdeling_id || ' by clerk ' || NEW.ekspedient, 
        NEW.bonnr);  
  END  
  IF (NEW.ART = 182) THEN  
  BEGIN  
    /* Settlement (kasseopgorelse) has been made succesfully*/  
    INSERT INTO easypos_events (  
        department,  
        machine,  
        clerk,  
        eventid,  
        eventtype,  
        transid,  
        empid,  
        eventdateandtime,  
        eventtext,  
        receiptnumber) 
    VALUES (  
        NEW.afdeling_id,  
        NEW.uafd_navn,  
        NEW.ekspedient,  
        '13008',
        'SETTLEMENT',  
        NEW.transid,  
        NEW.ekspedient,  
        NEW.dato,  
        'Settlement has been made succesfully',  
        NEW.bonnr);  
  END  
  IF (NEW.ART = 180) THEN 
  BEGIN  
    /* Close Day (dagsafslutning) has been made succesfully*/  
    INSERT INTO easypos_events (  
        department,  
        machine,  
        clerk,  
        eventid,  
        eventtype,  
        transid,  
        empid,  
        eventdateandtime,  
        eventtext,  
        receiptnumber) 
    VALUES (  
        NEW.afdeling_id,  
        NEW.uafd_navn,  
        NEW.ekspedient,  
        '13009',
        'CLOSE_DAY',  
        NEW.transid,  
        NEW.ekspedient,  
        NEW.dato,  
        'Close day has been made succesfully',  
        NEW.bonnr);  
  END  
  IF (NEW.ART = 5) THEN  
  BEGIN  
    /* Settlement of one payment has been made (afregning af en betalingsvej)*/  
  
    /*Fetch last settlements "end balance" and use as starting balance */  
    SELECT FIRST 1  
        s.new_balance  
    FROM settlements s  
    WHERE  
        s.department = NEW.afdeling_id AND  
        s.machine = NEW.uafd_navn AND  
        s.payment = NEW.betvej  
    ORDER BY  
        s.id DESC  
    INTO lStartingBalance;  
  
    /* If null - nothing was found */  
    if (lstartingbalance is null) then  
      lstartingbalance = 0;  
  
    INSERT INTO settlements (  
        created,  
        department,  
        machine,  
        clerk,  
        transid,  
        paymnentdisplayname,  
        payment,  
        Starting_balance,  
        Balance,  
        Transfer,  
        Difference,  
        New_Balance,  
        currencyname,  
        currency,  
        receiptnumber)  
    VALUES (  
        NEW.dato,  
        NEW.afdeling_id,  
        NEW.uafd_navn,  
        NEW.ekspedient,  
        NEW.transid,  
        SubString(NEW.bontext from 1 for 25), 
        NEW.betvej, 
        /*starting balance. Equals ending balance of previous settlement*/  
        :lStartingBalance,  
        /*Balance at the time of the settlement*/  
        NEW.momskr,  
        /*Difference*/  
        NEW.salgkr,  
        /*What has been tranferred to bank*/  
        NEW.rabat,  
        /*New balance. WIll also be next "days" starting balance*/  
        /*We need to replace a comma with a dot, so it will be in correct format to change to a numeric*/  
        REPLACE(NEW.levnavn, ',', '.'),  
        NEW.valuta_tekst,  
        NEW.valutakr,  
        NEW.bonnr);  
  
  END  
END^

