/* Trigger: TRANSAKTIONER_VALUTA */
CREATE OR ALTER TRIGGER TRANSAKTIONER_VALUTA FOR TRANSAKTIONER
ACTIVE BEFORE INSERT POSITION 1
AS 
declare variable lStdValuta VarChar(10); 
declare variable lRapporteringsValuta VarChar(10); 
declare variable lStdVal numeric (18,2); 
declare variable lRappValuta numeric (18,2); 
declare variable FraVal VarChar(10); 
declare variable TilVal VarChar(10); 
declare variable FraValSats numeric (18,2); 
declare variable TilValSats numeric (18,2); 
begin 
    lRapporteringsValuta = "DKR"; 
    lStdValuta           = "DKR"; 
    lStdVal              = 100; 
    lRappValuta          = 100; 
    FraVal               = "DKR"; 
    TilVal               = "DKR"; 
    FraValSats           = 100; 
    TilValSats           = 100; 
    Select StdValuta, RapporteringsValuta from Stamdata_Prg_ext Where Afdeling_ID=New.Afdeling_ID into :lStdValuta, :lRapporteringsValuta; 
 
    if (lStdValuta<>lRapporteringsValuta) then 
    begin 
        SELECT  VALUTALINIER.KURS FROM VALUTALINIER WHERE VALUTALINIER.VALUTA_TEKST=:lStdValuta into :lStdVal; 
        SELECT  VALUTALINIER.KURS FROM VALUTALINIER WHERE VALUTALINIER.VALUTA_TEKST=:lRapporteringsValuta into :lRappValuta; 
 
        if (lRappValuta<>0) then 
        begin 
            New.SALGKR_VAL     = New.SALGKR / lRappValuta * lStdVal; 
            New.KOSTPR_Val     = New.KOSTPR / lRappValuta * lStdVal; 
            New.VALUTAKR_Val   = New.VALUTAKR / lRappValuta * lStdVal; 
            New.MOMSKR_Val     = New.MOMSKR / lRappValuta * lStdVal; 
            New.ENGKR_Val      = New.ENGKR / lRappValuta * lStdVal; 
            New.Rabat_Val      = New.Rabat / lRappValuta * lStdVal; 
            New.Rabat_Moms_Val = New.Rabat_Moms_Val / lRappValuta * lStdVal; 
        end 
        else 
        begin 
            New.SALGKR_VAL     = New.SALGKR; 
            New.KOSTPR_Val     = New.KOSTPR; 
            New.VALUTAKR_Val   = New.VALUTAKR; 
            New.MOMSKR_Val     = New.MOMSKR; 
            New.ENGKR_Val      = New.ENGKR; 
            New.Rabat_Val      = New.Rabat; 
            New.Rabat_Moms_Val = New.Rabat_Moms_Val; 
        end 
    end 
    else 
    begin    
        New.SALGKR_VAL     = New.SALGKR; 
        New.KOSTPR_Val     = New.KOSTPR; 
        New.VALUTAKR_Val   = New.VALUTAKR; 
        New.MOMSKR_Val     = New.MOMSKR; 
        New.ENGKR_Val      = New.ENGKR; 
        New.Rabat_Val      = New.Rabat; 
        New.Rabat_Moms_Val = New.Rabat_Moms_Val; 
    end 
end^

