/* Trigger: TRANS_TO_STAT */
CREATE OR ALTER TRIGGER TRANS_TO_STAT FOR TRANSAKTIONER
INACTIVE AFTER INSERT POSITION 20
AS 
  Declare Variable lAktiv Integer; 
  Declare Variable lAar Integer; 
  Declare Variable lMdr Integer; 
  Declare Variable lLagerAntal Numeric (18,2); 
  Declare Variable lLagerKpr Numeric (18,2); 
  Declare Variable lLagerSpr Numeric (18,2); 
  Declare Variable lLagerSpr_Moms Numeric (18,2); 
begin 
  /*Ser om udvidet gruppe statistik er slet til*/ 
  Select Afd.UdvGrupStat from Afdeling Afd Where Afd.Afdelingsnummer=New.Afdeling_ID into :lAktiv; 
  if (:lAktiv<>0) then 
  begin 
    /*Stter r og mned*/ 
    lAar = Cast(Extract(Year from New.Dato) as Integer); 
    lMdr = Cast(Extract(month from New.Dato) as Integer); 
 
    /*Kontrollere om post findes*/ 
    if ((New.art=0) or 
        (New.art=1) or 
        (New.art=12) or 
        (New.art=11) or 
        (New.art=14)) then 
    begin 
      if (not exists (select ID from STAT where STAT.AFDELING_ID=New.AFDELING_ID and 
                                                STAT.VareGrp_ID=New.VAREGRPID and 
                                                STAT.Mdr=:lMdr and                    
                                                STAT.Aar=:lAar)) then 
      begin 
          /* 
            Hvis mnedsposten ikke findes hentes den nuv. lagerbeh. fra varegruppen og 
            denne indsttes 
          */ 
        insert into STAT 
          (VareGrp_ID, 
           Aar, 
           Mdr, 
           Lever_ID, 
           Afdeling_ID) 
        Values 
          (New.VAREGRPID, 
           :lAar, 
           :lMdr, 
           "Ingen leverandr", 
           New.Afdeling_ID); 
      end 
    end       
 
    if ((New.art=0) or (New.art=1)) then 
    begin 
      /*Salgstransaktion*/ 
      Update STAT set 
        Salg_Stk = Salg_Stk + New.SALGSTK, 
        Salg_Kpr = Salg_Kpr + New.KOSTPR, 
        Salg_Spr = Salg_Spr + New.SALGKR, 
        Salg_Moms = Salg_Moms + New.MOMSKR, 
        Salg_Rabat = Salg_Rabat + New.RABAT, 
        Salg_RabatMoms = Salg_RabatMoms + New.RABAT_MOMS 
      Where 
        STAT.VareGrp_ID=New.VareGrpID and 
        STAT.Afdeling_ID=New.Afdeling_ID and 
        STAT.Aar=:lAar and 
        STAT.Lever_ID = "Ingen leverandr" and 
        STAT.Mdr=:lMdr; 
    end 
 
    if ((New.art=11)) then 
    begin 
      /* Opdater tilgangsinformation */ 
      Update STAT set 
        Kob_Stk = Kob_Stk + New.SALGSTK, 
        Kob_Kpr = Kob_Kpr + New.KOSTPR, 
        Kob_Spr = Kob_Spr + New.SALGKR, 
        Kob_Moms = Kob_Moms + New.MOMSKR 
      Where 
        STAT.VareGrp_ID=New.VareGrpID and 
        STAT.Afdeling_ID=New.Afdeling_ID and 
        STAT.Aar=:lAar and 
        STAT.Lever_ID = "Ingen leverandr" and 
        STAT.Mdr=:lMdr; 
    end 
 
    if ((New.art=12)) then 
    begin 
      /* Opdater optllingsinformation (Status) */ 
      Update STAT set 
        Opt_Stk = Opt_Stk + New.SALGSTK, 
        Opt_Kpr = Opt_Kpr + New.KOSTPR, 
        Opt_Spr = Opt_Spr + New.SALGKR, 
        Opt_Moms = Opt_Moms + New.MOMSKR 
      Where 
        STAT.VareGrp_ID=New.VareGrpID and 
        STAT.Afdeling_ID=New.Afdeling_ID and 
        STAT.Aar=:lAar and 
        STAT.Lever_ID = "Ingen leverandr" and 
        STAT.Mdr=:lMdr; 
    end 
 
    if ((New.art=14)) then 
    begin 
      /* 
        Til- og afgangsinformationer. 
        PAKKELINJE vil fortlle, om det er en tilgang fra butik eller en afgang til butik 
        PAKKELINJE: 
          1: Afgang til butik eller afvist tilgang i salg (betyder reelt afgang til butik) 
          2: Tilgang fra butik (bde i kontor og i salg) 
          5: Afgang til butik v. direkte flytning (ikke via transportlager) 
          6: Tilgang fra butik v. direkte flytning (ikke via transportlager) 
      */ 
 
 
      if ((New.Pakkelinje=1) or (New.Pakkelinje=5)) then 
      begin 
        /* Opdater Afgang til butik */ 
        Update STAT set 
          OFra_Stk = OFra_Stk + New.SALGSTK, 
