DECLARE VARIABLE LAFDELIGNSNUMMER VARCHAR(30);
DECLARE VARIABLE LLAND VARCHAR(50);
DECLARE VARIABLE LMOMSFRIT INTEGER;
DECLARE VARIABLE LDATO DATE;

DECLARE VARIABLE LSPRISFROM NUMERIC(18,2);
DECLARE VARIABLE LKPRISFROM NUMERIC(18,2);
DECLARE VARIABLE LMOMSSATSFROM VARCHAR(25);
DECLARE VARIABLE LVALUTANAVNFROM VARCHAR(10);

DECLARE VARIABLE LSENDINGDEPARTMENT VARCHAR(10);
DECLARE VARIABLE LSENDINGDEPARTMENTINT INTEGER;

DECLARE VARIABLE LDEPARTMENTCURRENCY VARCHAR(10);
DECLARE VARIABLE LREPORTCURRENCY VARCHAR(10);
DECLARE VARIABLE LWEBSLADREHANK VARCHAR(4000);
BEGIN
  LWEBSLADREHANK = '';
  IF (NEW.KATEGORI5 = 'WEBDK') THEN
  BEGIN
    /*TRACE LOG*/
    LWEBSLADREHANK = LWEBSLADREHANK || 'Webordrelinie: ' || NEW.BONNR || ASCII_CHAR(13) || ASCII_CHAR(10);
    IF (CHAR_LENGTH(NEW.BONNR) < 14) THEN
    BEGIN
      LAFDELIGNSNUMMER = 'Indsat linie ID: ' || NEW.BONNR;
      INSERT INTO USER_TRANS (
          BRUGER,
