      LAFDELIGNSNUMMER = 'Indsat linie ID: Ukendt';
      INSERT INTO USER_TRANS (
          BRUGER,
          HVAD)
      VALUES (
          'WEBDANMARK',
          :LAFDELIGNSNUMMER);
    END
    /*INITIALISER VARIABLER*/
    LSPRIS = 0;
    LPROVISION = 0;
    LPROVSATS = 0;
    LVGRSALGPCT = 0;
    LLEVERSALGPCT = 0;
    LMOMSSATS = '';
    LENHED = '';
    LVALUTANAVN = '';
    LBEH_SALGSPRIS = 0;
    LANTALSTK = 0;
    LPOPUPVARETEKSTER = 0;
    LRABATKR = 0;
    LDATO = 'NOW';
    LSPRISFROM = 0;
    LKPRISFROM = 0;
    LMOMSSATSFROM = '';
    LVALUTANAVNFROM = '';
    LSENDINGDEPARTMENTINT = CAST(NEW.AFDELING_ID AS INTEGER);
    LWEBSLADREHANK = LWEBSLADREHANK || '  lSendingDepartmentint: ' || LSENDINGDEPARTMENTINT || ASCII_CHAR(13) || ASCII_CHAR(10);
    IF ((LSENDINGDEPARTMENTINT >= 200) AND
        (LSENDINGDEPARTMENTINT <= 299)) THEN
    BEGIN
      LSENDINGDEPARTMENT = '049';
    END
    ELSE
    IF ((LSENDINGDEPARTMENTINT >= 100) AND
        (LSENDINGDEPARTMENTINT <= 199)) THEN
    BEGIN
      LSENDINGDEPARTMENT = '050';
    END
    ELSE
    IF ((LSENDINGDEPARTMENTINT >= 400) AND
        (LSENDINGDEPARTMENTINT <= 499)) THEN
    BEGIN
      LSENDINGDEPARTMENT = '010';
    END
    ELSE
    BEGIN
      LSENDINGDEPARTMENT = '050';
    END
    LDEPARTMENTCURRENCY = '';
    LREPORTCURRENCY = '';

    LWEBSLADREHANK = LWEBSLADREHANK || '  Stregkode: ' || NEW.BONTEKST || ASCII_CHAR(13) || ASCII_CHAR(10);
    LWEBSLADREHANK = LWEBSLADREHANK || '  Afdeling: ' || NEW.AFDELING_ID || ASCII_CHAR(13) || ASCII_CHAR(10);
    /*HENT VAREINFORMATIONER - Henter priser i afdelingens valuta.*/
    SELECT
        VD.VAREPLU_ID,
        VD.STOERRELSE_NAVN,
        VD.LAENGDE_NAVN,
        VD.FARVE_NAVN,
        VD.SALGSPRISSTK,
        VD.VEJETKOSTPRISSTK,
        VD.PROVISION,
        VG.PROVSATS,
        VG.SALGRABATPCT AS VGRSALGPCT,
        LG.SALGRABATPCT AS LEVERSALGPCT,
        VD.MOMSSATS,
        MS.MOMSSATS AS MOMSSATSPCT,
        VD.ENHEDSNOGLE,
        VE.ENHEDSBETEGNELSE,
        VD.VALUTA_NAVN,
        VD.LEVERID,
        VD.VAREGRPID,
        VD.BEH_KOSTPRIS,
        VD.BEH_SALGSPRIS,
        VD.ANTALSTK,
        V.POPUPVARETEKSTER,
        V.SERVICEYDELSE,
        V.VARENAVN1,
        V.MODEL,
        V.KATEGORI5,
        S.STDVALUTA,
        S.RAPPORTERINGSVALUTA
    FROM VAREFRVSTR_DETAIL VD
        INNER JOIN VAREGRUPPER_DETAIL VG ON
              (VG.NAVN = VD.VAREGRPID
              AND VG.AFDELING_ID = VD.AFDELING_ID)
        INNER JOIN LEVERANDOERER_DETAIL LG ON
              (LG.NAVN = VD.LEVERID
              AND LG.AFDELING_ID = VD.AFDELING_ID)
        INNER JOIN MOMSSATSER MS ON
              (VD.MOMSSATS = MS.MOMSSATS_TITEL)
        INNER JOIN VARERENHEDER VE ON
              (VD.ENHEDSNOGLE = VE.ENHEDSNOGLE)
        INNER JOIN VARER V ON
              (V.PLU_NR = VD.VAREPLU_ID)
        INNER JOIN STAMDATA_PRG_EXT S ON
              S.AFDELING_ID = VD.AFDELING_ID
    WHERE
        VD.V509INDEX = NEW.BONTEKST
        AND VD.AFDELING_ID = NEW.AFDELING_ID
