         NEW.STOERRELSE,
         NEW.LAENGDE,
         NEW.FARVE,
         :LSPRIS,
         NEW.KOSTPRIS,
         :LPROVISION,
         :LPROVSATS,
         :LVGRSALGPCT,
         :LLEVERSALGPCT,
         :LMOMSSATS,
         NEW.MOMSKODE,
         :LENHED,
         NEW.ENHED,
         :LVALUTANAVN,
         NEW.LEVNAVN,
         NEW.VARE_GRP_ID,
         :LTMPPRIS,
         :LBEH_SALGSPRIS,
         :LANTALSTK,
         :LPOPUPVARETEKSTER,
         NEW.SERVICEYDELSE,
         NEW.VARENAVN,
         NEW.MODEL,
         NEW.KATEGORI5,
         :LDEPARTMENTCURRENCY,
         :LREPORTCURRENCY;

    /*Fetching country*/
    SELECT
        UDVALG_MASTER.MOMSFRIT
    FROM UDVALG_MASTER
    WHERE
        UDVALG_MASTER.BONNR = NEW.BONNR
    INTO :LMOMSFRIT;

    /*Setting price sold for in the shop for 1 peice*/
    LWEBPRIS = NEW.SALGSPRIS / NEW.ANTAL;
    /*Setting amout*/
    NEW.SALGS_STK = NEW.ANTAL;
    /* Setting price sold for totally*/
    NEW.PRIS = NEW.SALGSPRIS;

    /*Calculate discount. If item has been sold below registered salesprice discount hs been given*/
    IF (LSPRIS <> LWEBPRIS) THEN
    BEGIN
      LRABATKR = LSPRIS - LWEBPRIS;
    END
    ELSE
    BEGIN
      LRABATKR = 0;
    END


    IF (LMOMSFRIT = 1) THEN
    BEGIN
      /*VAT is 0*/
      NEW.MOMSKR = 0;
      NEW.RABAT_MOMS = 0;
    END
    ELSE
    BEGIN
      /*CALCULATE VAT*/
      NEW.MOMSKR = (NEW.SALGSPRIS / (100 + NEW.MOMSKODE)) * NEW.MOMSKODE;
      NEW.RABAT_MOMS = ((LRABATKR * NEW.ANTAL) / (100 + NEW.MOMSKODE)) * NEW.MOMSKODE;
    END
    /*SET TOTAL DISCOUNT*/
    NEW.RABATKR = LRABATKR * NEW.ANTAL;
    /*SET TOTAL RETAILPRICE*/
    NEW.KOSTPRIS = NEW.KOSTPRIS * NEW.ANTAL;

    /*SET RECEIPT TEXT*/
    NEW.VAREINFO = NEW.VARENAVN || ' ' || NEW.FARVE || ' ' || NEW.STOERRELSE || ' ' || NEW.LAENGDE;


    /*CHANGES NEEDED !!!!!*/
    IF (LDEPARTMENTCURRENCY <> LREPORTCURRENCY) THEN
    BEGIN
      NEW.VALUTA = 0;
      NEW.VALUTANAVN = :LVALUTANAVN;
    END
    ELSE
