    NEW.ART = 0;
    NEW.BETVEJ = 'Ingen betalingsvej';
    NEW.RABATTYPE = 103;
    NEW.RABATMAERKE = ' ';
    NEW.UAFD_GRP_NAVN = 'Ingen undergruppe';
