    NEW.BONTYPE = 0;
    NEW.ORISALGPRIS = 0;
    NEW.ORIFAMSALGPRIS = 0;
    NEW.ORIKOSTPRIS = 0;
    NEW.FAMVNR = '';


    LWEBSLADREHANK = LWEBSLADREHANK || '  lDepartmentCurrency: ' || LDEPARTMENTCURRENCY || ASCII_CHAR(13) || ASCII_CHAR(10);
    LWEBSLADREHANK = LWEBSLADREHANK || '  lReportCurrency: ' || LREPORTCURRENCY || ASCII_CHAR(13) || ASCII_CHAR(10);
    IF (LDEPARTMENTCURRENCY <> LREPORTCURRENCY) THEN
    BEGIN
      /*UDENLANDSK WEBORDER - DO MAKE MOVEMENT*/


      LWEBSLADREHANK = LWEBSLADREHANK || '  Do movement 1' || ASCII_CHAR(13) || ASCII_CHAR(10);
      /*TRANSAKTION TO TRACE MOVEMENT  TO  DEPARTMENT*/
      INSERT INTO TRANSAKTIONER (
          DATO,
          KUNDESALG,
          INDLAEST,
          ART,
          BETVEJ,
          LEVNAVN,
          FARVE_NAVN,
          LAENGDE_NAVN,
          STOERRELSE_NAVN,
          SALGKR,
          KOSTPR,
          VALUTAKR,
          VALUTA_TEKST,
          MOMSKR,
          DEBNR,
          RABAT,
          RABAT_MOMS,
          EKSPEDIENT,
          MKODE,
          VNRTRANS,
          VAREFRVSTRNR,
          PAKKELINJE,
          BEREGNKOSTPRIS,
          TRANSKATALOGVARE,
          BONNR,
          KASSENR,
          KATEGORI5,
          TILBUTIK,
          SERVICEYDELSE,
          ALENE_TEKST,
          TILBAGEFORELSE,
          RETUROMS,
          VAREGRPID,
          BONTEXT,
          SALGSTK,
          EXT_TRANS_ID,
          AFDELING_ID,
          UAFD_NAVN,
          UAFD_GRP_NAVN)
      VALUES (
