          /*Dato*/
          100,
          /*Kundesalg*/
          0,
          /*Indlaest*/
          14,
          /*art*/
          'Ingen betalingsvej',
          /*Betalingsvewj*/
          NEW.LEVNAVN,
          /*LevNavn*/
          NEW.FARVE,
          /*Farvwe*/
          NEW.LAENGDE,
          /*Laengde*/
          NEW.STOERRELSE,
          /*Stoerrelse*/
          :LSPRIS * NEW.ANTAL,
          /*SalgKr*/
          NEW.KOSTPRIS,
          /*KostPr*/
          0,
          /*ValutaKr*/
          :LVALUTANAVN,
          /*Valuta_Tekst*/
          ((:LSPRIS * NEW.ANTAL) / (100 + NEW.MOMSKODE)) * NEW.MOMSKODE,
          /*MomsKr*/
          '',
          /*DebNr*/
          0,
          /*Rabat*/
          0,
          /*RabatMoms*/
          9999,
          /*Ekspedint*/
          0,
          /*MKode*/
          1,
          /*VNrTrans*/
          NEW.VARENUMMER,
          /*VareFrvStrNr*/
          2,
          /*Pakkelinje*/
          0,
          /*BeregnKostpris*/
          NEW.BONNR,
          /*TransKatalogVare*/
          NEW.BONNR,
          /*BonNr*/
          1,
          /*KasseNr*/
          '',
          /*Kategori5*/
          :LSENDINGDEPARTMENT,
          /*TilButik*/
