          KOSTPR,
          VALUTAKR,
          VALUTA_TEKST,
          MOMSKR,
          DEBNR,
          RABAT,
          RABAT_MOMS,
          EKSPEDIENT,
          MKODE,
          VNRTRANS,
          VAREFRVSTRNR,
          PAKKELINJE,
          BEREGNKOSTPRIS,
          TRANSKATALOGVARE,
          BONNR,
          KASSENR,
          KATEGORI5,
          TILBUTIK,
          SERVICEYDELSE,
          ALENE_TEKST,
          TILBAGEFORELSE,
