/* Trigger: UNDERAFDELING_INTERSOLVE */
CREATE OR ALTER TRIGGER UNDERAFDELING_INTERSOLVE FOR UNDERAFDELING
ACTIVE BEFORE INSERT POSITION 20
AS 
declare Variable lStr Varchar(5); 
declare variable ii integer; 
begin 
  lStr = Cast(gen_id(INTERSOLVE_USER_GEN, 1) as Varchar(5)); 
  while (CHAR_LENGTH(lStr)<3) do 
  begin 
    lStr = '0' || lStr; 
  end 
  new.INTERSOLVE_USER = new.afdeling_ID || lStr; 
end^

