ACTIVE AFTER UPDATE POSITION 0
AS
BEGIN 
  If 
     ( 
        (NEW.StkAntal<>Old.StkAntal) or 
        (NEW.Kostpris<>Old.Kostpris) or 
        (NEW.SalgsPris<>Old.SalgsPris) or 
        (NEW.SalgsPris_Moms<>Old.SalgsPris_Moms) or 
