        (NEW.SalgsPris_VAL<>Old.SalgsPris_VAL) or 
        (NEW.SalgsPris_Moms_VAL<>Old.SalgsPris_Moms_VAL) 
     ) 
  then 
  begin 
    Update VareFrvStr_Detail Set
      AntalIOrdre = AntalIOrdre + (NEW.StkAntal-Old.StkAntal),
      IOrdreKpr = IOrdreKpr + (NEW.Kostpris-Old.Kostpris), 
      IOrdreSpr = IOrdreSpr + (NEW.SalgsPris-Old.SalgsPris), 
      IOrdreSpr_Moms = IOrdreSpr_Moms + (NEW.SalgsPris_Moms-Old.SalgsPris_Moms), 
      IOrdreKpr_VAL = IOrdreKpr_VAL + (NEW.Kostpris_VAL-Old.Kostpris_VAL), 
      IOrdreSpr_VAL = IOrdreSpr_VAL + (NEW.SalgsPris_VAL-Old.SalgsPris_VAL), 
      IOrdreSpr_Moms_VAL = IOrdreSpr_Moms_VAL + (NEW.SalgsPris_Moms_VAL-Old.SalgsPris_Moms_VAL), 
      SidsteBev = "NOW" 
      Where VareFrvStr_Detail.VarePlu_ID=NEW.VareFrvStrNr and 
            VareFrvStr_Detail.Farve_Navn=NEW.Farve_Navn and 
            VareFrvStr_Detail.Stoerrelse_Navn=NEW.Stoerrelse_Navn and 
            VareFrvStr_Detail.Laengde_Navn=NEW.Laengde_Navn and 
            VareFrvStr_Detail.Afdeling_ID=NEW.TilAfdeling; 
  end 
 
  If ((NEW.TilAfdeling<>Old.TilAfdeling)) then 
  begin 
    Update VareFrvStr_Detail Set 
      AntalIOrdre = AntalIOrdre - Old.StkAntal, 
      IOrdreKpr = IOrdreKpr -Old.Kostpris, 
      IOrdreSpr = IOrdreSpr -Old.SalgsPris, 
      IOrdreSpr_Moms = IOrdreSpr_Moms -Old.SalgsPris_Moms, 
      IOrdreKpr_VAL = IOrdreKpr_VAL -Old.Kostpris_VAL, 
      IOrdreSpr_VAL = IOrdreSpr_VAL -Old.SalgsPris_VAL, 
      IOrdreSpr_Moms_VAL = IOrdreSpr_Moms_VAL -Old.SalgsPris_Moms_VAL, 
      SidsteBev = "NOW" 
      Where VareFrvStr_Detail.VarePlu_ID=OLD.VareFrvStrNr and 
            VareFrvStr_Detail.Farve_Navn=OLD.Farve_Navn and 
            VareFrvStr_Detail.Stoerrelse_Navn=OLD.Stoerrelse_Navn and 
            VareFrvStr_Detail.Laengde_Navn=OLD.Laengde_Navn and 
            VareFrvStr_Detail.Afdeling_ID=OLD.TilAfdeling; 
 
    Update VareFrvStr_Detail Set 
      AntalIOrdre = AntalIOrdre + NEW.StkAntal, 
      IOrdreKpr = IOrdreKpr + NEW.Kostpris, 
      IOrdreSpr = IOrdreSpr + NEW.SalgsPris, 
      IOrdreSpr_Moms = IOrdreSpr_Moms + NEW.SalgsPris_Moms, 
      IOrdreKpr_VAL = IOrdreKpr_VAL + NEW.Kostpris_VAL, 
      IOrdreSpr_VAL = IOrdreSpr_VAL + NEW.SalgsPris_VAL, 
      IOrdreSpr_Moms_VAL = IOrdreSpr_Moms_VAL + NEW.SalgsPris_Moms_VAL, 
      SidsteBev = "NOW" 
      Where VareFrvStr_Detail.VarePlu_ID=NEW.VareFrvStrNr and 
            VareFrvStr_Detail.Farve_Navn=NEW.Farve_Navn and 
            VareFrvStr_Detail.Stoerrelse_Navn=NEW.Stoerrelse_Navn and 
            VareFrvStr_Detail.Laengde_Navn=NEW.Laengde_Navn and 
            VareFrvStr_Detail.Afdeling_ID=NEW.TilAfdeling; 
  end 
END^

