CREATE OR ALTER TRIGGER UPD_VARERVARIANT_ORDRE FOR VAREFRVSTR_DETAIL
ACTIVE AFTER UPDATE POSITION 53
AS 
  BEGIN 
     if ((OLD.AntalIOrdre <> NEW.AntalIOrdre) or 
         (OLD.IOrdreKpr <> NEW.IOrdreKpr) or 
         (OLD.IOrdreSpr <> NEW.IOrdreSpr) or 
         (OLD.IOrdreSpr_Moms <> NEW.IOrdreSpr_Moms) or 
         (OLD.IOrdreKpr_VAL<> 0) or 
         (OLD.IOrdreSpr_VAL<> 0) or 
         (OLD.IOrdreSpr_Moms_VAL<> 0) 
         ) then 
     begin 
      Update Varer_Detail Set 
        AntalIOrdre = AntalIOrdre + (NEW.AntalIOrdre - Old.AntalIOrdre), 
        IOrdreKPr = IOrdreKpr + (NEW.IOrdreKpr- Old.IOrdreKpr), 
        IOrdreSPr = IOrdreSpr + (NEW.IOrdreSpr- Old.IOrdreSpr), 
