/* Trigger: UPD_VGRP_STAT */
CREATE OR ALTER TRIGGER UPD_VGRP_STAT FOR VAREGRUPPER_DETAIL
ACTIVE AFTER UPDATE POSITION 30
AS 
  Declare Variable lAktiv Integer; 
  Declare Variable lAar Integer; 
  Declare Variable lMdr Integer; 
  Declare Variable lLagerAntal Numeric (18,2); 
  Declare Variable lLagerKpr Numeric (18,2); 
  Declare Variable lLagerSpr Numeric (18,2); 
  Declare Variable lLagerSpr_Moms Numeric (18,2); 
begin 
  /*Ser om udvidet gruppe statistik er slet til*/ 
  if (VareGrupper_Detail.HovedGruppe=0) then 
  begin 
    Select Afd.UdvGrupStat from Afdeling Afd Where Afd.Afdelingsnummer=VareGrupper_Detail.Afdeling_ID into :lAktiv; 
    if (:lAktiv<>0) then 
    begin 
      /*Stter r og mned*/ 
      lAar = Cast(Extract(Year from VareGrupper_Detail.SidsteBev) as Integer); 
      lMdr = Cast(Extract(month from VareGrupper_Detail.SidsteBev) as Integer);    
 
 
        if (not exists (select * from STAT where STAT.AFDELING_ID=VareGrupper_Detail.AFDELING_ID and 
                                                 STAT.VareGrp_ID=VareGrupper_Detail.NAVN and 
                                                 STAT.Mdr=:lMdr and 
                                                 STAT.Aar=:lAar)) then 
        begin 
          /* 
            Hvis mnedsposten ikke findes indsttes de nuv. lagertal. 
          */ 
          insert into STAT 
            (VareGrp_ID, 
             Aar, 
             Mdr, 
             Lever_ID, 
             Lager_Stk, 
             Lager_Kpr, 
             Lager_Spr, 
             Lager_Moms, 
             Afdeling_ID) 
          Values 
            (VareGrupper_Detail.NAVN, 
             :lAar, 
             :lMdr, 
             "Ingen leverandr", 
             VareGrupper_Detail.LagerStk, 
             VareGrupper_Detail.LagerKpr, 
             VareGrupper_Detail.LagerKr, 
             VareGrupper_Detail.LagerKr_Moms, 
             VareGrupper_Detail.Afdeling_ID); 
        end 
        else 
        begin 
          /* Hvis mnedsposten findes, opdateres stat. tabellen */ 
          Update STAT set 
            Lager_Stk = VareGrupper_Detail.LagerStk , 
            Lager_Kpr = VareGrupper_Detail.LagerKpr, 
            Lager_Spr = VareGrupper_Detail.LagerKr, 
            Lager_Moms = VareGrupper_Detail.LagerKr_Moms 
          Where 
            STAT.VareGrp_ID=VareGrupper_Detail.NAVN and 
            STAT.Afdeling_ID=VareGrupper_Detail.Afdeling_ID and 
            STAT.Aar=:lAar and 
            STAT.Mdr=:lMdr; 
        end 
    end 
  end 
end^

