INACTIVE BEFORE UPDATE POSITION 15
AS 
declare variable lStdValuta VarChar(10); 
declare variable lRapporteringsValuta VarChar(10); 
declare variable lStdVal numeric (18,2); 
declare variable lRappValuta numeric (18,2); 
begin 
    lRapporteringsValuta = "DKR"; 
