            New.IOrdreKpr_Val            = New.IOrdreKpr / lRappValuta * lStdVal; 
            New.IOrdreSpr_Val            = New.IOrdreSpr / lRappValuta * lStdVal; 
            New.IOrdreSpr_Moms_VAL       = New.IOrdreSpr_Moms / lRappValuta * lStdVal; 
        end 
        else 
        begin 
            New.Beh_Kostpris_VAL         = New.Beh_Kostpris; 
            New.Beh_Salgspris_Val        = New.Beh_Salgspris; 
            New.Beh_SalgsprisMoms_Val    = New.Beh_SalgsprisMoms; 
            New.VejetKostPrisStk_Val     = New.VejetKostPrisStk; 
            New.IOrdreKpr_Val            = New.IOrdreKpr; 
            New.IOrdreSpr_Val            = New.IOrdreSpr; 
            New.IOrdreSpr_Moms_VAL       = New.IOrdreSpr_Moms; 
        end 
    end 
    else 
    begin 
            New.Beh_Kostpris_VAL         = New.Beh_Kostpris; 
            New.Beh_Salgspris_Val        = New.Beh_Salgspris; 
            New.Beh_SalgsprisMoms_Val    = New.Beh_SalgsprisMoms; 
            New.VejetKostPrisStk_Val     = New.VejetKostPrisStk; 
            New.IOrdreKpr_Val            = New.IOrdreKpr; 
            New.IOrdreSpr_Val            = New.IOrdreSpr; 
            New.IOrdreSpr_Moms_VAL       = New.IOrdreSpr_Moms; 
    end 
end^

/* Trigger: VARER_DETAIL_VKOSTPR */
CREATE OR ALTER TRIGGER VARER_DETAIL_VKOSTPR FOR VARER_DETAIL
ACTIVE BEFORE UPDATE POSITION 20
AS
Begin
  if
     ((old.Beh_kostpris<>New.Beh_Kostpris) or
      (old.Beh_kostpris_VAL<>New.Beh_Kostpris_VAL) or
      (old.antalstk<>New.antalstk))
  then
  begin
    if (New.antalstk<>0) then
    begin
       New.vejetkostprisstk = (New.beh_kostpris / New.antalstk);

       if (New.vejetkostprisstk<0) then
         New.vejetkostprisstk = New.vejetkostprisstk * -1;
         
         
       New.vejetkostprisstk_VAL = (New.beh_kostpris_VAL / New.antalstk);

       if (New.vejetkostprisstk_VAL<0) then
         New.vejetkostprisstk_VAL = New.vejetkostprisstk_VAL * -1;
      /*
        Denne trigger beregner den "gemte" vejede kostpris, sfremt antallet ikke er 0.
        Hvis den bliver negativ (som den ikke burde kunne), vendes fortegnet.
       */
    end
  end
end^

