/* Trigger: VARER_VAREGRPLOBENR */
CREATE OR ALTER TRIGGER VARER_VAREGRPLOBENR FOR VARER
ACTIVE BEFORE INSERT POSITION 50
AS 
declare variable UseVareGrpLobeNr Integer; 
declare variable VareGrpLobeNr Integer; 
declare variable StrVareGrpLobeNr Varchar(10); 
declare variable VareGrpV509Index Varchar(5); 
begin 
  if (NEW.Kategori1='') then 
  begin 
    select first 1 GLOBALSTAMDATA.BrugVareGrpLobeNr from GLOBALSTAMDATA where GLOBALSTAMDATA.POSTNR=2 Into :UseVareGrpLobeNr; 
    if (UseVareGrpLobeNr=1) then 
    begin 
      select VAREGRUPPER.V509INdex, VAREGRUPPER.LobeNummer from VAREGRUPPER where VAREGRUPPER.NAVN=NEW.VAREGRPID into :VareGrpV509Index, :VareGrpLobeNr; 
      VareGrpLobeNr = VareGrpLobeNr + 1; 
      Update VAREGRUPPER set VAREGRUPPER.LobeNummer = :VareGrpLobeNr Where NAVN=NEW.VAREGRPID; 
      strVareGrpLobeNr = VareGrpLobeNr; 
      if(CHAR_LENGTH(strVareGrpLobeNr)=1) then 
      begin 
        New.kategori1 = :VareGrpV509Index || '-000' || + :VareGrpLobeNr; 
      end 
      else 
      if(CHAR_LENGTH(strVareGrpLobeNr)=2) then 
      begin 
        New.kategori1 = :VareGrpV509Index || '-00' || + :VareGrpLobeNr; 
      end 
      else 
      if(CHAR_LENGTH(strVareGrpLobeNr)=3) then 
      begin 
        New.kategori1 = :VareGrpV509Index || '-0' || + :VareGrpLobeNr; 
      end      
      else 
      if(CHAR_LENGTH(strVareGrpLobeNr)=4) then 
      begin 
        New.kategori1 = :VareGrpV509Index || '-' || + :VareGrpLobeNr; 
      end 
      else 
      begin 
        New.kategori1 = :VareGrpV509Index || '-' || + :VareGrpLobeNr; 
      end 
    end 
  end 
end^

