/* Trigger: WEBKUNDER_WEBDANMARK_DELETE */
CREATE OR ALTER TRIGGER WEBKUNDER_WEBDANMARK_DELETE FOR WEBKUNDER_WEBDANMARK
INACTIVE AFTER DELETE POSITION 10
AS
/*
  DISABLED 
  DISABLED as of 27-08-2025 due to Kaufmann has changed to ordinary weborders
  DISABLED 
*/
DECLARE VARIABLE V VARCHAR(50);
DECLARE VARIABLE LKUNDEID VARCHAR(10);
DECLARE VARIABLE LSTR VARCHAR(30);
BEGIN
  IF (USER = 'WEBDANMARK') THEN
  BEGIN
    IF ((OLD.EMAIL <> '') AND
        (NOT OLD.EMAIL IS NULL)) THEN
    BEGIN
      LKUNDEID = CAST(OLD.ID AS VARCHAR(10));

      LSTR = 'DC PAKKELABEL (' || LKUNDEID || ')';
      INSERT INTO USER_TRANS (
          BRUGER,
          HVAD)
      VALUES (
          USER,
          :LSTR);
      DELETE FROM WEBSHOP_POSTDK
      WHERE
          WEBSHOP_POSTDK.EMAIL = OLD.EMAIL;

      LSTR = 'DC ORDER PAYMENT (' || LKUNDEID || ')';
      INSERT INTO USER_TRANS (
          BRUGER,
          HVAD)
      VALUES (
          USER,
          :LSTR);
      FOR SELECT
              UDVALG_MASTER.BONNR
          FROM UDVALG_MASTER
          WHERE
              UDVALG_MASTER.UDVALGEMAIL = OLD.EMAIL
              AND UDVALG_MASTER.BONTYPE <> 0
          INTO :V
      DO
        DELETE FROM UDVALG_BETVEJ
        WHERE
            BONNR = :V;

      LSTR = 'DC ORDER DELIVERY (' || LKUNDEID || ')';
      INSERT INTO USER_TRANS (
          BRUGER,
          HVAD)
      VALUES (
          USER,
          :LSTR);
      FOR SELECT
              UDVALG_MASTER.BONNR
          FROM UDVALG_MASTER
          WHERE
              UDVALG_MASTER.UDVALGEMAIL = OLD.EMAIL
              AND UDVALG_MASTER.BONTYPE <> 0
          INTO :V
      DO
        DELETE FROM UDVALG_LEVERINGSADRESSE
        WHERE
            BONNR = :V;

      LSTR = 'DC ORDER LINES (' || LKUNDEID || ')';
      INSERT INTO USER_TRANS (
          BRUGER,
          HVAD)
      VALUES (
          USER,
          :LSTR);
      FOR SELECT
              UDVALG_MASTER.BONNR
          FROM UDVALG_MASTER
          WHERE
              UDVALG_MASTER.UDVALGEMAIL = OLD.EMAIL
              AND UDVALG_MASTER.BONTYPE <> 0
          INTO :V
      DO
        DELETE FROM UDVALG_DETAIL
        WHERE
            BONNR = :V;

      LSTR = 'DC ORDER HEAD(' || LKUNDEID || ')';
      INSERT INTO USER_TRANS (
          BRUGER,
          HVAD)
      VALUES (
          USER,
          :LSTR);
      DELETE FROM UDVALG_MASTER
      WHERE
          UDVALG_MASTER.UDVALGEMAIL = OLD.EMAIL
          AND UDVALG_MASTER.BONTYPE <> 0;

    END

    IF (OLD.KUNDEID2 <> '') THEN
    BEGIN
      LSTR = 'DC TRANS 1 (' || LKUNDEID || ')';
      INSERT INTO USER_TRANS (
          BRUGER,
          HVAD)
      VALUES (
          USER,
          :LSTR);
      UPDATE TRANSAKTIONER SET
          DEBNR = ''
      WHERE
          DEBNR = OLD.KUNDEID2
          AND ART IN (0, 1, 16, 23)
          AND DATO >= '2011-01-01';

      LSTR = 'DC TRANS 2(' || LKUNDEID || ')';
      INSERT INTO USER_TRANS (
          BRUGER,
          HVAD)
      VALUES (
          USER,
          :LSTR);
      UPDATE TRANSAKTIONER SET
          BETVEJ = 'Slettet',
          LEVNAVN = 'Slettet',
          FARVE_NAVN = 'Slettet',
          LAENGDE_NAVN = 'Slettet',
          STOERRELSE_NAVN = 'Slettet',
          VAREGRPID = 'Slettet'
      WHERE
          DEBNR = OLD.KUNDEID2
          AND ART = 304;
    END

    LSTR = 'DC CUSTOMER (' || LKUNDEID || ')';
    INSERT INTO USER_TRANS (
        BRUGER,
        HVAD)
    VALUES (
        USER,
        :LSTR);
    DELETE FROM WEBKUNDESALG_WEBDANMARK
    WHERE
        WEBKUNDESALG_WEBDANMARK.KUNDEID = OLD.KUNDEID2;
    INSERT INTO USER_TRANS (
        BRUGER,
        HVAD)
    VALUES (
        USER,
        'DC DONE');

  END
END^

