/* Trigger: WEBSHOP_VARIANTCHANGE */
CREATE OR ALTER TRIGGER WEBSHOP_VARIANTCHANGE FOR VAREFRVSTR_DETAIL
INACTIVE BEFORE UPDATE POSITION 35
/*
  DISABLED 
  DISABLED as of 27-08-2025 due to Kaufmann has changed to ordinary weborders
  DISABLED 
*/

AS
DECLARE VARIABLE WEBV INTEGER;
DECLARE VARIABLE LVGRPNRI INTEGER;
DECLARE VARIABLE LLAGERBEHOLDNING1 NUMERIC(18,2);
DECLARE VARIABLE LLAGERBEHOLDNING2 NUMERIC(18,2);
DECLARE VARIABLE LLAGERBEHOLDNING3 NUMERIC(18,2);
DECLARE VARIABLE LLAGERBEHOLDNING4 NUMERIC(18,2);
DECLARE VARIABLE LAXEL INTEGER;
DECLARE VARIABLE LKAUFMANN INTEGER;
DECLARE VARIABLE LQUINT INTEGER;
DECLARE VARIABLE LQUINTESS INTEGER;
DECLARE VARIABLE LWEBGRP1 INTEGER;
DECLARE VARIABLE LWEBGRP2 INTEGER;
DECLARE VARIABLE LWEBGRP3 INTEGER;
DECLARE VARIABLE LWEBGRP4 INTEGER;
DECLARE VARIABLE LLEVERNAVN VARCHAR(30);
BEGIN
  WEBV = 0;
  LVGRPNRI = 0;
  LLAGERBEHOLDNING1 = 0;
  LLAGERBEHOLDNING2 = 0;
  LLAGERBEHOLDNING3 = 0;
  LLAGERBEHOLDNING4 = 0;
  LWEBGRP1 = 0;
  LWEBGRP2 = 0;
  LWEBGRP3 = 0;
  LWEBGRP4 = 0;
  LAXEL = 0;
  LKAUFMANN = 0;
  LQUINT = 0;
  LQUINTESS = 0;
  LLEVERNAVN = NEW.LEVERID;
  IF ((OLD.ANTALSTK <> NEW.ANTALSTK)) THEN
  BEGIN
    /*Henter WEBVarer status*/
    SELECT
        WEBVARER
    FROM VARER
    WHERE
        PLU_NR = NEW.VAREPLU_ID
    INTO :WEBV;
    IF (:WEBV > 0) THEN
    BEGIN
      /*If this is an webitem then fetch webgroups already assigned*/
      IF (EXISTS(SELECT
                     VARER_WEBGRUPPER.WEBGRUPPE
                 FROM VARER_WEBGRUPPER
                 WHERE
                     VARER_WEBGRUPPER.VAREPLU_ID = NEW.VAREPLU_ID
                     AND VARER_WEBGRUPPER.WEBGRUPPE = 1)) THEN
      BEGIN
        LWEBGRP1 = 1;
      END
      IF (EXISTS(SELECT
                     VARER_WEBGRUPPER.WEBGRUPPE
                 FROM VARER_WEBGRUPPER
                 WHERE
                     VARER_WEBGRUPPER.VAREPLU_ID = NEW.VAREPLU_ID
                     AND VARER_WEBGRUPPER.WEBGRUPPE = 2)) THEN
      BEGIN
        LWEBGRP2 = 1;
      END
      IF (EXISTS(SELECT
                     VARER_WEBGRUPPER.WEBGRUPPE
                 FROM VARER_WEBGRUPPER
                 WHERE
                     VARER_WEBGRUPPER.VAREPLU_ID = NEW.VAREPLU_ID
                     AND VARER_WEBGRUPPER.WEBGRUPPE = 3)) THEN
      BEGIN
        LWEBGRP3 = 1;
      END
      IF (EXISTS(SELECT
                     VARER_WEBGRUPPER.WEBGRUPPE
                 FROM VARER_WEBGRUPPER
                 WHERE
                     VARER_WEBGRUPPER.VAREPLU_ID = NEW.VAREPLU_ID
                     AND VARER_WEBGRUPPER.WEBGRUPPE = 4)) THEN
      BEGIN
        LWEBGRP4 = 1;
      END
    END

    /*Henter varegruppens nummer*/
    SELECT
        CAST(V509INDEX AS INTEGER)
    FROM VAREGRUPPER
    WHERE
        NAVN = NEW.VAREGRPID
    INTO :LVGRPNRI;

    /*Kontrollerer for WEBGrupe 1, hvis den ikke findes*/
    IF ((:LVGRPNRI >= 30) AND
        (:LVGRPNRI <= 89) AND
        (:LWEBGRP1 = 0)) THEN
    BEGIN
      SELECT
          SUM(VAREFRVSTR_DETAIL.ANTALSTK)
      FROM VAREFRVSTR_DETAIL
      WHERE
          VAREFRVSTR_DETAIL.V509INDEX = NEW.V509INDEX
          AND VAREFRVSTR_DETAIL.AFDELING_ID IN ('010', '040')
      INTO :LLAGERBEHOLDNING1;
      /*if (NEW.AFDELING_ID IN ('010','040')) then*/
      /*Changed as of 25-03-2025 according to Jira issue KE-20*/
      IF (NEW.AFDELING_ID IN ('010', '002')) THEN
        LLAGERBEHOLDNING1 = :LLAGERBEHOLDNING1 + NEW.ANTALSTK - OLD.ANTALSTK;
      IF (:LLAGERBEHOLDNING1 <> 0) THEN
      BEGIN
        INSERT INTO VARER_WEBGRUPPER (
            VAREPLU_ID,
            WEBGRUPPE)
        VALUES (
            NEW.VAREPLU_ID,
            1);
        LAXEL = 1;
      END
    END

    /*Kontrollerer for WEBGrupe 2, hvis den ikke findes.*/
    IF ((:LVGRPNRI >= 01) AND
        (:LVGRPNRI <= 59) AND
        (:LWEBGRP2 = 0)) THEN
    BEGIN
      SELECT
          SUM(VAREFRVSTR_DETAIL.ANTALSTK)
      FROM VAREFRVSTR_DETAIL
      WHERE
          VAREFRVSTR_DETAIL.V509INDEX = NEW.V509INDEX
          AND VAREFRVSTR_DETAIL.AFDELING_ID IN ('025', '050')
      INTO :LLAGERBEHOLDNING2;
      IF (NEW.AFDELING_ID IN ('025', '050')) THEN
        LLAGERBEHOLDNING2 = :LLAGERBEHOLDNING2 + NEW.ANTALSTK - OLD.ANTALSTK;
      IF (:LLAGERBEHOLDNING2 <> 0) THEN
      BEGIN
        LKAUFMANN = 1;
        INSERT INTO VARER_WEBGRUPPER (
            VAREPLU_ID,
            WEBGRUPPE)
        VALUES (
            NEW.VAREPLU_ID,
            2);
      END
    END

    /*Kontrollerer for WEBGrupe 3, hvis den ikke findes.*/
    IF ((:LVGRPNRI >= 60) AND
        (:LVGRPNRI <= 89) AND
        (:LWEBGRP3 = 0)) THEN
    BEGIN
      SELECT
          SUM(VAREFRVSTR_DETAIL.ANTALSTK)
      FROM VAREFRVSTR_DETAIL
      WHERE
          VAREFRVSTR_DETAIL.V509INDEX = NEW.V509INDEX
          AND VAREFRVSTR_DETAIL.AFDELING_ID IN ('049')
      INTO :LLAGERBEHOLDNING3;
      IF (NEW.AFDELING_ID IN ('049')) THEN
        LLAGERBEHOLDNING3 = :LLAGERBEHOLDNING3 + NEW.ANTALSTK - OLD.ANTALSTK;
      IF (:LLAGERBEHOLDNING3 <> 0) THEN
      BEGIN
        LQUINT = 1;
        INSERT INTO VARER_WEBGRUPPER (
            VAREPLU_ID,
            WEBGRUPPE)
        VALUES (
            NEW.VAREPLU_ID,
            3);
      END
    END

    /*Kontrollerer for WEBGrupe 4, hvis den ikke findes.*/
    IF ((:LVGRPNRI >= 161) AND
        (:LVGRPNRI <= 190) AND
        (:LWEBGRP4 = 0)) THEN
    BEGIN
      SELECT
          SUM(VAREFRVSTR_DETAIL.ANTALSTK)
      FROM VAREFRVSTR_DETAIL
      WHERE
          VAREFRVSTR_DETAIL.V509INDEX = NEW.V509INDEX
          AND VAREFRVSTR_DETAIL.AFDELING_ID IN ('051')
      INTO :LLAGERBEHOLDNING4;
      IF (NEW.AFDELING_ID IN ('051')) THEN
        LLAGERBEHOLDNING4 = :LLAGERBEHOLDNING4 + NEW.ANTALSTK - OLD.ANTALSTK;
      IF (:LLAGERBEHOLDNING4 <> 0) THEN
      BEGIN
        LQUINTESS = 1;
        INSERT INTO VARER_WEBGRUPPER (
            VAREPLU_ID,
            WEBGRUPPE)
        VALUES (
            NEW.VAREPLU_ID,
            4);
      END
    END

    IF ((:LAXEL = 1) OR
        (:LKAUFMANN = 1) OR
        (:LQUINT = 1) OR
        (:LQUINTESS = 1)) THEN
    BEGIN
      /*Setting item to be webitem*/
      IF (:WEBV = 0) THEN
      BEGIN
        UPDATE VARER SET
            WEBVARER = 1
        WHERE
            PLU_NR = NEW.VAREPLU_ID;
        WEBV = 1;
      END
    END

    IF (:WEBV > 0) THEN
    BEGIN
      /*Aktive*/
      IF (NOT EXISTS(SELECT
                         WEBSHOPSTOCK_WEBDANMARK.V509INDEX
                     FROM WEBSHOPSTOCK_WEBDANMARK
                     WHERE
                         WEBSHOPSTOCK_WEBDANMARK.V509INDEX = NEW.V509INDEX
                         AND WEBSHOPSTOCK_WEBDANMARK.AFD_ID = NEW.AFDELING_ID)) THEN
      BEGIN
        INSERT INTO WEBSHOPSTOCK_WEBDANMARK (
            WEBSHOPSTOCK_WEBDANMARK.V509INDEX,
            WEBSHOPSTOCK_WEBDANMARK.AFD_ID,
            WEBSHOPSTOCK_WEBDANMARK.HVTYPE)
        VALUES (
            NEW.V509INDEX,
            NEW.AFDELING_ID,
            "TRIGGER");
      END
    END
  END
END^

