CREATE OR ALTER PROCEDURE BEREGN_GNSNTLAGER (
    NEWSTK NUMERIC(15,2),
    OLDSTK NUMERIC(15,2),
    NEWKPR NUMERIC(15,2),
    OLDKPR NUMERIC(15,2),
    NEWSPR NUMERIC(15,2),
