CREATE OR ALTER PROCEDURE DIVBYZERO (
    DIVVALUE NUMERIC(15,2),
    DIVIDER NUMERIC(15,2))
RETURNS (
    DIVRESULT NUMERIC(15,2))
AS
BEGIN
     if (Not (DIVIDER)=0) Then
     BEGIN
        DIVRESULT = (DIVVALUE) / (DIVIDER);
        SUSPEND;
        EXIT;
     END
     else
     BEGIN
        DIVRESULT = 0;
        SUSPEND;
        EXIT;
     END
  END^


CREATE OR ALTER PROCEDURE DK_UPPER (
    IN_P VARCHAR(30))
RETURNS (
    OUT_P VARCHAR(30))
AS
DECLARE VARIABLE TSTR VARCHAR(30); 
DECLARE VARIABLE II INTEGER; 
begin          
  ii = 1; 
  TStr = ''; 
  if ((IN_P='') or (IN_P is null)) then 
  begin 
    TStr = ''; 
  end 
  else 
  begin 
    while (ii <= CHAR_LENGTH(in_p)) do 
    begin 
      if (SUBSTRING(In_P FROM ii FOR 1)='') then 
      begin 
        Tstr = Tstr || ''; 
      end 
      else 
      begin 
        if (SUBSTRING(In_P FROM ii FOR 1)='') then 
        begin 
          Tstr = Tstr || ''; 
        end 
        else 
        begin 
          if (SUBSTRING(In_P FROM ii FOR 1)='') then 
          begin 
             Tstr = Tstr || ''; 
          end 
          else 
          begin 
            Tstr = Tstr || Upper(SUBSTRING(In_P FROM ii FOR 1)); 
          end 
        end 
      end 
      ii = ii + 1; 
    end 
  end 
  Out_P = TStr; 
  suspend; 
end^


