CREATE OR ALTER PROCEDURE INS_UPS_LEVVGRP_LAGER (
    LEV_NAVN VARCHAR(30),
    LEV_V509 VARCHAR(30),
    LEV_V509UNIKT VARCHAR(30),
    VGRP_NAVN VARCHAR(30),
    VGRP_V509 VARCHAR(30),
    VGRP_V509UNIKT VARCHAR(30),
    VGRP_HOVEDGRP INTEGER,
    AFDELING_ID VARCHAR(15),
    AAR INTEGER,
    MDR INTEGER,
    LSTK NUMERIC(15,2),
    LKPR NUMERIC(15,2),
    LSPR NUMERIC(15,2),
    LSPR_MOMS NUMERIC(15,2))
AS
begin 
    INSERT INTO LEVVGRP_LAGER ( 
      LEV_NAVN, 
      LEV_V509, 
      LEV_V509UNIKT, 
      VGRP_NAVN, 
      VGRP_V509, 
      VGRP_V509UNIKT, 
      VGRP_HOVEDGRP,      
      AFDELING_ID, 
      AAR, 
      MDR, 
      LSTK, 
      LKPR, 
      LSPR, 
      LSPR_MOMS) 
    VALUES ( 
      :LEV_NAVN, 
      :LEV_V509, 
      :LEV_V509UNIKT, 
      :VGRP_NAVN, 
      :VGRP_V509,     
      :VGRP_V509UNIKT, 
      :VGRP_HOVEDGRP, 
      :AFDELING_ID, 
      :AAR, 
      :MDR, 
      :LSTK, 
      :LKPR, 
      :LSPR, 
      :LSPR_MOMS); 
 
/*    WHEN SQLCODE -803 DO */ 
/*    WHEN SQLCODE -901 DO */ 
/*    WHEN GDSCODE lock_conflict DO */ 
    WHEN GDSCODE no_dup DO 
      UPDATE LEVVGRP_LAGER SET 
        LEV_V509 = :LEV_V509, 
        LEV_V509UNIKT = :LEV_V509UNIKT, 
        VGRP_V509 = :VGRP_V509, 
        VGRP_V509UNIKT = :VGRP_V509UNIKT, 
        LSTK = :LSTK, 
        LKPR = :LKPR, 
        LSPR = :LSPR, 
        LSPR_MOMS = :LSPR_MOMS 
      WHERE 
        LEV_NAVN = :LEV_NAVN AND 
        VGRP_NAVN = :VGRP_NAVN AND 
        VGRP_HOVEDGRP = :VGRP_HOVEDGRP AND 
        AFDELING_ID = :AFDELING_ID AND 
        AAR = :AAR AND 
        MDR = :MDR; 
 
end^


