CREATE OR ALTER PROCEDURE OPRET_AFDVARER (
    PENGROSPRIS NUMERIC(15,2),
    PVAREPLU_ID VARCHAR(30),
    PVEJETKOSTPRISSTK NUMERIC(15,2),
    PMINBEHOLDNING NUMERIC(15,2),
    PGENBESTILLING NUMERIC(15,2),
    PNORMALSTKANTAL NUMERIC(15,2),
    PVALUTA_NAVN VARCHAR(30),
    PPROVISION NUMERIC(15,2),
    POPRETTELSESDATO DATE)
AS
DECLARE VARIABLE LDEPARTMENT TYPE OF COLUMN AFDELING.AFDELINGSNUMMER;
DECLARE VARIABLE LCURRENCY TYPE OF COLUMN VALUTA.TEKST;
DECLARE VARIABLE LCREATECURRENCY TYPE OF COLUMN VALUTA.TEKST;
BEGIN
  /*This procedure will create the head item summary records per department. Summary over all variants*/
  /*Parameters are keept for EasyPOS Kontor v1 legacy calls*/
  FOR SELECT
          A.AFDELINGSNUMMER,
          S.STDVALUTA
      FROM AFDELING A
          INNER JOIN STAMDATA_PRG_EXT S ON
                S.AFDELING_ID = A.AFDELINGSNUMMER
      INTO :LDEPARTMENT,
           :LCURRENCY
  DO
  BEGIN
    /*If the transfered currency is an empty string/null,  set it to what we just fetched*/
    /*Kept this way to support legacy code in EasyPOS KOntor v1*/
    IF (COALESCE(:PVALUTA_NAVN, '') = '') THEN
    BEGIN
      :LCREATECURRENCY = :LCURRENCY;
    END
    ELSE
    BEGIN
      :LCREATECURRENCY = :PVALUTA_NAVN;
    END

    IF (NOT EXISTS(SELECT
                       VAREPLU_ID
                   FROM VARER_DETAIL
                   WHERE
                       VAREPLU_ID = :PVAREPLU_ID
                       AND AFDELING_ID = :LDEPARTMENT)) THEN
    BEGIN
      INSERT INTO VARER_DETAIL (
          VAREPLU_ID,
          VEJETKOSTPRISSTK,
          ENGROSPRIS,
          MINBEHOLDNING,
          GENBESTILLING,
          NORMALSTKANTAL,
          VALUTA_NAVN,
          PROVISION,
          OPRETTELSESDATO,
          AFDELING_ID)
      VALUES (
          :PVAREPLU_ID,
          COALESCE(:PVEJETKOSTPRISSTK, 0),
          COALESCE(:PENGROSPRIS, 0),
          COALESCE(:PMINBEHOLDNING, 0),
          COALESCE(:PGENBESTILLING, 0),
          COALESCE(:PNORMALSTKANTAL, 1),
          :LCREATECURRENCY,
          COALESCE(:PPROVISION, 0),
          :POPRETTELSESDATO,
          :LDEPARTMENT);
    END
  END
END^


