CREATE OR ALTER PROCEDURE P_CALCULATECUSTOMERBONUS (
    I_RECEIPTNUMBER INTEGER,
    I_MASTERID TYPE OF COLUMN SALE_HEADER.ID,
    I_DATE365DAYSAGO DATE,
    I_UPDATECUSTOMER INTEGER)
RETURNS (
    O_MESSAGE BLOB SUB_TYPE TEXT SEGMENT SIZE 80,
    O_POINTS_EARNED NUMERIC(15,2),
    O_POINTS_USED NUMERIC(15,2),
    O_POINTS_BALANCE NUMERIC(15,2))
AS
/* 
  This procedure will calculate how much a customer gets in bonus. 
  The routine will fetch sales information regarding: 
 
   - Sold items 
   - Payments 
 
  to calcu?ate the bonus to a customer. 
  The routine will use these tables: 
 
  - SALE_HEADER 
  - SALE_ITEMS 
  - SALE_PAYMENTS 
*/
DECLARE VARIABLE llogactions BLOB SUB_TYPE 1 SEGMENT SIZE 80;
DECLARE VARIABLE lcustomernumber TYPE OF COLUMN sale_header.customer;
DECLARE VARIABLE lmasterid TYPE OF COLUMN sale_header.id;
DECLARE VARIABLE lsolddiscounttype TYPE OF COLUMN sale_items.discounttype;
DECLARE VARIABLE litembarcode TYPE OF COLUMN sale_items.barcode;
DECLARE VARIABLE lsoldquantity TYPE OF COLUMN sale_items.quantity;
DECLARE VARIABLE lsoldsalesprice TYPE OF COLUMN sale_items.salesprice;
DECLARE VARIABLE lsolddiscount TYPE OF COLUMN sale_items.discount;
DECLARE VARIABLE lsolddepartment TYPE OF COLUMN sale_header.department;
DECLARE VARIABLE lsoldmachine TYPE OF COLUMN sale_header.machine;
DECLARE VARIABLE lsoldclerk TYPE OF COLUMN sale_header.clerk;
DECLARE VARIABLE ltotalsalespriceforsale FLOAT;
DECLARE VARIABLE lpointstoaddtocustomer FLOAT;
DECLARE VARIABLE ltemppoints FLOAT;
DECLARE VARIABLE lpointsper1000 FLOAT;
DECLARE VARIABLE lpaidwithpointsamount FLOAT;
DECLARE VARIABLE lpaidwithpoints FLOAT;
DECLARE VARIABLE litemsupplier TYPE OF COLUMN varer.leverid;
DECLARE VARIABLE lcustomergroup TYPE OF COLUMN kundedata.kundegruppe;
DECLARE VARIABLE litemgroup TYPE OF COLUMN varer.varegrpid;
DECLARE VARIABLE litemfamilygroup VARCHAR(100);
DECLARE VARIABLE lsalespointamount FLOAT;
DECLARE VARIABLE lskipcalculatepoints SMALLINT;
DECLARE VARIABLE lbarcode_factor FLOAT;
DECLARE VARIABLE litemsupplier_factor FLOAT;
DECLARE VARIABLE lcustomergroup_factor FLOAT;
DECLARE VARIABLE litemgroup_factor FLOAT;
DECLARE VARIABLE litemfamilygroup_factor FLOAT;
DECLARE VARIABLE lsolddepartment_factor FLOAT;
DECLARE VARIABLE lsolddepartment_webfactor FLOAT;
DECLARE VARIABLE lhighestfactor FLOAT;
DECLARE VARIABLE lbarcode_active SMALLINT;
DECLARE VARIABLE litemsupplier_active SMALLINT;
DECLARE VARIABLE lcustomergroup_active SMALLINT;
DECLARE VARIABLE litemgroup_active SMALLINT;
DECLARE VARIABLE litemfamilygroup_active SMALLINT;
DECLARE VARIABLE lsolddepartment_active SMALLINT;
DECLARE VARIABLE lsolddepartment_manrabat SMALLINT;
DECLARE VARIABLE lsolddepartment_autorabat SMALLINT;
DECLARE VARIABLE lcustomerbonusname TYPE OF COLUMN kundebonus_setup.navn;
DECLARE VARIABLE lcustomerbonuspoint TYPE OF COLUMN kundebonus_setup.point;
DECLARE VARIABLE lcustomerbonusminimumlimit TYPE OF COLUMN kundebonus_setup.min_limit;
DECLARE VARIABLE lcustomerbonusmaximumlimit TYPE OF COLUMN kundebonus_setup.max_limit;
DECLARE VARIABLE lcustomerbonusactive TYPE OF COLUMN kundebonus_setup.aktiv;
DECLARE VARIABLE lcustomeramonutpastyear FLOAT;
DECLARE VARIABLE ltotadsalespriceofsale FLOAT;
DECLARE VARIABLE lamountofcustomersale FLOAT;
DECLARE VARIABLE lfamilyarticlenumber TYPE OF COLUMN familyvarer.familynummer;
DECLARE VARIABLE lallfloatvalues VARCHAR(1000);
DECLARE VARIABLE lmaximumvalue FLOAT;
DECLARE VARIABLE lminimumvalue FLOAT;
DECLARE VARIABLE lcurrentcustomerpoints FLOAT;
DECLARE VARIABLE lendingpoints FLOAT;
DECLARE VARIABLE lcurrentdate DATE;
DECLARE VARIABLE lsoldwebordre INTEGER;
DECLARE VARIABLE lpointsusedbyweborder FLOAT;
BEGIN
  /* Step 1: Fetch sale and check customer number */
  IF ((i_MasterID <> 0) AND
      (NOT(i_MasterID IS NULL))) THEN
  BEGIN
    SELECT
        Sale_header.Customer,
        Sale_header.ID
    FROM Sale_header
    WHERE
        Sale_header.ID = :i_MasterID
    INTO :lCustomerNumber,
         :lMasterID;
  END
  ELSE
  BEGIN
    SELECT
        Sale_header.Customer,
        Sale_header.ID
    FROM Sale_header
    WHERE
        Sale_header.Recieptnumber = :I_RECEIPTNUMBER
    INTO :lCustomerNumber,
         :lMasterID;
  END

  IF (lCustomerNumber <> '') THEN
  BEGIN
    /*There is a customer to this sale*/

    /*Log what happens*/
    IF (I_UPDATECUSTOMER <> 1) THEN
      :lLogActions = CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || 'mivi SIMULATION' || ASCII_CHAR(13) || ASCII_CHAR(10);
    ELSE
      :lLogActions = '';
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || 'Iterating receipt: ' || :i_receiptnumber || ' to customer: ' || :lCustomerNumber || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '=====================================================' || ASCII_CHAR(13) || ASCII_CHAR(10);

    /*INITIALIZE VARIABLES*/
    lSoldDiscountType = '';
    lSoldDiscount = 0;

    lItemBarcode = '';
    lSoldQuantity = 0;
    lSoldSalesprice = 0;
    lSoldDepartment = '';
    lsoldmachine = '';
    lsoldclerk = 0;
    lSoldWebOrdre = 0;

    lTotalSalesPriceForSale = 0;

    lPointsToAddToCustomer = 0;
    lTempPoints = 0;
    lPointsPer1000 = 0;
    lPaidWithPointsAmount = 0;
    lPaidWithPoints = 0;

    lItemSupplier = '';
    lCustomerGroup = '';
    lItemGroup = '';
    lItemFamilyGroup = '';

    lSalesPointAmount = 0;
    lSkipCalculatePoints = 0;

    lBarcode_Factor = 0;
    lItemSupplier_factor = 0;
    lCustomerGroup_factor = 0;
    lItemGroup_factor = 0;
    lItemFamilyGroup_factor = 0;
    lSoldDepartment_factor = 0;
    lSoldDepartment_webfactor = 0;

    lHighestFactor = 0;
    lBarcode_Active = 0;
    lItemSupplier_active = 0;
    lCustomerGroup_active = 0;
    lItemGroup_active = 0;
    lItemFamilyGroup_active = 0;
    lSoldDepartment_active = 0;
    lSoldDepartment_manrabat = 0;
    lSoldDepartment_autorabat = 0;

    lCustomerBonusName = '';
    lCustomerBonusPoint = 0;
    lCustomerBonusMinimumLimit = 0;
    lCustomerBonusMaximumLimit = 0;
    lCustomerBonusActive = 0;

    lCustomerAmonutPastYear = 0;
    lTotadSalespriceOfSale = 0;
    lAmountOfCustomerSale = 0;
    lFamilyArticleNumber = 0;
    lAllFloatValues = '';
    lMaximumValue = 0;
    lMinimumValue = 0;

    /* Fetching ALL customer bonus settings AT this TIME - All will be saved to log*/
    FOR SELECT
            KUNDEBONUS_SETUP.NAVN,
            KUNDEBONUS_SETUP.POINT,
            KUNDEBONUS_SETUP.MIN_LIMIT,
            KUNDEBONUS_SETUP.MAX_LIMIT,
            KUNDEBONUS_SETUP.AKTIV
        FROM KUNDEBONUS_SETUP
        ORDER BY
            MIN_LIMIT ASC
        INTO :lCustomerBonusName,
             :lCustomerBonusPoint,
             :lCustomerBonusMinimumLimit,
             :lCustomerBonusMaximumLimit,
             :lCustomerBonusActive
    DO
    BEGIN
      /*Log*/
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || 'Bonus setup:' || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Name: ' || :lCustomerBonusName || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Point: ' || ROUND(:lCustomerBonusPoint, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Min buy: ' || ROUND(:lCustomerBonusMinimumLimit, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Aktive: ' || ROUND(:lCustomerBonusActive, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  ' || ASCII_CHAR(13) || ASCII_CHAR(10);
    END

    /* Fetching what customer has bought sinse the date transferred*/
    SELECT
        SUM(SALGSBELOB) AS SPR
    FROM KUNDEBONUS_LOG
    WHERE
        KUNDEBONUS_LOG.KUNDENUMMER = :lCustomerNumber AND
        KUNDEBONUS_LOG.DATO >= :I_Date365DaysAgo
    INTO :lCustomerAmonutPastYear;

    /* Fetching which setting to use regarding how much the customer has bought */
    /* We need to know wether the sale is a sale or a return sale*/
    SELECT
        SUM(sale_items.salesprice)
    FROM Sale_header
        INNER JOIN Sale_items ON (Sale_header.Id = Sale_items.Master_id)
    WHERE
        Sale_header.ID = :lmasterid
    INTO :lTotadSalespriceOfSale;

    IF (lTotadSalespriceOfSale < 0) THEN
    BEGIN
      :lAmountOfCustomerSale = lCustomerAmonutPastYear;
    END
    ELSE
    BEGIN
      :lAmountOfCustomerSale = lCustomerAmonutPastYear + lTotadSalespriceOfSale;
    END

    /*Log*/
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '=====================================================' || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || 'Customer sale:' || ASCII_CHAR(13) || ASCII_CHAR(10);
    IF (lCustomerAmonutPastYear IS NULL) THEN
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Customer buyings past 365 days: 0.00' || ASCII_CHAR(13) || ASCII_CHAR(10);
    ELSE
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Customer buyings past 365 days: ' || :lCustomerAmonutPastYear || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Customer buyings current sale: ' || ROUND(:lTotadSalespriceOfSale, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  ' || ASCII_CHAR(13) || ASCII_CHAR(10);

    /*Lacate which customer bouns to use*/
    SELECT FIRST 1
        KUNDEBONUS_SETUP.NAVN,
        KUNDEBONUS_SETUP.POINT,
        KUNDEBONUS_SETUP.MIN_LIMIT,
        KUNDEBONUS_SETUP.MAX_LIMIT,
        KUNDEBONUS_SETUP.AKTIV
    FROM KUNDEBONUS_SETUP
    WHERE
        /*Value must be bigger than minimum limit, or value must be negative and minimum limit 0*/ 
        ((KUNDEBONUS_SETUP.MIN_LIMIT <= :lAmountOfCustomerSale) OR (KUNDEBONUS_SETUP.MIN_LIMIT = 0 AND 
        :lAmountOfCustomerSale < 0)) AND 
        KUNDEBONUS_SETUP.AKTIV = 1 
        /*Order so the biggest settings will be the first*/ 
    ORDER BY
        MIN_LIMIT DESC
    INTO :lCustomerBonusName,
         :lCustomerBonusPoint,
         :lCustomerBonusMinimumLimit,
         :lCustomerBonusMaximumLimit,
         :lCustomerBonusActive;

    /*Log*/
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '=====================================================' || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || 'Will use this one to calculation of point per 1000:' || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Name: ' || :lCustomerBonusName || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Point: ' || ROUND(:lCustomerBonusPoint, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Min buy: ' || ROUND(:lCustomerBonusMinimumLimit, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Aktive: ' || :lCustomerBonusActive || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  ' || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lPointsPer1000 = lCustomerBonusPoint;

    /*Fetching how the sale was paid regarding points (and points in amount)*/
    SELECT
        COALESCE(SUM(Sale_payments.amount), 0),
        COALESCE(SUM(Sale_payments.points), 0)
    FROM Sale_header
        INNER JOIN sale_payments ON (Sale_header.Id = sale_payments.Master_id AND
              sale_payments.points <> 0)
    WHERE
        Sale_header.ID = :lmasterid
    INTO :lPaidWithPointsAmount,
         :lPaidWithPoints;

    /*Log*/
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '=====================================================' || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || 'Sale paid with' || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Point: ' || ROUND(:lPaidWithPoints, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Amount: ' || ROUND(:lPaidWithPointsAmount, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  ' || ASCII_CHAR(13) || ASCII_CHAR(10);

    /* Iterate through all sales lines */
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '=====================================================' || ASCII_CHAR(13) || ASCII_CHAR(10);
    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || 'ITERATE THROUGH ALL SALES LINES' || ASCII_CHAR(13) || ASCII_CHAR(10);
    FOR SELECT
            Sale_header.Customer,
            Sale_header.Department,
            Sale_header.machine,
            Sale_header.clerk,
            Sale_items.weborder,
            Sale_items.Barcode,
            Sale_items.discount,
            Sale_items.Discounttype,
            sale_items.quantity,
            sale_items.salesprice
        FROM Sale_header
            INNER JOIN Sale_items ON (Sale_header.Id = Sale_items.Master_id)
        WHERE
            Sale_header.ID = :lmasterid
        INTO :lCustomerNumber,
             :lSoldDepartment,
             :lsoldmachine,
             :lsoldclerk,
             :lsoldwebordre,
             :lItemBarcode,
             :lSoldDiscount,
             :lSoldDiscountType,
             :lSoldQuantity,
             :lSoldSalesprice
    DO
    BEGIN
      /*Fetch information to current item*/
      lTotalSalesPriceForSale = lTotalSalesPriceForSale + lsoldsalesprice;
      SELECT
          varer.leverid,
          varer.varegrpid,
          LEVERANDOERER_DETAIL.KUNDEBONUS_AKTIV AS Lever_Aktiv,
          LEVERANDOERER_DETAIL.KUNDEBONUS_FAKTOR AS Lever_Faktor,
          VAREGRUPPER_DETAIL.KUNDEBONUS_AKTIV AS VGrp_Aktiv,
          VAREGRUPPER_DETAIL.KUNDEBONUS_FAKTOR AS VGrp_Faktor,
          STAMDATA_PRG_INT.KUNDEBONUS_AKTIV AS Afd_Aktiv,
          STAMDATA_PRG_INT.KUNDEBONUS_FAKTOR AS Afd_Faktor,
          STAMDATA_PRG_INT.KUNDEBONUS_WEBFAKTOR AS Afd_WEBFaktor,
          STAMDATA_PRG_INT.KUNDEBONUS_AUTORABATPOINT AS Afd_AutoRabatPoint,
          STAMDATA_PRG_INT.KUNDEBONUS_MANRABATPOINT AS Afd_ManuelRabatPoint,
          VARER_DETAIL.KUNDEBONUS_AKTIV AS Varer_Aktiv,
          VARER_DETAIL.KUNDEBONUS_FAKTOR AS Varer_Faktor,
          FAMILYVARER.KUNDEBONUS_AKTIV AS Fam_Aktiv,
          FAMILYVARER.FAMILYNUMMER,
          FAMILYVARER.KUNDEBONUS_FAKTOR AS Fam_Faktor,
          (SELECT
               Kundedata.KUNDEGRUPPE
           FROM kundedata
           WHERE
               Kundedata.KUNDENUMMER = :lCustomerNumber) AS KGroup,
          (SELECT
               KUNDEBONUS_AKTIV
           FROM KUNDEGRUPPE
           WHERE
               KUNDEGRUPPE = (SELECT
                                  Kundedata.KUNDEGRUPPE
                              FROM kundedata
                              WHERE
                                  Kundedata.KUNDENUMMER = :lCustomerNumber)) AS KG_Aktiv,
          (SELECT
               KUNDEBONUS_FAKTOR
           FROM KUNDEGRUPPE
           WHERE
               KUNDEGRUPPE = (SELECT
                                  Kundedata.KUNDEGRUPPE
                              FROM kundedata
                              WHERE
                                  Kundedata.KUNDENUMMER = :lCustomerNumber)) AS KG_Faktor
      FROM VAREFRVSTR_DETAIL
          INNER JOIN LEVERANDOERER_DETAIL ON VAREFRVSTR_DETAIL.AFDELING_ID = LEVERANDOERER_DETAIL.AFDELING_ID AND
                VAREFRVSTR_DETAIL.LEVERID = LEVERANDOERER_DETAIL.NAVN
          INNER JOIN VAREGRUPPER_DETAIL ON VAREFRVSTR_DETAIL.VAREGRPID = VAREGRUPPER_DETAIL.NAVN AND
                VAREFRVSTR_DETAIL.AFDELING_ID = VAREGRUPPER_DETAIL.AFDELING_ID
          INNER JOIN STAMDATA_PRG_INT ON VAREFRVSTR_DETAIL.AFDELING_ID = STAMDATA_PRG_INT.AFDELING_ID
          INNER JOIN VARER_DETAIL ON VAREFRVSTR_DETAIL.VAREPLU_ID = VARER_DETAIL.VAREPLU_ID AND
                VAREFRVSTR_DETAIL.AFDELING_ID = VARER_DETAIL.AFDELING_ID
          INNER JOIN VARER ON VARER.plu_nr = varefrvstr_detail.vareplu_id
          LEFT JOIN FAMILYVARER ON VAREFRVSTR_DETAIL.VAREPLU_ID = FAMILYVARER.VAREPLU_ID
      WHERE
          VAREFRVSTR_DETAIL.V509INDEX = :lItemBarcode AND
          VAREFRVSTR_DETAIL.AFDELING_ID = :lSoldDepartment
      INTO :lItemSupplier,
           :lItemGroup,
           :lItemSupplier_active,
           :lItemSupplier_factor,
           :lItemGroup_active,
           :lItemGroup_factor,
           :lSoldDepartment_active,
           :lSoldDepartment_factor,
           :lSoldDepartment_webfactor,
           :lSoldDepartment_autorabat,
           :lSoldDepartment_manrabat,
           :lBarcode_Active,
           :lBarcode_Factor,
           :lItemFamilyGroup_active,
           :lFamilyArticleNumber,
           :lItemFamilyGroup_factor,
           :lCustomerGroup,
           :lCustomerGroup_active,
           :lCustomerGroup_factor;

      /*Do check family - if not set, set to factor 0 (never the highest)*/
      IF (lFamilyArticleNumber IS NULL) THEN
      BEGIN
        :lItemFamilyGroup = 'Ikke tilknyttet familyvare';
        :lItemFamilyGroup_active = 1;
        :lItemFamilyGroup_factor = 0;
      END
      ELSE
      BEGIN
        :lItemFamilyGroup = CAST(lFamilyArticleNumber AS VARCHAR(100));
      END

      /* Adding to log */
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || 'SOLD ITEM' || ASCII_CHAR(13) || ASCII_CHAR(10);
      /* Iteminformation */
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || 'ITEM INFORMATION:' || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Barcode: ' || :lItemBarcode || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Department: ' || :lSoldDepartment || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Customer group.: ' || :lCustomerGroup || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Brand: ' || :lItemSupplier || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Group: ' || :lItemGroup || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Fam.Grp: ' || :lItemFamilyGroup || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Quantity: ' || ROUND(:lSoldQuantity, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Salesprice: ' || ROUND(:lSoldSalesprice, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Discount: ' || ROUND(:lSoldDiscount, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Discount type: ' || :lSoldDiscountType || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Discount mark: ' || 'Unknown' || ASCII_CHAR(13) || ASCII_CHAR(10);

      /* Factors */
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    FACTORS: ' || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Item: ' || ROUND(:lBarcode_Factor, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Department: ' || ROUND(:lSoldDepartment_factor, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Customergroup:' || ROUND(:lCustomerGroup_factor, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Brand: ' || ROUND(:lItemSupplier_factor, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Group: ' || ROUND(:lItemGroup_factor, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Familygroup: ' || ROUND(:lItemFamilyGroup_factor, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);

      /*Set string to find max (and min) value*/
      IF (lsoldwebordre = 1) THEN
      BEGIN
        :lAllFloatValues = CAST(lBarcode_Factor AS VARCHAR(25)) || ',' || CAST(lItemSupplier_factor AS VARCHAR(25)) || ',' || CAST(lItemGroup_factor AS VARCHAR(25)) || ',' || CAST(lItemFamilyGroup_factor AS VARCHAR(25)) || ',' || CAST(lSoldDepartment_WEBFactor AS VARCHAR(25)) || ',' || CAST(lSoldDepartment_factor AS VARCHAR(25)) || ',' || CAST(lCustomerGroup_factor AS VARCHAR(25));
      END
      ELSE
      BEGIN
        :lAllFloatValues = CAST(lBarcode_Factor AS VARCHAR(25)) || ',' || CAST(lItemSupplier_factor AS VARCHAR(25)) || ',' || CAST(lItemGroup_factor AS VARCHAR(25)) || ',' || CAST(lItemFamilyGroup_factor AS VARCHAR(25)) || ',' || CAST(lSoldDepartment_factor AS VARCHAR(25)) || ',' || CAST(lCustomerGroup_factor AS VARCHAR(25));
      END

      /*Find max (and min) value*/
      SELECT
          O_MAXVALUE,
          O_MINVALUE
      FROM P_GetMaxAndMinValue(:lAllFloatValues)
      INTO :lMaximumValue,
           :lMinimumValue;

      /*Set highest factor to be used*/
      :lHighestFactor = lMaximumValue;
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Max factor: ' || ROUND(:lHighestFactor, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);

      /*Active / Inactive*/
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    active / inactive: ' || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Item: ' || :lBarcode_Active || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Department: ' || :lSoldDepartment_active || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Customergroup:' || :lCustomerGroup_active || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Brand: ' || :lItemSupplier_active || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Group: ' || :lItemGroup_active || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Familygroup: ' || :lItemFamilyGroup_active || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Manuel discount: ' || :lSoldDepartment_manrabat || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Automatic discunt: ' || :lSoldDepartment_autorabat || ASCII_CHAR(13) || ASCII_CHAR(10);

      /* Do calculate bonus */
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  CALCULATED BONUS:' || ASCII_CHAR(13) || ASCII_CHAR(10);

      IF ((lBarcode_Active = 1) AND
          (lSoldDepartment_Active = 1) AND
          (lItemSupplier_Active = 1) AND
          (lCustomerGroup_Active = 1) AND
          (lItemGroup_Active = 1) AND
          (lItemFamilyGroup_Active = 1)) THEN
      BEGIN
        /* Check amount payed with points */
        IF (lPaidWithPointsAmount > 0) THEN
        BEGIN
          IF (lSoldSalesprice < lPaidWithPointsAmount) THEN
          BEGIN
            /*Not all used here. */
            :lPaidWithPointsAmount = lPaidWithPointsAmount - lSoldSalesprice;
            :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Descreasing amount paid with points (1): ' || ROUND(:lPaidWithPointsAmount, 2) || ' --> ' ||(ROUND(:lPaidWithPointsAmount, 2) - ROUND(:lSoldSalesprice, 2)) || ASCII_CHAR(13) || ASCII_CHAR(10);
          END
          ELSE
          BEGIN
            IF (lSoldSalesprice = lPaidWithPointsAmount) THEN
            BEGIN
              /* All used */
              lPaidWithPointsAmount = 0;
              :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Descreasing amount paid with points (2): ' || ROUND(:lPaidWithPointsAmount, 2) || ' --> ' ||(ROUND(:lPaidWithPointsAmount, 2) - ROUND(:lSoldSalesprice, 2)) || ASCII_CHAR(13) || ASCII_CHAR(10);
            END
            ELSE
            BEGIN
              /* Salesprice is bigger than payed with - calculate amount eligible to bonus */
              lSoldSalesprice = lSoldSalesprice - lPaidWithPointsAmount;
              :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Descreasing amount paid with points (3): ' || ROUND(:lPaidWithPointsAmount, 2) || ' --> 0.00' || ASCII_CHAR(13) || ASCII_CHAR(10);
              :lPaidWithPointsAmount = 0;
              :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    new bonus eligible amount: ' || ROUND(:lSoldSalesprice, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);

              --              IF ((lSoldDepartment_AutoRabat = 0) AND
              --                  (lSoldDiscount <> 0) AND (Upper(lsolddiscounttype)='MANUEL')) THEN
              IF ((lSoldDepartment_AutoRabat = 0) AND
                  (lSoldDiscount <> 0)) THEN
              BEGIN
                /* No bonus on any kind of discount */
                lSkipCalculatePoints = 1;
                :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    No bonus when discount given: ' || ROUND(:lSoldDiscount, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
              END
              ELSE
              BEGIN
                IF ((lSoldDepartment_ManRabat = 0) AND
                    (lSoldDiscount <> 0)) THEN
                BEGIN
                  IF (UPPER(lsolddiscounttype) <> 'MANUEL') THEN
                  BEGIN
                    /* Automatic discount - Bonus can be given */
                    lSkipCalculatePoints = 0;
                  END
                  ELSE
                  BEGIN
                    /* Manuel discount, packetsale og customer discount - no bonus */
                    lSkipCalculatePoints = 1;
                    :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    No bonus when manual discount given: ' || ROUND(:lSoldDiscount, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
                  END
                END
                ELSE
                BEGIN
                  lSkipCalculatePoints = 0;
                END
              END

              IF (lSkipCalculatePoints = 0) THEN
              BEGIN
                lSalesPointAmount = lSalesPointAmount + lSoldSalesprice;
                /* Calculate Point */
                lTempPoints = lSoldSalesprice / 1000 * lPointsPer1000 * lHighestFactor;
                lPointsToAddToCustomer = lPointsToAddToCustomer + lTempPoints;

                :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Calculate points: ' || ROUND(:lSoldSalesprice, 2) || ' using factor: ' || ROUND(:lHighestFactor, 2) || ' where : ' || ROUND(:lPointsPer1000, 2) || ' per 1000 kroner' || ASCII_CHAR(13) || ASCII_CHAR(10);
                :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    points: ' || ROUND(:lTempPoints, 2) || ' total: ' || ROUND(:lPointsToAddToCustomer, 2) || ' bonus amount: ' || ROUND(:lSalesPointAmount, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
              END
            END
          END
        END
        ELSE
        BEGIN
          /* Either not payed with point or all has been handled. */

          IF ((lSoldDepartment_AutoRabat = 0) AND
              (lSoldDiscount <> 0)) THEN
          BEGIN
            /* No bonus on any kind of discount */
            lSkipCalculatePoints = 1;
            :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    No bonus when discount given: ' || ROUND(:lSoldDiscount, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
          END
          ELSE
          BEGIN
            IF ((lSoldDepartment_ManRabat = 0) AND
                (lSoldDiscount <> 0)) THEN
            BEGIN
              IF (UPPER(lsolddiscounttype) <> 'MANUEL') THEN
              BEGIN
                /* Automatic discount - Bonus can be given */
                lSkipCalculatePoints = 0;
              END
              ELSE
              BEGIN
                /* Manuel discount, packetsale og customer discount - no bonus */
                lSkipCalculatePoints = 1;
                :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    No bonus when manual discount given: ' || ROUND(:lSoldDiscount, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
              END
            END
            ELSE
            BEGIN
              lSkipCalculatePoints = 0;
            END
          END

          IF (lSkipCalculatePoints = 0) THEN
          BEGIN
            lSalesPointAmount = lSalesPointAmount + lSoldSalesprice;
            /* Calculate Point */
            lTempPoints = lSoldSalesprice / 1000 * lPointsPer1000 * lHighestFactor;
            lPointsToAddToCustomer = lPointsToAddToCustomer + lTempPoints;

            :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Calculate points: ' || ROUND(:lSoldSalesprice, 2) || ' using factor: ' || ROUND(:lHighestFactor, 2) || ' where : ' || ROUND(:lPointsPer1000, 2) || ' per 1000 kroner' || ASCII_CHAR(13) || ASCII_CHAR(10);
            :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    points: ' || ROUND(:lTempPoints, 2) || ' total: ' || ROUND(:lPointsToAddToCustomer, 2) || ' bonus amount: ' || ROUND(:lSalesPointAmount, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
          END
        END
        :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || ASCII_CHAR(13) || ASCII_CHAR(10);
      END
      ELSE
      BEGIN
        :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  BONUS NOT ACTIVE' || ASCII_CHAR(13) || ASCII_CHAR(10);
        /* CHECK amount payed WITH points */
        IF (lPaidWithPointsAmount > 0) THEN
        BEGIN
          IF (lSoldSalesprice < lPaidWithPointsAmount) THEN
          BEGIN
            /* Not all used here. */
            lPaidWithPointsAmount = lPaidWithPointsAmount - lSoldSalesprice;
            :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Descreasing amount paid with points (4): ' || ROUND(:lPaidWithPointsAmount, 2) || ' --> ' ||(ROUND(:lPaidWithPointsAmount, 2) - ROUND(:lSoldSalesprice, 2)) || ASCII_CHAR(13) || ASCII_CHAR(10);
          END
          ELSE
          BEGIN
            IF (lSoldSalesprice = lPaidWithPointsAmount) THEN
            BEGIN
              /* All used */
              lPaidWithPointsAmount = 0;
              :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Descreasing amount paid with points (5): ' || ROUND(:lPaidWithPointsAmount, 2) || ' --> ' ||(ROUND(:lPaidWithPointsAmount, 2) - ROUND(:lSoldSalesprice, 2)) || ASCII_CHAR(13) || ASCII_CHAR(10);
            END
            ELSE
            BEGIN
              /* Not all used, but the rest is not bonus sale */
              lPaidWithPointsAmount = 0;
              :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '    Descreasing amount paid with points (6): ' || ROUND(:lPaidWithPointsAmount, 2) || ' --> 0.00' || ASCII_CHAR(13) || ASCII_CHAR(10);
            END
          END
        END
      END
    END

    /*Fetch customer current points and customer number.*/
    SELECT
        POINTSALDO
    FROM KUNDEDATA
    WHERE
        KUNDENUMMER = :lcustomernumber
    INTO :lCurrentCustomerPoints;

    SELECT
        SUM(webdwiorderpayment.amount)
    FROM webdwiorderhead
        LEFT JOIN webdwiorderpayment ON webdwiorderpayment.orderid = webdwiorderhead.orderid AND
              UPPER(webdwiorderpayment.transact) = UPPER('customEr')
    WHERE
        webdwiorderhead.cusnumber = :lcustomernumber AND
        webdwiorderhead.status = 255
    INTO :lPointsUsedByWEBOrder;

    /*Now bonus has been calculated. Set return variables*/
    :o_message = lLogActions;
    :o_points_earned = ROUND(lPointsToAddToCustomer, 2);
    :o_points_balance = ROUND((lCurrentCustomerPoints - lPointsUsedByWEBOrder), 2);
    :o_points_used = ROUND(lPaidWithPoints, 2);

    IF (I_UPDATECUSTOMER = 1) THEN
    BEGIN
      /*Do update customer and insert log */
      /*Set current date*/
      :lCurrentDate = 'NOW';

      /*Add points to customer*/
      UPDATE Kundedata SET
          POINTSALDO = POINTSALDO + ROUND((:lPointsToAddToCustomer - :lPaidWithPoints), 2)
      WHERE
          KUNDENUMMER = :lcustomernumber;
      /*Calculate points customer will end at*/
      :lEndingPoints = ROUND(:lCurrentCustomerPoints, 2) + ROUND(:lPointsToAddToCustomer, 2) - ROUND(:lPaidWithPoints, 2);

      /*Log*/
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '=====================================================' || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || 'KUNDEPOINT: ' || :lcustomernumber || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Bonuspoints before: ' || ROUND(:lCurrentCustomerPoints, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Bonuspoints Earned: ' || ROUND(:lPointsToAddToCustomer, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Bonuspoints used: ' || ROUND(:lPaidWithPoints, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Bonuspoints balance after: ' || ROUND(:lEndingPoints, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Bonuspoints used on unhandled weborders: ' || ROUND(:lPointsUsedByWEBOrder, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Sales price of the sale: ' || ROUND(:lTotalSalesPriceForSale, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);
      :lLogActions = :lLogActions || CAST(CURRENT_TIMESTAMP AS VARCHAR(20)) || ' ' || '  Eligible sales price for bonus: ' || ROUND(:lSalesPointAmount, 2) || ASCII_CHAR(13) || ASCII_CHAR(10);

      /*Insert into log regarding points*/
      INSERT INTO KUNDEBONUS_LOG (
          KUNDENUMMER,
          DATO,
          BONNR,
          SALGSBELOB,
          POINTBELOB,
          START_POINT,
          SLUT_POINT,
          OPTJENT_POINT,
          BRUGT_POINT)
      VALUES (
          :lcustomernumber,
          :lCurrentDate,
          :i_receiptnumber,
          ROUND(:lTotalSalesPriceForSale, 2),
          ROUND(:lSalesPointAmount, 2),
          ROUND(:lCurrentCustomerPoints, 2),
          ROUND(:lEndingPoints, 2),
          ROUND(:lPointsToAddToCustomer, 2),
          ROUND(:lPaidWithPoints, 2));

      /*Insert detailed log*/
      INSERT INTO KUNDEBONUS_LOG_HANDLING (
          BONNR,
          KUNDENUMMER,
          DATO,
          LOG)
      VALUES (
          :i_receiptnumber,
          :lcustomernumber,
          :lCurrentDate,
          :lLogActions);

      /*Insert tracing log (also needed in EasyPOS Kontor v1 to get the log)*/
      INSERT INTO sladrehank (
          dato,
          art,
          salgkr,
          kostpr,
          bontext,
          debnr,
          ekspedient,
          afdeling_id,
          uafd_navn,
          uafd_grp_navn)
      VALUES (
          :lCurrentDate,
          13001,
          :lPointsToAddToCustomer,
          :lPaidWithPoints,
          'Bonus points added to customer.  Customer: ' || :lcustomernumber || ' added ' || ROUND((:lPointsToAddToCustomer - :lPaidWithPoints), 2) || ' points',
          :lcustomernumber,
          :lsoldClerk,
          :lsolddepartment,
          :lSoldMachine,
          'Ingen undergruppe');
    END
  END
  ELSE
  BEGIN
    /*No customer to this sale. Return 0 point and a message to caller*/
    :o_message = 'No cusomer connected to sale';
    :o_points_earned = 0;
    :o_points_balance = 0;
    :o_points_used = 0;
  END

  SUSPEND;
END^


CREATE OR ALTER PROCEDURE P_CLOSEDAY (
    I_DEPARTMENT TYPE OF COLUMN AFDELING.AFDELINGSNUMMER,
    I_MACHINE TYPE OF COLUMN UNDERAFDELING.NAVN,
    I_DATETIMEOFCLOSEDAY DATE,
    I_CLERK TYPE OF COLUMN EKSPEDIENTER.NR,
    I_SIMULATEFINACIALRECORDS INTEGER,
    I_REFERENCENUMBER INTEGER)
RETURNS (
    O_CLOSEDAYREFERENCENUMBER INTEGER)
AS
DECLARE VARIABLE LGRUPPENAVN TYPE OF COLUMN AFDELINGSGRUPPERDETAIL.GRUPPENAVN;
DECLARE VARIABLE LHOVEDMASKINE TYPE OF COLUMN AFDELINGSGRUPPERDETAIL.HOVEDMASKINE;
DECLARE VARIABLE LMASTERDEPARTMENT TYPE OF COLUMN AFDELING.AFDELINGSNUMMER;
DECLARE VARIABLE LMASTERMACHINE TYPE OF COLUMN UNDERAFDELING.NAVN;
DECLARE VARIABLE LMACHINEISPARTOFAGROUP INTEGER;
DECLARE VARIABLE LDATETIMEOFPRIORCLOSEDAY DATE;
DECLARE VARIABLE LLOOPDATE DATE;
DECLARE VARIABLE LSQLFROMDATE DATE;
DECLARE VARIABLE LSQLTODATE DATE;
DECLARE VARIABLE LGENERATORNAME VARCHAR(200);
DECLARE VARIABLE LDIFFERENCEGROUPANDPAYMENT NUMERIC(15,2);
DECLARE VARIABLE LFORBRUGSKONTO TYPE OF COLUMN BEHOLDNING.FORBRUGSKONTO;
DECLARE VARIABLE LLAGERKONTO TYPE OF COLUMN BEHOLDNING.LAGERKONTO;
DECLARE VARIABLE LKASSATIONSKONTO TYPE OF COLUMN BEHOLDNING.KASSATIONSKONTO;
DECLARE VARIABLE LRABATKONTO TYPE OF COLUMN BEHOLDNING.RABATKONTO;
DECLARE VARIABLE LGAVEKORTRABATMK TYPE OF COLUMN KONTOPLAN.MOMSKODE;
DECLARE VARIABLE LDIFFKONTO TYPE OF COLUMN BEHOLDNING.DIFFKONTO;
DECLARE VARIABLE LDIFFKONTOMOMSKODE TYPE OF COLUMN KONTOPLAN.MOMSKODE;
DECLARE VARIABLE LFORSKYDNINGSKONTO TYPE OF COLUMN BEHOLDNING.FORSKYDNINGSKONTO;
DECLARE VARIABLE LAFRUNDINGSKONTO TYPE OF COLUMN BEHOLDNING.AFRUNDINGSKONTO;
DECLARE VARIABLE LGAVEKORTOVERFORKONTO TYPE OF COLUMN BEHOLDNING.GAVEKORTSOVERFORKONTO;
DECLARE VARIABLE LFRAGTKONTO TYPE OF COLUMN BEHOLDNING.FRAGTKONTO;
DECLARE VARIABLE LFRAGTKONTOMK TYPE OF COLUMN KONTOPLAN.MOMSKODE;
DECLARE VARIABLE LFRAGTKONTOMOMSFRIT TYPE OF COLUMN BEHOLDNING.FRAGTKONTOMOMSFRIT;
DECLARE VARIABLE LFRAGTKONTOMOMSFRITMK TYPE OF COLUMN KONTOPLAN.MOMSKODE;
DECLARE VARIABLE LTILMOMSAFREGNING TYPE OF COLUMN BEHOLDNING.TILMOMSAFREGNING;
DECLARE VARIABLE LTILMOMSAFREGNINGMK TYPE OF COLUMN KONTOPLAN.MOMSKODE;
DECLARE VARIABLE LFREMTIDMOMSMODREGNING TYPE OF COLUMN BEHOLDNING.FREMTIDMOMSMODREGNING;
DECLARE VARIABLE LFREMTIDMOMSMODREGNINGMK TYPE OF COLUMN KONTOPLAN.MOMSKODE;
DECLARE VARIABLE LGEBYRKONTO TYPE OF COLUMN UNDERAFDELING.GEBYRKONTO;
DECLARE VARIABLE LGEBYRKONTOMK TYPE OF COLUMN KONTOPLAN.MOMSKODE;
DECLARE VARIABLE LGEBYRKONTOMOMSFRIT TYPE OF COLUMN UNDERAFDELING.GEBYRKONTOMOMSFRIT;
DECLARE VARIABLE LGEBYRKONTOMOMSFRITMK TYPE OF COLUMN KONTOPLAN.MOMSKODE;
DECLARE VARIABLE LVATFACTOR NUMERIC(15,2);
DECLARE VARIABLE LFORSKYDNING NUMERIC(15,2);
BEGIN
  /*
  Date: 14-06-2024
  Author: Mivi

  This procedure will handle a CloseDay.
  A close day is what "end the day". It is also sometimes referred to as a Z-Report (or thats is the outcome)
  Eventhough its called Close Day, it can be made several times a day, or every other day. It does not have to be exactly one time a day.
  If this is handled, and its more than one day sinse last time, each day must be handled by itself. So if last one was saturday and we now have friday, we will end sunday, monday, tuesday etc.
  Input here is:
   - Department (Must exists prior to calling this)
   - Machine (Must exists and belong to "department" prior to calling this)
   - Date and time
   - Clerk (Must exists prior to calling this)
   The machine can be part of a group. So we must first check for that.
   Turnover will be summerized in the table CLOSE_DAY.
   This will be made for:
    - Groups (can be made by summerize sales lines (ART in (0,1) in table TRANSAKTIONER - This is without vouchers)
    - Payments (Can be made by summeriza payment lines (ART=16 in table TRANSAKTIONER). But we must exclude vouchers sold here (ART=23 in Table TRANSAKTIONER)
   These are the two methods most people ask for.
   These sum of these 2 must be the same afterwards.

   The routine must create some financial records here.
   This includes:
      - The turnover. Somewhere we have to be able to see, which of the 2 methods we want to use. Date og Turnovers - Groups can be summarized per day - Payments cannot.
        - The routine will summerize:
          - GROUPS per day. Along with GROUPS we will have a sum of Costprice (can be used to vareforbrug / lagerforskydnin)
          - PAYMENTS. This can only be done for the entire period. It cannot be split into days
      - Used Vouchers (Store credit and gift certificates)
      - Issued vouchers (Only gift certificate)
        - Here there is a discount field. It is possible to sell a voucher and give a discount
      - Petty Cash
      - Customer Deposit
      - Customer Sales
      - Fees and Shipment fees. Will reside in tbe same table
    - Vareforbrug / Lagerforskydning.
      - Settlement. These are stored in table SETTLEMENT and are alreadyde.
      - Check that COMPLETE SALE sets SIDSTESALG in BEHOLDNING
      - VAT in and out on vouhers + Discount on voucher
      - Reset payments
      - Set date of last Close Day
      - Build financial records
      - More?

    What we do not have in here;
     - ENGROS SALG
  */

  /* *************************************** */
  /* *************************************** */
  /* Finding if machine belongs to a group or not. If it does, find the master machine (which holds account numbers etc) */
  /* *************************************** */
  /* *************************************** */
  /*Does machine machine belong to a group*/
  /*Hereafter we will have the name of the group the machine belongs to and the master machine and department*/
  SELECT
      AFDELINGSGRUPPERDETAIL.GRUPPENAVN,
      AFDELINGSGRUPPERDETAIL.HOVEDMASKINE
  FROM AFDELINGSGRUPPERDETAIL
  WHERE
      AFDELINGSGRUPPERDETAIL.MASKINE = :I_MACHINE
  INTO :LGRUPPENAVN,
       :LHOVEDMASKINE;

  IF (:LGRUPPENAVN IS NULL) THEN
  BEGIN
    /*This machine does not belong to a group of machines, and is "it self"*/
    :LMASTERDEPARTMENT = :I_DEPARTMENT;
    :LMASTERMACHINE = :I_MACHINE;
    :LMACHINEISPARTOFAGROUP = 0;
  END
  ELSE
  BEGIN
    /*This machine does belong to a group of machines and we must locate to machine, which is the master*/
    :LMACHINEISPARTOFAGROUP = 1;
    IF (:LHOVEDMASKINE = 1) THEN
    BEGIN
      /*We have already located the master machine. Its ourself*/
      :LMASTERDEPARTMENT = :I_DEPARTMENT;
      :LMASTERMACHINE = :I_MACHINE;
    END
    ELSE
    BEGIN
      /*We need to find the master machine*/
      SELECT
          AFDELINGSGRUPPER.AFDELING_ID,
          AFDELINGSGRUPPERDETAIL.MASKINE
      FROM AFDELINGSGRUPPERDETAIL
          INNER JOIN AFDELINGSGRUPPER ON
                AFDELINGSGRUPPER.GRUPPENAVN = AFDELINGSGRUPPERDETAIL.GRUPPENAVN
      WHERE
          AFDELINGSGRUPPERDETAIL.HOVEDMASKINE = 1
          AND AFDELINGSGRUPPERDETAIL.GRUPPENAVN = :LGRUPPENAVN
      INTO :LMASTERDEPARTMENT,
           :LMASTERMACHINE;
    END
  END

  /* MIVI - Do we have the right reference number */
  /* *************************************** */
  /* *************************************** */
  /* Finding next reference number to en close day */
  /* *************************************** */
  /* *************************************** */
  /*Lets find the next reference number (receipt number) for this Close Day*/
  /*Due to restrictions regarding SAF-T I strongly recommend settings these in SkyPOS PER MACHINE*/
  IF (:I_SIMULATEFINACIALRECORDS = 1) THEN
  BEGIN
    :O_CLOSEDAYREFERENCENUMBER = :I_REFERENCENUMBER;
  END
  ELSE
  BEGIN
    LGENERATORNAME = 'GEN_DAGSAFSLUT_' || :LMASTERDEPARTMENT || :LMASTERMACHINE;
    EXECUTE STATEMENT 'select gen_id(' || LGENERATORNAME || ',1) from rdb$database'
        INTO :O_CLOSEDAYREFERENCENUMBER;
  END

  /* *************************************** */
  /* *************************************** */
  /* Finding the date of the last close day made */
  /* *************************************** */
  /* *************************************** */
  /*Now lets find the prior date of close day*/
  /*Each Close Day will add an antry into table SLADREHANK with art=180*/
  /*If the machine belongs to a group of machines, its the master machine, that holds the log. */
  SELECT
      MAX(DATO) AS DATO
  FROM SLADREHANK
  WHERE
      AFDELING_ID = :LMASTERDEPARTMENT
      AND UAFD_NAVN = :LMASTERMACHINE
      AND ART = 180
  INTO LDATETIMEOFPRIORCLOSEDAY;

  /* *************************************** */
  /* *************************************** */
  /* Find default VAT - will be used when calculating VAT on vouchers */
  /* *************************************** */
  /* *************************************** */
  SELECT
      MOMSSATSER.MOMSSATS
  FROM STAMDATA_PRG_EXT
      INNER JOIN MOMSSATSER ON
            MOMSSATSER.MOMSSATS_TITEL = STAMDATA_PRG_EXT.STDMOMS
  WHERE
      STAMDATA_PRG_EXT.AFDELING_ID = '001'
  INTO :LVATFACTOR;

  /* *************************************** */
  /* *************************************** */
  /* Finding accounts numbers to be used in general */
  /* *************************************** */
  /* *************************************** */
  /* We will now fetch accounts numbers which we need at a later time*/

  SELECT
      BEHOLDNING.FORBRUGSKONTO,
      BEHOLDNING.LAGERKONTO,
      BEHOLDNING.KASSATIONSKONTO,
      BEHOLDNING.RABATKONTO,
      KP3.MOMSKODE AS GAVEKORTRABATMK,
      BEHOLDNING.DIFFKONTO,
      KP1.MOMSKODE AS DIFFKONTOMOMSKODE,
      BEHOLDNING.FORSKYDNINGSKONTO,
      BEHOLDNING.AFRUNDINGSKONTO,
      BEHOLDNING.GAVEKORTSOVERFORKONTO,
      BEHOLDNING.FRAGTKONTO,
      KP2.MOMSKODE AS FRAGTKONTOMK,
      BEHOLDNING.FRAGTKONTOMOMSFRIT,
      KP4.MOMSKODE AS FRAGTKONTOMOMSFRITMK,
      BEHOLDNING.TILMOMSAFREGNING,
      KP5.MOMSKODE AS TILMOMSAFREGNINGMK,
      BEHOLDNING.FREMTIDMOMSMODREGNING,
      KP6.MOMSKODE AS FREMTIDMOMSMODREGNINGMK
  FROM BEHOLDNING
      INNER JOIN KONTOPLAN KP1 ON
            BEHOLDNING.AFDELING_ID = KP1.AFDELING_ID
            AND BEHOLDNING.DIFFKONTO = KP1.KONTONR
      INNER JOIN KONTOPLAN KP2 ON
            BEHOLDNING.AFDELING_ID = KP2.AFDELING_ID
            AND BEHOLDNING.FRAGTKONTO = KP2.KONTONR
      INNER JOIN KONTOPLAN KP3 ON
            BEHOLDNING.AFDELING_ID = KP3.AFDELING_ID
            AND BEHOLDNING.RABATKONTO = KP3.KONTONR
      INNER JOIN KONTOPLAN KP4 ON
            BEHOLDNING.AFDELING_ID = KP4.AFDELING_ID
            AND BEHOLDNING.FRAGTKONTOMOMSFRIT = KP4.KONTONR
      INNER JOIN KONTOPLAN KP5 ON
            BEHOLDNING.AFDELING_ID = KP5.AFDELING_ID
            AND BEHOLDNING.TILMOMSAFREGNING = KP5.KONTONR
      INNER JOIN KONTOPLAN KP6 ON
            BEHOLDNING.AFDELING_ID = KP6.AFDELING_ID
            AND BEHOLDNING.FREMTIDMOMSMODREGNING = KP6.KONTONR
  WHERE
      BEHOLDNING.UAFD_NAVN = :LMASTERMACHINE
  INTO :LFORBRUGSKONTO,
       :LLAGERKONTO,
       :LKASSATIONSKONTO,
       :LRABATKONTO,
       :LGAVEKORTRABATMK,
       :LDIFFKONTO,
       :LDIFFKONTOMOMSKODE,
       :LFORSKYDNINGSKONTO,
       :LAFRUNDINGSKONTO,
       :LGAVEKORTOVERFORKONTO,
       :LFRAGTKONTO,
       :LFRAGTKONTOMK,
       :LFRAGTKONTOMOMSFRIT,
       :LFRAGTKONTOMOMSFRITMK,
       :LTILMOMSAFREGNING,
       :LTILMOMSAFREGNINGMK,
       :LFREMTIDMOMSMODREGNING,
       :LFREMTIDMOMSMODREGNINGMK;

  SELECT
      UNDERAFDELING.GEBYRKONTO,
      KP1.MOMSKODE AS GEBYRKONTOMK,
      UNDERAFDELING.GEBYRKONTOMOMSFRIT,
      KP2.MOMSKODE AS GEBYRKONTOMOMSFRITK
  FROM UNDERAFDELING
      INNER JOIN KONTOPLAN KP1 ON
            UNDERAFDELING.AFDELING_ID = KP1.AFDELING_ID
            AND UNDERAFDELING.GEBYRKONTO = KP1.KONTONR
      INNER JOIN KONTOPLAN KP2 ON
            UNDERAFDELING.AFDELING_ID = KP2.AFDELING_ID
            AND UNDERAFDELING.GEBYRKONTOMOMSFRIT = KP2.KONTONR
  WHERE
      UNDERAFDELING.NAVN = :LMASTERMACHINE
  INTO :LGEBYRKONTO,
       :LGEBYRKONTOMK,
       :LGEBYRKONTOMOMSFRIT,
       :LGEBYRKONTOMOMSFRITMK;

  /* This we will do Turnover "per day" in the date interval we have */
  /* The date interval is "last time we did close day (found earlier via SQL)and NOW (transferred from the caller) */

  /*Lets just lige set starting date without time*/
  :LLOOPDATE = CAST(EXTRACT(YEAR FROM :LDATETIMEOFPRIORCLOSEDAY) || '-' || EXTRACT(MONTH FROM :LDATETIMEOFPRIORCLOSEDAY) || '-' || EXTRACT(DAY FROM :LDATETIMEOFPRIORCLOSEDAY) AS DATE);
  --Whatever we need to do - we need to start at start date and time
  :LSQLFROMDATE = LDATETIMEOFPRIORCLOSEDAY;
  --We need to end the day at midnight
  --  :lSQLToDate = cast(cast(:lLoopDate as varchar(10)) || ' 23:59:59' as date);
  :LSQLTODATE = CAST(EXTRACT(YEAR FROM :LLOOPDATE) || '-' || EXTRACT(MONTH FROM :LLOOPDATE) || '-' || EXTRACT(DAY FROM :LLOOPDATE) || ' 23:59:59' AS DATE);

  --As long as Loopdate is lower than the date we need to go to - continue
  WHILE ((:LLOOPDATE <= :I_DATETIMEOFCLOSEDAY)) DO
  BEGIN
    /* *************************************** */
    /* *************************************** */
    /* Setting turnover per group. Both with and without VAT */
    /* *************************************** */
    /* *************************************** */
    /*Now - Lets find the turnover per group*/
    INSERT INTO CLOSE_DAY (
        DEPARTMENT,
        MACHINE,
        CLERK,
        REFERENCENUMBER,
        TYPE_,
        DATE_OF_CLOSEDAY,
        LASTDATE_OF_CLOSEDAY,
        STARTDATE,
        ENDDATE,
        NAME_,
        DISPLAYNAME,
        ACCOUNTNUMBER,
        VAT_CODE,
        AMOUNT,
        AMOUNT_COST)
        /*First we get all sales with VAT*/
    SELECT
        :I_DEPARTMENT,
        :I_MACHINE,
        :I_CLERK,
        :O_CLOSEDAYREFERENCENUMBER,
        1,
        :I_DATETIMEOFCLOSEDAY,
        :LDATETIMEOFPRIORCLOSEDAY,
        :LSQLFROMDATE,
        :LSQLTODATE,
        /*Group*/
        TRANSAKTIONER.VAREGRPID AS GROUPNAME,
        /*Displayname*/
        TRANSAKTIONER.VAREGRPID AS GROUPNAME,
        /*Master machines turnover acount with VAT*/
        (SELECT
             VGRP_KONTI.OMSKONTO
         FROM VGRP_KONTI
         WHERE
             VGRP_KONTI.NAVN = TRANSAKTIONER.VAREGRPID
             AND VGRP_KONTI.AFDELING_ID = :LMASTERDEPARTMENT
             AND VGRP_KONTI.MASKINE = :LMASTERMACHINE) TURNOVERACCOUNTNUMBER,
        /*Account VAT Type (With VAT)*/
        (SELECT
             KONTOPLAN.EXTERNMOMSKODE
         FROM VGRP_KONTI
             INNER JOIN KONTOPLAN ON
                   KONTOPLAN.KONTONR = (SELECT
                                            VGRP_KONTI.OMSKONTO
                                        FROM VGRP_KONTI
                                        WHERE
                                            VGRP_KONTI.NAVN = TRANSAKTIONER.VAREGRPID
                                            AND VGRP_KONTI.AFDELING_ID = :LMASTERDEPARTMENT
                                            AND VGRP_KONTI.MASKINE = :LMASTERMACHINE)
                   AND KONTOPLAN.AFDELING_ID = :LMASTERDEPARTMENT
         WHERE
             VGRP_KONTI.NAVN = TRANSAKTIONER.VAREGRPID
             AND VGRP_KONTI.AFDELING_ID = :LMASTERDEPARTMENT
             AND VGRP_KONTI.MASKINE = :LMASTERMACHINE) AS ACOUNTTYPE,
        /*Amount turnover*/
        SUM(TRANSAKTIONER.SALGKR) AS AMOUNT,
        SUM(TRANSAKTIONER.KOSTPR) AS AMOUNT_COST
    FROM TRANSAKTIONER
    WHERE
        TRANSAKTIONER.DATO >= :LSQLFROMDATE
        AND TRANSAKTIONER.DATO <= :LSQLTODATE
        AND TRANSAKTIONER.MOMSKR <> 0
        AND TRANSAKTIONER.ART IN (0, 1)
        AND ((
        /*Own machine*/
        TRANSAKTIONER.UAFD_NAVN = :LMASTERMACHINE
        AND TRANSAKTIONER.AFDELING_ID = :LMASTERDEPARTMENT)
        OR (
        /*Group of machines*/
        TRANSAKTIONER.UAFD_NAVN IN (SELECT
                                        AFDELINGSGRUPPERDETAIL.MASKINE
                                    FROM AFDELINGSGRUPPERDETAIL
                                    WHERE
                                        AFDELINGSGRUPPERDETAIL.GRUPPENAVN = :LGRUPPENAVN)))
    GROUP BY
        TRANSAKTIONER.VAREGRPID

    UNION

    /*Second we get all sales without VAT*/
    SELECT
        :I_DEPARTMENT,
        :I_MACHINE,
        :I_CLERK,
        :O_CLOSEDAYREFERENCENUMBER,
        1,
        :I_DATETIMEOFCLOSEDAY,
        :LDATETIMEOFPRIORCLOSEDAY,
        :LSQLFROMDATE,
        :LSQLTODATE,
        /*Group*/
        TRANSAKTIONER.VAREGRPID AS GROUPNAME,
        /*Displayname*/
        TRANSAKTIONER.VAREGRPID AS GROUPNAME,
        /*Master machines turnover acount without VAT*/
        (SELECT
             VGRP_KONTI.OMSMOMSFRITKONTO
         FROM VGRP_KONTI
         WHERE
             VGRP_KONTI.NAVN = TRANSAKTIONER.VAREGRPID
             AND VGRP_KONTI.AFDELING_ID = :LMASTERDEPARTMENT
             AND VGRP_KONTI.MASKINE = :LMASTERMACHINE) TURNOVERACCOUNTNUMBER,
        /*Account VAT Type (Without VAT)*/
        (SELECT
             KONTOPLAN.EXTERNMOMSKODE
         FROM VGRP_KONTI
             INNER JOIN KONTOPLAN ON
                   KONTOPLAN.KONTONR = (SELECT
                                            VGRP_KONTI.OMSMOMSFRITKONTO
                                        FROM VGRP_KONTI
                                        WHERE
                                            VGRP_KONTI.NAVN = TRANSAKTIONER.VAREGRPID
                                            AND VGRP_KONTI.AFDELING_ID = :LMASTERDEPARTMENT
                                            AND VGRP_KONTI.MASKINE = :LMASTERMACHINE)
                   AND KONTOPLAN.AFDELING_ID = :LMASTERDEPARTMENT
         WHERE
             VGRP_KONTI.NAVN = TRANSAKTIONER.VAREGRPID
             AND VGRP_KONTI.AFDELING_ID = :LMASTERDEPARTMENT
             AND VGRP_KONTI.MASKINE = :LMASTERMACHINE) AS ACOUNTTYPE,
        /*Amount turnover*/
        SUM(TRANSAKTIONER.SALGKR) AS AMOUNT,
        SUM(TRANSAKTIONER.KOSTPR) AS AMOUNT_COST
    FROM TRANSAKTIONER
    WHERE
        TRANSAKTIONER.DATO >= :LSQLFROMDATE
        AND TRANSAKTIONER.DATO <= :LSQLTODATE
        AND TRANSAKTIONER.MOMSKR = 0
        AND TRANSAKTIONER.ART IN (0, 1)
        AND ((
        /*Own machine*/
        TRANSAKTIONER.UAFD_NAVN = :LMASTERMACHINE
        AND TRANSAKTIONER.AFDELING_ID = :LMASTERDEPARTMENT)
        OR (
        /*Group of machines*/
        TRANSAKTIONER.UAFD_NAVN IN (SELECT
                                        AFDELINGSGRUPPERDETAIL.MASKINE
                                    FROM AFDELINGSGRUPPERDETAIL
                                    WHERE
                                        AFDELINGSGRUPPERDETAIL.GRUPPENAVN = :LGRUPPENAVN)))

    GROUP BY
        TRANSAKTIONER.VAREGRPID;
    /*END - Turnover per Group - END*/

    -- Increment the date by one day
    LLOOPDATE = DATEADD(1 DAY TO LLOOPDATE);
    --From regarding SQL is now Loop Date
    :LSQLFROMDATE = LLOOPDATE;
    --lSQLToDate is now Loop date at midnight
    :LSQLTODATE = CAST(EXTRACT(YEAR FROM :LLOOPDATE) || '-' || EXTRACT(MONTH FROM :LLOOPDATE) || '-' || EXTRACT(DAY FROM :LLOOPDATE) || ' 23:59:59' AS DATE);
    IF (:LSQLTODATE > :I_DATETIMEOFCLOSEDAY) THEN
    BEGIN
      --To date is now exceeds I_EndDate - Set to I_EndDate
      :LSQLTODATE = :I_DATETIMEOFCLOSEDAY;
    END
  END

  /* *************************************** */
  /* *************************************** */
  /* Setting turnover per payment. Both with and without VAT */
  /* *************************************** */
  /* *************************************** */
  /*Turnover per payment*/
  /*This works because each sale we register via P_COMPLETESALE we summerize the turnover to the respective Payment method*/
  /* This I can in any way Easily do per day in date interval*/
  INSERT INTO CLOSE_DAY (
      DEPARTMENT,
      MACHINE,
      CLERK,
      REFERENCENUMBER,
      TYPE_,
      DATE_OF_CLOSEDAY,
      LASTDATE_OF_CLOSEDAY,
      STARTDATE,
      ENDDATE,
      NAME_,
      DISPLAYNAME,
      ACCOUNTNUMBER,
      VAT_CODE,
      AMOUNT,
      AMOUNT_COST)
      /*First we get all payments with VAT*/
  SELECT
      :I_DEPARTMENT,
      :I_MACHINE,
      :I_CLERK,
      :O_CLOSEDAYREFERENCENUMBER,
      0,
      :I_DATETIMEOFCLOSEDAY,
      :LDATETIMEOFPRIORCLOSEDAY,
      (SELECT
           BEHOLDNING.SIDSTEDAG
       FROM BEHOLDNING
       WHERE
           BEHOLDNING.UAFD_NAVN = :LMASTERMACHINE
           AND BEHOLDNING.AFDELING_ID = :LMASTERDEPARTMENT),
      (SELECT
           BEHOLDNING.SIDSTESALG
       FROM BEHOLDNING
       WHERE
           BEHOLDNING.UAFD_NAVN = :LMASTERMACHINE
           AND BEHOLDNING.AFDELING_ID = :LMASTERDEPARTMENT),
      BEHOLDNING_DETAIL.BETVEJ,
      BEHOLDNING_DETAIL.DISPLAYFELT,
      (SELECT
           BETALINGSVEJ.BETVEJ_OMS_KONTONR
       FROM BETALINGSVEJ
       WHERE
           BETALINGSVEJ.BETVEJ_NAVN = BEHOLDNING_DETAIL.BETVEJ
           AND BETALINGSVEJ.AFDELING_ID = :LMASTERDEPARTMENT
           AND BETALINGSVEJ.UAFD_NAVN = :LMASTERMACHINE) TURNOVERACCOUNTNUMBER,
      (SELECT
           KONTOPLAN.EXTERNMOMSKODE
       FROM BETALINGSVEJ
           INNER JOIN KONTOPLAN ON
                 KONTOPLAN.KONTONR = (SELECT
                                          BETALINGSVEJ.BETVEJ_OMS_KONTONR
                                      FROM BETALINGSVEJ
                                      WHERE
                                          BETALINGSVEJ.BETVEJ_NAVN = BEHOLDNING_DETAIL.BETVEJ
                                          AND BETALINGSVEJ.AFDELING_ID = :LMASTERDEPARTMENT
                                          AND BETALINGSVEJ.UAFD_NAVN = :LMASTERMACHINE)
                 AND KONTOPLAN.AFDELING_ID = :LMASTERDEPARTMENT
       WHERE
           BETALINGSVEJ.BETVEJ_NAVN = BEHOLDNING_DETAIL.BETVEJ
           AND BETALINGSVEJ.AFDELING_ID = :LMASTERDEPARTMENT
           AND BETALINGSVEJ.UAFD_NAVN = :LMASTERMACHINE) AS ACOUNTTYPE,
      SUM(BEHOLDNING_DETAIL.DGOMS) AS PAYMENTTURNOVER,
      0
  FROM BEHOLDNING_DETAIL
  WHERE
      ((
      /*Own machine*/
      BEHOLDNING_DETAIL.UAFD_NAVN = :LMASTERMACHINE
      AND BEHOLDNING_DETAIL.AFDELING_ID = :LMASTERDEPARTMENT)
      OR (
      /*Group of machines*/
      BEHOLDNING_DETAIL.UAFD_NAVN IN (SELECT
                                          AFDELINGSGRUPPERDETAIL.MASKINE
                                      FROM AFDELINGSGRUPPERDETAIL
                                      WHERE
                                          AFDELINGSGRUPPERDETAIL.GRUPPENAVN = :LGRUPPENAVN)))
  GROUP BY
      BEHOLDNING_DETAIL.BETVEJ,
      BEHOLDNING_DETAIL.DISPLAYFELT
  HAVING
      (SUM(BEHOLDNING_DETAIL.DGOMS) <> 0)

  UNION

  /*Second we get all payments without VAT*/
  SELECT
      :I_DEPARTMENT,
      :I_MACHINE,
      :I_CLERK,
      :O_CLOSEDAYREFERENCENUMBER,
      0,
      :I_DATETIMEOFCLOSEDAY,
      :LDATETIMEOFPRIORCLOSEDAY,
      (SELECT
           BEHOLDNING.SIDSTEDAG
       FROM BEHOLDNING
       WHERE
           BEHOLDNING.UAFD_NAVN = :LMASTERMACHINE
           AND BEHOLDNING.AFDELING_ID = :LMASTERDEPARTMENT),
      (SELECT
           BEHOLDNING.SIDSTESALG
       FROM BEHOLDNING
       WHERE
           BEHOLDNING.UAFD_NAVN = :LMASTERMACHINE
           AND BEHOLDNING.AFDELING_ID = :LMASTERDEPARTMENT),
      BEHOLDNING_DETAIL.BETVEJ,
      BEHOLDNING_DETAIL.DISPLAYFELT,
      (SELECT
           BETALINGSVEJ.BETVEJ_OMS_KONTONRMOMSFRIT
       FROM BETALINGSVEJ
       WHERE
           BETALINGSVEJ.BETVEJ_NAVN = BEHOLDNING_DETAIL.BETVEJ
           AND BETALINGSVEJ.AFDELING_ID = :LMASTERDEPARTMENT
           AND BETALINGSVEJ.UAFD_NAVN = :LMASTERMACHINE) TURNOVERACCOUNTNUMBER,
      (SELECT
           KONTOPLAN.EXTERNMOMSKODE
       FROM BETALINGSVEJ
           INNER JOIN KONTOPLAN ON
                 KONTOPLAN.KONTONR = (SELECT
                                          BETALINGSVEJ.BETVEJ_OMS_KONTONRMOMSFRIT
                                      FROM BETALINGSVEJ
                                      WHERE
                                          BETALINGSVEJ.BETVEJ_NAVN = BEHOLDNING_DETAIL.BETVEJ
                                          AND BETALINGSVEJ.AFDELING_ID = :LMASTERDEPARTMENT
                                          AND BETALINGSVEJ.UAFD_NAVN = :LMASTERMACHINE)
                 AND KONTOPLAN.AFDELING_ID = :LMASTERDEPARTMENT
       WHERE
           BETALINGSVEJ.BETVEJ_NAVN = BEHOLDNING_DETAIL.BETVEJ
           AND BETALINGSVEJ.AFDELING_ID = :LMASTERDEPARTMENT
           AND BETALINGSVEJ.UAFD_NAVN = :LMASTERMACHINE) AS ACOUNTTYPE,
      SUM(BEHOLDNING_DETAIL.DGOMS_FRIT) AS PAYMENTTURNOVER,
      0
  FROM BEHOLDNING_DETAIL
  WHERE
      ((
      /*Own machine*/
      BEHOLDNING_DETAIL.UAFD_NAVN = :LMASTERMACHINE
      AND BEHOLDNING_DETAIL.AFDELING_ID = :LMASTERDEPARTMENT)
      OR (
      /*Group of machines*/
      BEHOLDNING_DETAIL.UAFD_NAVN IN (SELECT
                                          AFDELINGSGRUPPERDETAIL.MASKINE
                                      FROM AFDELINGSGRUPPERDETAIL
                                      WHERE
                                          AFDELINGSGRUPPERDETAIL.GRUPPENAVN = :LGRUPPENAVN)))
  GROUP BY
      BEHOLDNING_DETAIL.BETVEJ,
      BEHOLDNING_DETAIL.DISPLAYFELT
  HAVING
      (SUM(BEHOLDNING_DETAIL.DGOMS_FRIT) <> 0);
  /*END - Turnover per payment - END*/

  /* *************************************** */
  /* *************************************** */
  /* Checking that turnover per group equals turnover per payment. This should always be TRUE */
  /* Turnover per payment is summarized duing use and per group is based on transactions.*/
  /* *************************************** */
  /* *************************************** */
  /*
  At this point we should have records in CLOSE_DAY per group and payment - Sum of each must be identical.
  We will just fetch the sum of each and compare
  */
  SELECT
      SUM(
      CASE WHEN CD.TYPE_ = 1 THEN
          CD.AMOUNT
        ELSE 0
      END) - SUM(
      CASE WHEN CD.TYPE_ = 0 THEN
          CD.AMOUNT
        ELSE 0
      END) AS DIFFERENCE
  FROM CLOSE_DAY CD
  WHERE
      CD.DEPARTMENT = :LMASTERDEPARTMENT
      AND CD.MACHINE = :LMASTERMACHINE
      AND CD.LASTDATE_OF_CLOSEDAY = :LDATETIMEOFPRIORCLOSEDAY
      AND CD.DATE_OF_CLOSEDAY = :I_DATETIMEOFCLOSEDAY
  INTO :LDIFFERENCEGROUPANDPAYMENT;

