        AND CDC.MACHINE = :LMASTERMACHINE
        AND CDC.REFERENCENUMBER = :O_CLOSEDAYREFERENCENUMBER
    ORDER BY
        CDC.ID;

    /* Petty cash */
    INSERT INTO POSTERINGER (
        BEHANDLET,
        DATO,
        KONTOTYPE,
        KONTONR,
        BILAGSNR,
        AFDELING,
        MODKONTO,
        TEKST,
        BELOB,
        MOMSKODE,
