        4,
        140,
        0
    FROM CLOSE_DAY_PETTYCASH CDP
    WHERE
        CDP.DEPARTMENT = :LMASTERDEPARTMENT
        AND CDP.MACHINE = :LMASTERMACHINE
        AND CDP.REFERENCENUMBER = :O_CLOSEDAYREFERENCENUMBER
    ORDER BY
