    /* VAT of all vouchers*/
    INSERT INTO POSTERINGER (
        BEHANDLET,
        DATO,
        KONTOTYPE,
        KONTONR,
        BILAGSNR,
        AFDELING,
        MODKONTO,
        TEKST,
        BELOB,
        MOMSKODE,
        MODBILAG,
        VALUTA,
        VALUTAKODE,
        AFDELING_ID,
        UAFD_NAVN,
        UAFD_GRP_NAVN,
        POSTTYPE,
        SORTERING,
        EKSPORTERET)
    SELECT
