        0,
        '',
        CDV.DEPARTMENT,
        CDV.MACHINE,
        'Ingen undergruppe',
        5,
        150,
        0
    FROM CLOSE_DAY_VOUCHERS CDV
    WHERE
        CDV.DEPARTMENT = :LMASTERDEPARTMENT
        AND CDV.MACHINE = :LMASTERMACHINE
        AND CDV.REFERENCENUMBER = :O_CLOSEDAYREFERENCENUMBER
        --      AND UPPER(CDV.VOUCHERDIRECTION) = 'ISSUED'
    ORDER BY
        CDV.ID;

    /* This is freigth and Fee */
    INSERT INTO POSTERINGER (
        BEHANDLET,
        DATO,
        KONTOTYPE,
        KONTONR,
        BILAGSNR,
        AFDELING,
        MODKONTO,
        TEKST,
        BELOB,
        MOMSKODE,
        MODBILAG,
        VALUTA,
        VALUTAKODE,
        AFDELING_ID,
        UAFD_NAVN,
        UAFD_GRP_NAVN,
        POSTTYPE,
        SORTERING,
        EKSPORTERET)
    SELECT
        0,
        CDF.DATE_OF_FEEFREIGHT,
        'D',
        CDF.ACCOUNTNUMBER,
        CDF.RECEIPTNUMBER,
        CDF.DEPARTMENT,
        '',
        /*All vouchers*/
        CDF.TEXT,
        CDF.AMOUNT * -1,
        CDF.VAT_CODE,
        '',
        0,
        '',
        CDF.DEPARTMENT,
        CDF.MACHINE,
        'Ingen undergruppe',
        25,
        10,
        0
    FROM CLOSE_DAY_FEEFREIGHT CDF
    WHERE
        CDF.DEPARTMENT = :LMASTERDEPARTMENT
        AND CDF.MACHINE = :LMASTERMACHINE
        AND CDF.REFERENCENUMBER = :O_CLOSEDAYREFERENCENUMBER
    ORDER BY
        CDF.ID;

    /* Turnover - CURRENTLY PER PAYMENT METHOD*/
    INSERT INTO POSTERINGER (
        BEHANDLET,
        DATO,
        KONTOTYPE,
        KONTONR,
        BILAGSNR,
        AFDELING,
        MODKONTO,
        TEKST,
        BELOB,
        MOMSKODE,
        MODBILAG,
        VALUTA,
        VALUTAKODE,
        AFDELING_ID,
        UAFD_NAVN,
        UAFD_GRP_NAVN,
        POSTTYPE,
        SORTERING,
        EKSPORTERET)
    SELECT
        0,
        CC.ENDDATE,
        'D',
        CC.ACCOUNTNUMBER,
        CC.REFERENCENUMBER,
        CC.DEPARTMENT,
        '',
        'Oms. ' || CC.DISPLAYNAME,
        CC.AMOUNT * -1,
        CC.VAT_CODE,
        '',
        0,
        '',
        CC.DEPARTMENT,
        CC.MACHINE,
        'Ingen undergruppe',
        0,
        10,
        0
    FROM CLOSE_DAY CC
    WHERE
        CC.DEPARTMENT = :LMASTERDEPARTMENT
        AND CC.MACHINE = :LMASTERMACHINE
        AND CC.REFERENCENUMBER = :O_CLOSEDAYREFERENCENUMBER
        AND CC.TYPE_ = 0
        AND CC.AMOUNT <> 0
    ORDER BY
        CC.ID;

    SELECT
        SUM(S.NEW_BALANCE) - SUM(S.STARTING_BALANCE)
    FROM SETTLEMENTS S
    WHERE
        S.DEPARTMENT = :LMASTERDEPARTMENT
        AND S.MACHINE = :LMASTERMACHINE
        AND S.CREATED > :LDATETIMEOFPRIORCLOSEDAY
    INTO :LFORSKYDNING;

    IF (:LFORSKYDNING <> 0) THEN
    BEGIN
      INSERT INTO POSTERINGER (
          BEHANDLET,
          DATO,
          KONTOTYPE,
          KONTONR,
          BILAGSNR,
          AFDELING,
          MODKONTO,
          TEKST,
          BELOB,
          MOMSKODE,
          MODBILAG,
          VALUTA,
          VALUTAKODE,
          AFDELING_ID,
          UAFD_NAVN,
          UAFD_GRP_NAVN,
          POSTTYPE,
          SORTERING,
          EKSPORTERET)
      VALUES (
          0,
          'NOW',
          'F',
          :LFORSKYDNINGSKONTO,
          :O_CLOSEDAYREFERENCENUMBER,
          :LMASTERDEPARTMENT,
          '',
          'Forskydning',
          :LFORSKYDNING,
          'MK',
          '',
          0,
          '',
          :LMASTERDEPARTMENT,
          :LMASTERMACHINE,
          'Ingen undergruppe',
          8,
          40,
          0);
    END

    /* Set */
    /*   the date of last Close Day on master machine or all machines in the group. */
    UPDATE BEHOLDNING SET
        BEHOLDNING.SIDSTEDAG = :I_DATETIMEOFCLOSEDAY,
        BEHOLDNING.DGSTK = 0,
        BEHOLDNING.DGKR = 0,
        BEHOLDNING.DGKPR = 0,
        BEHOLDNING.DGRAB = 0,
        BEHOLDNING.DGKRMOMS = 0,
        BEHOLDNING.OPFYLD_NR = 0,
        BEHOLDNING.LAST_NR_PLUK = 0,
        BEHOLDNING.DAY_FIRST_NR = 0
    WHERE
        (BEHOLDNING.AFDELING_ID = :LMASTERMACHINE
        AND BEHOLDNING.UAFD_NAVN = :LMASTERDEPARTMENT)
        OR (BEHOLDNING.UAFD_NAVN IN (SELECT
                                         AFDELINGSGRUPPERDETAIL.MASKINE
                                     FROM AFDELINGSGRUPPERDETAIL
                                     WHERE
                                         AFDELINGSGRUPPERDETAIL.GRUPPENAVN = :LGRUPPENAVN));
    /* Set */
    /*   Starting Balance - SUM of all machines in the group / the master machine*/
    /*   Only set on master machine */
    UPDATE BEHOLDNING SET
        BEHOLDNING.BEHOLDMORGEN = (SELECT
                                       SUM(BEHOLDNING_DETAIL.BELOB) AS SUMBELOB
                                   FROM BEHOLDNING_DETAIL
                                   WHERE
                                       (BEHOLDNING_DETAIL.AFDELING_ID = :LMASTERMACHINE
                                       AND BEHOLDNING_DETAIL.UAFD_NAVN = :LMASTERDEPARTMENT)
                                       OR (BEHOLDNING_DETAIL.UAFD_NAVN IN (SELECT
                                                                               AFDELINGSGRUPPERDETAIL.MASKINE
                                                                           FROM AFDELINGSGRUPPERDETAIL
                                                                           WHERE
                                                                               AFDELINGSGRUPPERDETAIL.GRUPPENAVN = :LGRUPPENAVN)))
    WHERE
        (BEHOLDNING.AFDELING_ID = :LMASTERMACHINE
        AND BEHOLDNING.UAFD_NAVN = :LMASTERDEPARTMENT);

    /* Reset all fields regarding turnover and fee*/
    UPDATE BEHOLDNING_DETAIL SET
        BEHOLDNING_DETAIL.DGOMS = 0,
        BEHOLDNING_DETAIL.DGOMS_FRIT = 0,
        BEHOLDNING_DETAIL.DGOMS_MOMSFRIT = 0,
        BEHOLDNING_DETAIL.VALDGOMS = 0,
        BEHOLDNING_DETAIL.VALDGOMS_FRIT = 0,
        BEHOLDNING_DETAIL.VALDGOMS_MOMS = 0,
        BEHOLDNING_DETAIL.VALDGOMS_MOMSFRIT = 0,
        BEHOLDNING_DETAIL.IND_GEBYR = 0,
        BEHOLDNING_DETAIL.UD_GEBYR = 0
    WHERE
        (BEHOLDNING_DETAIL.AFDELING_ID = :LMASTERMACHINE
        AND BEHOLDNING_DETAIL.UAFD_NAVN = :LMASTERDEPARTMENT)
        OR (BEHOLDNING_DETAIL.UAFD_NAVN IN (SELECT
                                                AFDELINGSGRUPPERDETAIL.MASKINE
                                            FROM AFDELINGSGRUPPERDETAIL
                                            WHERE
                                                AFDELINGSGRUPPERDETAIL.GRUPPENAVN = :LGRUPPENAVN));

    /* Mark all records in TRANSAKTIONER as ended for this in*/
    /* Date interval between this and last time, not already andded and for this machine or machines in the group */
    UPDATE TRANSAKTIONER SET
        TRANSAKTIONER.DAGSAFSLUTTET = 1
    WHERE
        TRANSAKTIONER.ART IN (0, 1, 2, 3, 4, 5, 16, 18, 21, 22,
                              23, 24, 25, 178, 179, 254, 255)
        AND TRANSAKTIONER.DATO >= :LDATETIMEOFPRIORCLOSEDAY
        AND TRANSAKTIONER.DATO <= :I_DATETIMEOFCLOSEDAY
        AND ((TRANSAKTIONER.DAGSAFSLUTTET = 0)
        OR (TRANSAKTIONER.DAGSAFSLUTTET IS NULL))
        AND ((TRANSAKTIONER.AFDELING_ID = :LMASTERMACHINE
        AND TRANSAKTIONER.UAFD_NAVN = :LMASTERDEPARTMENT)
        OR (TRANSAKTIONER.UAFD_NAVN IN (SELECT
                                            AFDELINGSGRUPPERDETAIL.MASKINE
                                        FROM AFDELINGSGRUPPERDETAIL
                                        WHERE
                                            AFDELINGSGRUPPERDETAIL.GRUPPENAVN = :LGRUPPENAVN)));

    /*Insert tracing log*/
    /*This is also used to find when the last close day was made later (and earlier in this procedure)*/
    INSERT INTO TRANSAKTIONER (
        ART,
        DATO,
        EKSPEDIENT,
        BONTEXT,
        BONNR,
        AFDELING_ID,
        UAFD_NAVN,
        UAFD_GRP_NAVN)
    VALUES (
        180,
        :I_DATETIMEOFCLOSEDAY,
        :I_CLERK,
        'DAGSAFSLUTTET KORREKT (SkyPOS)',
        :O_CLOSEDAYREFERENCENUMBER,
        :LMASTERDEPARTMENT,
        :LMASTERMACHINE,
        'Ingen undergruppe');
  END

  SUSPEND;
END^


CREATE OR ALTER PROCEDURE P_CREATECOLOR (
    I_COLOR TYPE OF COLUMN FARVER.NAVN,
    I_SHORTKEY TYPE OF COLUMN FARVER.V509INDEX,
    I_WEBNAME TYPE OF COLUMN FARVER.WEB_NAVN,
    I_STDMOMS TYPE OF COLUMN FARVER.MOMSSATS)
RETURNS (
    O_COLOR TYPE OF COLUMN FARVER.NAVN)
AS
DECLARE VARIABLE LRANDOM_NUMBER TYPE OF COLUMN FARVER.V509INDEX;
DECLARE VARIABLE LRANDOM_NUMBER_INT INTEGER;
BEGIN
  /* ************************ */
  /* Created: 07-03-2025 */
  /* Author: MIvi */
  /* Purpose: */
  /* This procedure will insert 1 new color should it not already exists */
  /* if the short code is not set, then this routine will create a random 5 digtis code */
  /* The routine will check for the name UPPER. It will return the correct name, if it is just a matter of casing */
  /* ************************ */

  /*Lets see if the UPPER color exists. If so, return this as it is cretaed. If not - create it*/
  SELECT
      FARVER.NAVN
  FROM FARVER
  WHERE
      UPPER(FARVER.NAVN) = UPPER(:I_COLOR)
  INTO :O_COLOR;

  /*Check color exists*/
  IF (:O_COLOR IS NULL) THEN
  BEGIN
    IF (COALESCE(:I_STDMOMS, '') = '') THEN
    BEGIN
      /*We have not yet fetched default data*/
      SELECT
          STAMDATA_PRG_EXT.STDMOMS
      FROM STAMDATA_PRG_EXT
      WHERE
          STAMDATA_PRG_EXT.AFDELING_ID = (SELECT
                                              AFDELING.AFDELINGSNUMMER
                                          FROM AFDELING
                                          WHERE
                                              AFDELING.HOVEDAFDELING = 1)
      INTO :I_STDMOMS;
    END
    /*
     The short number for this is in EasyPOS v1 smartimport set to the first 5 letters of the itemgroups name
     if this already exists,  a randomm 5 digit number is assigned.
     Here we just  assign a random 5 digit number
    */
    /*
     If I_SHORTKEY is provided (not null or empty), use it.
     Otherwise generate random 5-digit shortkey.
    */
    IF (I_SHORTKEY IS NOT NULL AND TRIM(I_SHORTKEY) <> '') THEN
    BEGIN
      :LRANDOM_NUMBER = :I_SHORTKEY;
    END
    ELSE
    BEGIN
      -- Generate random number until unique
      :LRANDOM_NUMBER_INT = (10000 + CAST(RAND() * 90000 AS INTEGER));
      :LRANDOM_NUMBER = CAST(:LRANDOM_NUMBER_INT AS VARCHAR(5));
      -- lets keep doing this until we get a unique number
      WHILE (EXISTS(SELECT 1 FROM FARVER WHERE V509INDEX = :LRANDOM_NUMBER)) DO
      BEGIN
        -- Generate new number
        :LRANDOM_NUMBER_INT = (10000 + CAST(RAND() * 90000 AS INTEGER));
        :LRANDOM_NUMBER = CAST(:LRANDOM_NUMBER_INT AS VARCHAR(5));
      END
    END

    /*Color does not exists - create new */
    INSERT INTO FARVER (
        NAVN,
        UNIKTNAVN,
        V509INDEX,
        V509INDEXUNIKT,
        DTUNR,
        PROVSATS,
        STD_AV,
        FARVEVISES,
        MOMSSATS,
        FLEURID,
        TR_ID,
        FRANCHISE,
        WEB_NAVN,
        BESKRIVELSE)
    VALUES (
        :I_COLOR,
        UPPER(:I_COLOR),
        :LRANDOM_NUMBER,
        UPPER(:LRANDOM_NUMBER),
        0,
        0,
        0,
        1,
        :I_STDMOMS,
        '',
        '',
        0,
        COALESCE(:I_COLOR,''),
        '');


    /*Return name of color*/
    :O_COLOR = :I_COLOR;

    EXECUTE PROCEDURE OPRET_AFDFARVER(:I_COLOR,
                                      UPPER(:I_COLOR));
  END
  SUSPEND;
END^


CREATE OR ALTER PROCEDURE P_CREATEHEADITEM (
