    I_POPUPVARETEKSTER TYPE OF COLUMN VARER.POPUPVARETEKSTER,
    I_SERVICEYDELSE TYPE OF COLUMN VARER.SERVICEYDELSE,
    I_SERIENRVARE TYPE OF COLUMN VARER.SERIENRVARE,
    I_ETIKETANTAL TYPE OF COLUMN VARER.ETIKETANTAL,
    I_FAMILY TYPE OF COLUMN VARER.FAMILY,
    I_FAMILYVARER TYPE OF COLUMN VARER.FAMILYVARER,
    I_WEBVARER TYPE OF COLUMN VARER.WEBVARER,
    I_MEDTAGIOPFYLDNING TYPE OF COLUMN VARER.MEDTAGIOPFYLDNING,
