    I_SEASON TYPE OF COLUMN VARER.SEASON,
    I_QUALITY TYPE OF COLUMN VARER.QUALITY,
    I_EXCLUDE_FROM_REPORTS TYPE OF COLUMN VARER.EXCLUDE_FROM_REPORTS,
    I_WEIGHT TYPE OF COLUMN VARER.WEIGHT,
    I_NORMALSOLDAMOUNT TYPE OF COLUMN VARER_DETAIL.NORMALSTKANTAL)
RETURNS (
    O_ARTICLENUMBER TYPE OF COLUMN VARER.PLU_NR,
    O_LEVERID TYPE OF COLUMN VARER.LEVERID,
    O_VAREGRPID TYPE OF COLUMN VARER.VAREGRPID)
AS
BEGIN
  /* ************************
    Created: 06-03-2025
    Author: MIvi
    Purpose:
    This procedure will create 1 new item in VARER. This is often referrred to as 1 head item.
    After it has been created succesfully, we call procedure to create department specific records in VARER_DETAIL
    the fields below are not included in the parameters (explanation for each)

    if I_PluNr is null or empty, we will fetch next item number

    Created: 18-03-2025
    Author: MIvi
    Will return brand and itemgroup if spelling was off

    Created: 07-06-2025
    Author: MIvi
    BrandID and ItemGroupID has been added as input. Will be used if set as short number (V509INDEX) on brands (LEVERANDOERER) and Itemgroups (VAREGRUPPPER)
  ************************ */

  IF (COALESCE(:I_PLU_NR, '') = '') THEN
  BEGIN
    /*No article number set - set one now*/
    SELECT
        O_ARTICLENUMBER
    FROM P_NEXTHEADITEMARTICLENUMBER
    INTO :I_PLU_NR;
  END

  /* *************** BRAND ***************** */
  /* Lets check if the supplier exists, and if not create it */
  SELECT
      O_BRAND
  FROM P_CREATESUPPLIER(:I_LEVERID, :I_LEVERV509INDEX, NULL, NULL, NULL, NULL, :I_LEVERID, NULL, NULL, NULL, NULL)
  INTO :I_LEVERID;
  :O_LEVERID = :I_LEVERID;
  /* *************** BRAND ***************** */

  /* *************** ITEMGROUP ***************** */
  /*Check itemgroup exists*/
  SELECT
      O_ITEMGROUP
  FROM P_CREATEITEMGROUP(:I_VAREGRPID, :I_VAREGRPIDV509INDEX, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
  INTO :I_VAREGRPID;
  :O_VAREGRPID = :I_VAREGRPID;
  /* *************** ITEMGROUP ***************** */




  INSERT INTO VARER (
      PLU_NR,
      UNIKTPLU_NR,
      ALT_VARE_NR,
      VARENAVN1,
      VARENAVN2,
      VARENAVN3,
      MODEL,
      KATEGORI1,
      KATEGORI2,
      KATEGORI3,
      KATEGORI4,
      KATEGORI5,
      VARETEKSTER,
      LEVERID,
      VAREGRPID,
      POPUPVARETEKSTER,
      SERVICEYDELSE,
      SERIENRVARE,
      ETIKETANTAL,
      FAMILY,
      FAMILYVARER,
      WEBVARER,
      MEDTAGIOPFYLDNING,
      BRUGRABAT,
      SPERRET,
      INGENNULSALG,
      FIFOITEM,
      INTRASTAT,
      WEBAKTIV,
      GENDER,
      COUNTRY_OF_ORIGION,
      SEASON,
      QUALITY,
      EXCLUDE_FROM_REPORTS,
      WEIGHT)
  VALUES (
      :I_PLU_NR,
      UPPER(:I_PLU_NR),
      COALESCE(:I_ALT_VARE_NR, ''),
      COALESCE(:I_VARENAVN1, ''),
      COALESCE(:I_VARENAVN2, ''),
      COALESCE(:I_VARENAVN3, ''),
      :I_MODEL,
      COALESCE(:I_KATEGORI1, ''),
      COALESCE(:I_KATEGORI2, ''),
      COALESCE(:I_KATEGORI3, ''),
      COALESCE(:I_KATEGORI4, ''),
      COALESCE(:I_KATEGORI5, ''),
      COALESCE(:I_VARETEKSTER, ''),
      :I_LEVERID,
      :I_VAREGRPID,
      COALESCE(:I_POPUPVARETEKSTER, 0),
      COALESCE(:I_SERVICEYDELSE, 0),
      COALESCE(:I_SERIENRVARE, 0),
      COALESCE(:I_ETIKETANTAL, 1),
      COALESCE(:I_FAMILY, 0),
      COALESCE(:I_FAMILYVARER, ''),
      :I_WEBVARER,
      :I_MEDTAGIOPFYLDNING,
      :I_BRUGRABAT,
      :I_SPERRET,
      :I_INGENNULSALG,
      :I_FIFOITEM,
      COALESCE(:I_INTRASTAT, ''),
      :I_WEBAKTIV,
      COALESCE(:I_GENDER, ''),
      COALESCE(:I_COUNTRY_OF_ORIGION, ''),
      COALESCE(:I_SEASON, ''),
      COALESCE(:I_QUALITY, ''),
      :I_EXCLUDE_FROM_REPORTS,
      :I_WEIGHT);

  :O_ARTICLENUMBER = :I_PLU_NR;

  EXECUTE PROCEDURE OPRET_AFDVARER(NULL, --Engros price. Unused
                                   :I_PLU_NR,
                                   NULL, --Costprice - will be set on variants instead, which via trigger sets this on
                                   NULL, --Minimum stock - will be set on variants instead
                                   NULL, --Reorder amonut - when null will be set inside procuder
                                   COALESCE(:I_NORMALSOLDAMOUNT, 1), --Normal sold amonut
                                   NULL, --Currency - when null will be set inside procuder
                                   NULL, --Unused
                                   'NOW');
  SUSPEND;
END^


CREATE OR ALTER PROCEDURE P_CREATEITEMGROUP (
