    I_ITEMGROUPID TYPE OF COLUMN VAREGRUPPER.V509INDEX,
    I_STDMOMS TYPE OF COLUMN VAREGRUPPER.MOMSSATS,
    I_STD_AV TYPE OF COLUMN VAREGRUPPER.STD_AV,
    I_HOVEDGRUPPE TYPE OF COLUMN VAREGRUPPER.HOVEDGRUPPE,
    I_GRUPPEREF TYPE OF COLUMN VAREGRUPPER.GRUPPEREF,
    I_BENYTLAENGDE TYPE OF COLUMN VAREGRUPPER.BENYTLAENGDE,
    I_WEB_NAVN TYPE OF COLUMN VAREGRUPPER.WEB_NAVN,
    I_ALTIDWEBVARE TYPE OF COLUMN VAREGRUPPER.ALTIDWEBVARE)
RETURNS (
    O_ITEMGROUP TYPE OF COLUMN VAREGRUPPER.NAVN)
AS
DECLARE VARIABLE LRANDOM_NUMBER TYPE OF COLUMN VAREGRUPPER.V509INDEX;
DECLARE VARIABLE LRANDOM_NUMBER_INT INTEGER;
DECLARE VARIABLE L_CHECKNAME TYPE OF COLUMN VAREGRUPPER.NAVN;
DECLARE VARIABLE L_COUNTER INTEGER;
BEGIN
  /* ************************
    Created: 06-03-2025
    Author: MIvi
    Purpose:
    This procedure will insert 1 new itemgroup should it not already exists
    if the short code is not set, then this routine will create a random 5 digtis code
    if I_STDMOMS is null, they will be fetched from head department

    Created: 18-03-2025
    Author: MIvi
    Will check if Upper(NAme) exists, and return what is found. Otherwise create


    Changed: 07-05-2025
    Author: Mivi
    Added: VareGrpID has been added as an input. If this is set, this will be used. If not a random value will be set
   ************************ */

  /*Initialize*/
  O_ITEMGROUP = NULL;
  LRANDOM_NUMBER = '';
  LRANDOM_NUMBER_INT = 0;
  L_CHECKNAME = '';
  L_COUNTER = 0;

  /*If ItemgroupID is set, this value is more important and will be used.*/
  /*Itemgroup will be set to a unique value by adding a number after if not already unique*/
  IF (COALESCE(:I_ITEMGROUPID, '') <> '') THEN
  BEGIN
    -- Check if ITEMGROUPID exists already
    SELECT
        NAVN
    FROM VAREGRUPPER
    WHERE
        UPPER(V509INDEX) = UPPER(:I_ITEMGROUPID)
    INTO :O_ITEMGROUP;

    IF (:O_ITEMGROUP IS NOT NULL) THEN
    BEGIN
      /*It was already there - lets just leave*/
      SUSPEND;
      EXIT;
    END

    -- Not found: we will use I_ITEMGROUPID as V509INDEX
    :LRANDOM_NUMBER = :I_ITEMGROUPID;

    -- Ensure NAVN is unique
    :L_CHECKNAME = :I_ITEMGROUP;
    :L_COUNTER = 1;
    WHILE (EXISTS(SELECT 1 FROM VAREGRUPPER WHERE UPPER(NAVN) = UPPER(:L_CHECKNAME))) DO
    BEGIN
      :L_CHECKNAME = :I_ITEMGROUP || ' ' || :L_COUNTER;
      :L_COUNTER = :L_COUNTER + 1;
    END

    -- Moms fallback
    IF (COALESCE(:I_STDMOMS, '') = '') THEN
    BEGIN
      SELECT
          STDMOMS
      FROM STAMDATA_PRG_EXT
      WHERE
          AFDELING_ID = (SELECT
                             AFDELINGSNUMMER
                         FROM AFDELING
                         WHERE
                             HOVEDAFDELING = 1)
      INTO :I_STDMOMS;
    END

    -- Insert new item group
    INSERT INTO VAREGRUPPER (
        NAVN,
        UNIKTNAVN,
        V509INDEX,
        MOMSSATS,
        STD_AV,
        HOVEDGRUPPE,
        GRUPPEREF,
        BENYTLAENGDE,
        WEB_NAVN,
        ALTIDWEBVARE)
    VALUES (
        :L_CHECKNAME,
        UPPER(:L_CHECKNAME),
        :LRANDOM_NUMBER,
        :I_STDMOMS,
        COALESCE(:I_STD_AV, 0),
        COALESCE(:I_HOVEDGRUPPE, 0),
        COALESCE(:I_GRUPPEREF, ''),
        COALESCE(:I_BENYTLAENGDE, 0),
        COALESCE(:I_WEB_NAVN, ''),
        COALESCE(:I_ALTIDWEBVARE, 0));

    O_ITEMGROUP = :L_CHECKNAME;

    EXECUTE PROCEDURE OPRET_AFDVAREGRUPPE(:L_CHECKNAME,
                                          UPPER(:L_CHECKNAME),
                                          'Ingen varegruppe',
                                          0,
                                          '');

    SUSPEND;
    EXIT;
  END

  /*Now - ItemgroupID was not set, and one will be create if needs be*/
  SELECT
      VAREGRUPPER.NAVN
  FROM VAREGRUPPER
  WHERE
      UPPER(VAREGRUPPER.NAVN) = UPPER(:I_ITEMGROUP)
  INTO :O_ITEMGROUP;

  /*Check supplier exists*/
  IF (:O_ITEMGROUP IS NULL) THEN
  BEGIN
    IF (COALESCE(:I_STDMOMS, '') = '') THEN
    BEGIN
      /*We have not yet fetched default data*/
      SELECT
          STAMDATA_PRG_EXT.STDMOMS
      FROM STAMDATA_PRG_EXT
      WHERE
          STAMDATA_PRG_EXT.AFDELING_ID = (SELECT
                                              AFDELING.AFDELINGSNUMMER
                                          FROM AFDELING
                                          WHERE
                                              AFDELING.HOVEDAFDELING = 1)
      INTO :I_STDMOMS;
    END

    /*
     The short number for this is in EasyPOS v1 smartimport set to the first 5 letters of the itemgroups name
     if this already exists,  a randomm 5 digit number is assigned.
     Here we just  assign a random 5 digit number
    */
    IF (COALESCE(:I_ITEMGROUPID, '') = '') THEN
    BEGIN
      :LRANDOM_NUMBER_INT = (10000 + CAST(RAND() * 90000 AS INTEGER));
      :LRANDOM_NUMBER = CAST(:LRANDOM_NUMBER_INT AS VARCHAR(5));
      -- lets keep doing this until we get a unique number
      WHILE (EXISTS(SELECT 1 FROM VAREGRUPPER WHERE V509INDEX = :LRANDOM_NUMBER)) DO
      BEGIN
        -- Generate new number
        :LRANDOM_NUMBER_INT = (10000 + CAST(RAND() * 90000 AS INTEGER));
        :LRANDOM_NUMBER = CAST(:LRANDOM_NUMBER_INT AS VARCHAR(5));
      END
    END
    ELSE
    BEGIN
      :LRANDOM_NUMBER = :I_ITEMGROUPID;
    END

    /*Itemgroup does not exists - create new */
    INSERT INTO VAREGRUPPER (
        NAVN,
        UNIKTNAVN,
        V509INDEX,
        MOMSSATS)
    VALUES (
        :I_ITEMGROUP,
        UPPER(:I_ITEMGROUP),
        :LRANDOM_NUMBER,
        :I_STDMOMS);

    :O_ITEMGROUP = :I_ITEMGROUP;

    EXECUTE PROCEDURE OPRET_AFDVAREGRUPPE(:I_ITEMGROUP,
                                          UPPER(:I_ITEMGROUP),
                                          'Ingen varegruppe',
                                          0,
                                          ''); -- Unsed but left becuase of legacy calls from EasyPOS Kontor v1
  END
  SUSPEND;
END^


CREATE OR ALTER PROCEDURE P_CREATEITEMS (
    I_UUID TYPE OF COLUMN CREATEUPDATE_ITEM.UUID,
    I_MASTER_ID TYPE OF COLUMN CREATEUPDATE_ITEM.ID,
    I_MESSAGE BLOB SUB_TYPE BINARY SEGMENT SIZE 80)
RETURNS (
    O_SUCCES INTEGER,
    O_MESSAGE BLOB SUB_TYPE BINARY SEGMENT SIZE 80)
AS
DECLARE VARIABLE LITEMLOG TYPE OF COLUMN CREATEUPDATE_ITEMS.ACTION_;
DECLARE VARIABLE LARTICLENUMBER TYPE OF COLUMN CREATEUPDATE_ITEMS.ARTICLENUMBER;
DECLARE VARIABLE LBRAND TYPE OF COLUMN CREATEUPDATE_ITEMS.BRAND;
DECLARE VARIABLE LBRANDID TYPE OF COLUMN CREATEUPDATE_ITEMS.BRANDID;
DECLARE VARIABLE LITEMGROUP TYPE OF COLUMN CREATEUPDATE_ITEMS.ITEMGROUP;
DECLARE VARIABLE LITEMGROUPID TYPE OF COLUMN CREATEUPDATE_ITEMS.ITEMGROUPID;
DECLARE VARIABLE LSTYLE TYPE OF COLUMN CREATEUPDATE_ITEMS.STYLE;
DECLARE VARIABLE LBARCODE TYPE OF COLUMN CREATEUPDATE_ITEMS.BARCODE;
DECLARE VARIABLE LEANNUMBER TYPE OF COLUMN CREATEUPDATE_ITEMS.EANNUMBER;
DECLARE VARIABLE LCOLOR TYPE OF COLUMN CREATEUPDATE_ITEMS.COLOR;
DECLARE VARIABLE LSIZE_ TYPE OF COLUMN CREATEUPDATE_ITEMS.SIZE_;
DECLARE VARIABLE LLENGTH_ TYPE OF COLUMN CREATEUPDATE_ITEMS.LENGTH_;
DECLARE VARIABLE LNEWARTICLENUMBER BLOB SUB_TYPE 0 SEGMENT SIZE 80;
DECLARE VARIABLE LNEWBARCODES BLOB SUB_TYPE 0 SEGMENT SIZE 80;
DECLARE VARIABLE LPROCESSLOG BLOB SUB_TYPE 0 SEGMENT SIZE 80;
DECLARE VARIABLE LSQLSTRING BLOB SUB_TYPE 0 SEGMENT SIZE 80;
DECLARE VARIABLE L_START_TIME DATE;
DECLARE VARIABLE L_UPDATE_TIME DATE;
DECLARE VARIABLE LPROCESS TYPE OF COLUMN CREATEUPDATE_ITEM.PROCESS;
DECLARE VARIABLE LID TYPE OF COLUMN CREATEUPDATE_ITEMS.ID;
DECLARE VARIABLE LSUPPLIERSARTICLENUMBER TYPE OF COLUMN CREATEUPDATE_ITEMS.SUPPLIERSARTICLENUMBER;
DECLARE VARIABLE LDESCRIPTION TYPE OF COLUMN CREATEUPDATE_ITEMS.DESCRIPTION;
DECLARE VARIABLE LDESCRIPTION2 TYPE OF COLUMN CREATEUPDATE_ITEMS.DESCRIPTION2;
DECLARE VARIABLE LDESCRIPTION3 TYPE OF COLUMN CREATEUPDATE_ITEMS.DESCRIPTION3;
DECLARE VARIABLE LLONGDESCRIPTION TYPE OF COLUMN CREATEUPDATE_ITEMS.LONGDESCRIPTION;
DECLARE VARIABLE LSEASON TYPE OF COLUMN CREATEUPDATE_ITEMS.SEASON;
DECLARE VARIABLE LGENDER TYPE OF COLUMN CREATEUPDATE_ITEMS.GENDER;
DECLARE VARIABLE LWEIGHT TYPE OF COLUMN CREATEUPDATE_ITEMS.WEIGHT;
DECLARE VARIABLE LCOUNTRYOFORIGION TYPE OF COLUMN CREATEUPDATE_ITEMS.COUNTRYOFORIGION;
DECLARE VARIABLE LQUALITY TYPE OF COLUMN CREATEUPDATE_ITEMS.QUALITY;
DECLARE VARIABLE LTARRIF_INTRASTAT TYPE OF COLUMN CREATEUPDATE_ITEMS.TARRIF_INTRASTAT;
DECLARE VARIABLE LLABELQUANTITY TYPE OF COLUMN CREATEUPDATE_ITEMS.LABELQUANTITY;
DECLARE VARIABLE LCATEGORY1 TYPE OF COLUMN CREATEUPDATE_ITEMS.CATEGORY1;
DECLARE VARIABLE LCATEGORY2 TYPE OF COLUMN CREATEUPDATE_ITEMS.CATEGORY2;
DECLARE VARIABLE LCATEGORY3 TYPE OF COLUMN CREATEUPDATE_ITEMS.CATEGORY3;
DECLARE VARIABLE LCATEGORY4 TYPE OF COLUMN CREATEUPDATE_ITEMS.CATEGORY4;
DECLARE VARIABLE LCATEGORY5 TYPE OF COLUMN CREATEUPDATE_ITEMS.CATEGORY5;
DECLARE VARIABLE LSALESPRICEITEM TYPE OF COLUMN CREATEUPDATE_ITEMS.SALESPRICEITEM;
DECLARE VARIABLE LSERVICE TYPE OF COLUMN CREATEUPDATE_ITEMS.SERVICE;
DECLARE VARIABLE LWEBITEM TYPE OF COLUMN CREATEUPDATE_ITEMS.WEBITEM;
DECLARE VARIABLE LBLOCKEDFORSALE TYPE OF COLUMN CREATEUPDATE_ITEMS.BLOCKEDFORSALE;
DECLARE VARIABLE LBLOACKEDFORREGULATION TYPE OF COLUMN CREATEUPDATE_ITEMS.BLOACKEDFORREGULATION;
DECLARE VARIABLE LNORMALSOLDQUANTITY TYPE OF COLUMN CREATEUPDATE_ITEMS.NORMALSOLDQUANTITY;
DECLARE VARIABLE LHANDLED INTEGER;
DECLARE VARIABLE LVARIANTCREATE INTEGER;
DECLARE VARIABLE LHEADITEMCREATE INTEGER;
DECLARE VARIABLE LNUMBEROFHEADITEMS INTEGER;
DECLARE VARIABLE LNUMBEROFVARIANTS INTEGER;
DECLARE VARIABLE LSPERRET INTEGER;
DECLARE VARIABLE LSTDMOMS TYPE OF COLUMN STAMDATA_PRG_EXT.STDMOMS;
DECLARE VARIABLE LSTDVALUTA TYPE OF COLUMN STAMDATA_PRG_EXT.STDVALUTA;
DECLARE VARIABLE LSTDAVANCE TYPE OF COLUMN STAMDATA_PRG_EXT.STDAVANCE;
DECLARE VARIABLE LRANDOM_NUMBER TYPE OF COLUMN LEVERANDOERER.V509INDEX;
DECLARE VARIABLE LINT INTEGER;
DECLARE VARIABLE LMESSAGE BLOB SUB_TYPE 0 SEGMENT SIZE 80;
BEGIN
  /* ************************ */
  /* Created: 26-02-2025 */
  /* Author: MIvi */
  /* Purpose: */
  /* This procedure will handle records we need to Create */
  /* At this point we know its about CREATE and that record exists with the UUID in CREATEUPDATE_ITEM */
  /* */
  /* The routine will update the status of the iteration for each record in CREATEUPDATE_ITEMS */
  /* The routine will:*/
  /*  - Check if head item exists. We can see: Does not exists, exists 1 time or several times. */
  /*    - if it does not exist, we create it. Brand and Itemgroup will checked and created if they do not exists*/
  /*    - If we find 1, we fetch brand, itemgroup, style and article number (we do not know how we found this one*/
  /*    - If we find more than 1, we skip. Settings hits more than one*/
  /*  - Check if variant exists. Here we can also get 0, 1 or severawl*/
  /*    - if it does not exist, we create it. Color, Size and length wull be checked and created if they do not exists*/
  /*    - If we find 1, we fetch Color, size, length, barcode and EAN NUmber (we do not know how we found this one*/
  /*    - If we find more than 1, we skip. Settings hits more than one*/
  /* - After an variant has been created, we will handle prices. And in this routine we will also handle head item discounts and variant discounts*/
  /* - We will build a list of newly create head items and newly created variants. This will be returned to the caller at the end aling with a log. */
  /* */
  /* */
  /* Updated: 07-06-2025*/
  /* Author: Mivi*/
  /* Will now also fetch BrandID and ItemgroupID. If set program will see, if they exists, and overwrite whatever Brand and/or Itemgroup was set by caller. */
  /* If they do not exists, this value wil be used as short number (V509INDEX) on brands (LEVERANDOERER) and Itemgroups (VAREGRUPPPER) if not empty  */
  /* */
  /* */
  /* ************************ */

  /*Initiate log*/
  :LPROCESSLOG = COALESCE(:I_MESSAGE, '') || ASCII_CHAR(13) || ASCII_CHAR(10) || 'Insert procedure started' || ASCII_CHAR(13) || ASCII_CHAR(10);
  :LPROCESSLOG = :LPROCESSLOG || '  UUID: ' || COALESCE(:I_UUID, '') || ASCII_CHAR(13) || ASCII_CHAR(10);
  :LPROCESSLOG = :LPROCESSLOG || '  Type: Insert' || ASCII_CHAR(13) || ASCII_CHAR(10);

  /*Set master for external access to starter */
  SELECT
      O_START_TIME
  FROM P_CREATEUPDATE_ITEMS_START(:I_UUID, :LPROCESSLOG)
  INTO :L_START_TIME;

  /*Initiate*/
  :LPROCESS = 0;
  :LSTDMOMS = NULL;
  :LSTDVALUTA = NULL;
  :LSTDAVANCE = NULL;
  :LNEWARTICLENUMBER = '';
  :LNEWBARCODES = '';
  :LINT = 0;
  :LMESSAGE = '';

  /*Iterate through items*/
  FOR SELECT
          CREATEUPDATE_ITEMS.ID,
          CREATEUPDATE_ITEMS.ARTICLENUMBER,
          CREATEUPDATE_ITEMS.BRAND,
          CREATEUPDATE_ITEMS.BRANDID,
          CREATEUPDATE_ITEMS.ITEMGROUP,
          CREATEUPDATE_ITEMS.ITEMGROUPID,
          CREATEUPDATE_ITEMS.STYLE,
          CREATEUPDATE_ITEMS.BARCODE,
          CREATEUPDATE_ITEMS.EANNUMBER,
          CREATEUPDATE_ITEMS.SUPPLIERSARTICLENUMBER,
          CREATEUPDATE_ITEMS.COLOR,
          CREATEUPDATE_ITEMS.SIZE_,
          CREATEUPDATE_ITEMS.LENGTH_,
          CREATEUPDATE_ITEMS.DESCRIPTION,
          CREATEUPDATE_ITEMS.DESCRIPTION2,
          CREATEUPDATE_ITEMS.DESCRIPTION3,
          CREATEUPDATE_ITEMS.LONGDESCRIPTION,
          CREATEUPDATE_ITEMS.SEASON,
          CREATEUPDATE_ITEMS.GENDER,
          CREATEUPDATE_ITEMS.WEIGHT,
          CREATEUPDATE_ITEMS.COUNTRYOFORIGION,
          CREATEUPDATE_ITEMS.QUALITY,
          CREATEUPDATE_ITEMS.TARRIF_INTRASTAT,
          CREATEUPDATE_ITEMS.LABELQUANTITY,
          CREATEUPDATE_ITEMS.CATEGORY1,
          CREATEUPDATE_ITEMS.CATEGORY2,
          CREATEUPDATE_ITEMS.CATEGORY3,
          CREATEUPDATE_ITEMS.CATEGORY4,
          CREATEUPDATE_ITEMS.CATEGORY5,
          CREATEUPDATE_ITEMS.SALESPRICEITEM,
          CREATEUPDATE_ITEMS.SERVICE,
          CREATEUPDATE_ITEMS.WEBITEM,
          CREATEUPDATE_ITEMS.BLOCKEDFORSALE,
          CREATEUPDATE_ITEMS.BLOACKEDFORREGULATION,
          CREATEUPDATE_ITEMS.NORMALSOLDQUANTITY
      FROM CREATEUPDATE_ITEMS
      WHERE
          CREATEUPDATE_ITEMS.MASTER_ID = :I_MASTER_ID
          AND CREATEUPDATE_ITEMS.HANDLED IN (0,1) /*We only want those which have not been handled in a potential update senario*/
      ORDER BY
          CREATEUPDATE_ITEMS.ID ASC
      INTO :LID,
           :LARTICLENUMBER,
           :LBRAND,
           :LBRANDID,
           :LITEMGROUP,
           :LITEMGROUPID,
           :LSTYLE,
           :LBARCODE,
           :LEANNUMBER,
           :LSUPPLIERSARTICLENUMBER,
           :LCOLOR,
           :LSIZE_,
           :LLENGTH_,
           :LDESCRIPTION,
           :LDESCRIPTION2,
           :LDESCRIPTION3,
           :LLONGDESCRIPTION,
           :LSEASON,
           :LGENDER,
           :LWEIGHT,
           :LCOUNTRYOFORIGION,
           :LQUALITY,
           :LTARRIF_INTRASTAT,
           :LLABELQUANTITY,
           :LCATEGORY1,
           :LCATEGORY2,
           :LCATEGORY3,
           :LCATEGORY4,
           :LCATEGORY5,
           :LSALESPRICEITEM,
           :LSERVICE,
           :LWEBITEM,
           :LBLOCKEDFORSALE,
           :LBLOACKEDFORREGULATION,
           :LNORMALSOLDQUANTITY
  DO
  BEGIN
    /* Lets see blocked value
            0: Item is not blocked
            1: Item is block for sale
            2: Item is blocked for stock regulation
            3: Item is blocked for both sale and stock regulation
            */
    IF ((:LBLOACKEDFORREGULATION > 0) AND
        (:LBLOCKEDFORSALE > 0)) THEN
    BEGIN
      :LSPERRET = 3;
    END
    ELSE
    IF (:LBLOACKEDFORREGULATION > 0) THEN
    BEGIN
      :LSPERRET = 2;
    END
    ELSE
    IF (:LBLOCKEDFORSALE > 0) THEN
    BEGIN
      :LSPERRET = 1;
    END
    ELSE
    BEGIN
      :LSPERRET = 0;
    END

    /*Increment process*/
    :LPROCESS = :LPROCESS + 1;
    /*Log action*/
    :LPROCESSLOG = :LPROCESSLOG || 'Handling record ' || :LPROCESS || ASCII_CHAR(13) || ASCII_CHAR(10);
    /*Set process*/
    SELECT
        O_END_TIME
    FROM P_CREATEUPDATE_ITEMS_UPDATE(:I_UUID, 1, :LPROCESS, :LPROCESSLOG)
    INTO :L_UPDATE_TIME;

    :LITEMLOG = '';
    :LHANDLED = 0;
    :LVARIANTCREATE = 0;
    :LHEADITEMCREATE = 0;
    :LNUMBEROFHEADITEMS = 0;


    /* ******************************************* */
    /* *************** HEAD ITEM ***************** */
    /* ******************************************* */
    /*
      To create we need  Brand + Item + Style. And it must not exists in advance.
      if article number is also set, this must also not exists in advance.
    */
    IF (((COALESCE(:LBRAND, '') = '') AND
        (COALESCE(:LBRANDID, '') = '')) OR
        ((COALESCE(:LITEMGROUP, '') = '') AND
        (COALESCE(:LITEMGROUPID, '') = '')) OR
        (COALESCE(:LSTYLE, '') = '')) THEN
    BEGIN
      /*We do not have brand and itemgroup and style in place. Cannot create item*/
      :LITEMLOG = 'Skipped item. ' || ASCII_CHAR(13) || ASCII_CHAR(10);
      :LPROCESSLOG = :LPROCESSLOG || '  Skipped item. Brand: ' || COALESCE(:LBRAND, '') || ', Itemgroup: ' || COALESCE(:LITEMGROUP, '') || ', Style: ' || COALESCE(:LSTYLE, '') || ' not set correctly.' || ASCII_CHAR(13) || ASCII_CHAR(10);
      :LHEADITEMCREATE = 255;
    END
    ELSE
    BEGIN
      IF (NOT(COALESCE(:LBRANDID, '') = '')) THEN
      BEGIN
        /*User has entered an ID to the brand. Lets check this and return the brand name*/
        /*Using a Brand ID takes precendence over Brand name*/
        IF (EXISTS(SELECT
                       NAVN
                   FROM LEVERANDOERER
                   WHERE
                       UPPER(LEVERANDOERER.V509INDEX) = UPPER(:LBRANDID))) THEN
        BEGIN
          /*The ID we came in with exists. So lets fetch the brand.*/
          SELECT
              NAVN
          FROM LEVERANDOERER
          WHERE
              UPPER(LEVERANDOERER.V509INDEX) = UPPER(:LBRANDID)
          INTO :LBRAND;
        END
        ELSE
        BEGIN
          /*The brand ID does not exist. There we just use the brand name that came in, and later we will create it*/
        END
      END

      IF (NOT(COALESCE(:LITEMGROUPID, '') = '')) THEN
      BEGIN
        /*User has entered an ID to the itemgroup. Lets check this and return the itemgroup name*/
        IF (EXISTS(SELECT
                       NAVN
                   FROM VAREGRUPPER
                   WHERE
                       UPPER(VAREGRUPPER.V509INDEX) = UPPER(:LITEMGROUPID))) THEN
        BEGIN
          /*The ID we came in with exists. So lets fetch the itemgroup.*/
          SELECT
              NAVN
          FROM VAREGRUPPER
          WHERE
              UPPER(VAREGRUPPER.V509INDEX) = UPPER(:LITEMGROUPID)
          INTO :LITEMGROUP;
        END
        ELSE
        BEGIN
          /*The itemgroup ID does not exist. There we just use the brand name that came in, and later we will create it*/
        END
      END


      /*Brand + Itemgroup + Style (Uppers) are not empty and we might have an article number*/
      /*Lets check if they are unique and can be created*/
      SELECT
          COUNT(*)
      FROM VARER
      WHERE
          ((VARER.PLU_NR = COALESCE(:LARTICLENUMBER, '')) OR (UPPER(VARER.LEVERID) = UPPER(COALESCE(:LBRAND, ''))
          AND UPPER(VARER.VAREGRPID) = UPPER(COALESCE(:LITEMGROUP, ''))
          AND UPPER(VARER.MODEL) = UPPER(COALESCE(:LSTYLE, ''))))
      INTO :LNUMBEROFHEADITEMS;

      IF (:LNUMBEROFHEADITEMS > 1) THEN
      BEGIN
        /*Apperently we already have an item with these information*/
        :LITEMLOG = 'Skipped head item. ' || ASCII_CHAR(13) || ASCII_CHAR(10);
        :LPROCESSLOG = :LPROCESSLOG || '  Skipped head item. Combination already in data several times. Brand: ' || COALESCE(:LBRAND, '') || ', Itemgroup: ' || COALESCE(:LITEMGROUP, '') || ', Style: ' || COALESCE(:LSTYLE, '') || '), article number: ' || COALESCE(:LARTICLENUMBER, '') || ASCII_CHAR(13) || ASCII_CHAR(10);
        :LHEADITEMCREATE = 254;
      END
      ELSE
      BEGIN
        IF (:LNUMBEROFHEADITEMS = 1) THEN
        BEGIN
          /*Apperently we already have an item with these information*/
          /*Lets fetch values not set yet via items send in*/
          SELECT
              VARER.PLU_NR,
              VARER.LEVERID,
              VARER.VAREGRPID,
              VARER.MODEL
          FROM VARER
          WHERE
              ((VARER.PLU_NR = COALESCE(:LARTICLENUMBER, '')) OR (UPPER(VARER.LEVERID) = UPPER(COALESCE(:LBRAND, ''))
              AND UPPER(VARER.VAREGRPID) = UPPER(COALESCE(:LITEMGROUP, ''))
              AND UPPER(VARER.MODEL) = UPPER(COALESCE(:LSTYLE, ''))))
          INTO :LARTICLENUMBER,
               :LBRAND,
               :LITEMGROUP,
               :LSTYLE;

          :LITEMLOG = 'Skipped head item. ' || ASCII_CHAR(13) || ASCII_CHAR(10);
          :LPROCESSLOG = :LPROCESSLOG || '  Skipped head item. Combination already in data. Brand: ' || COALESCE(:LBRAND, '') || ', Itemgroup: ' || COALESCE(:LITEMGROUP, '') || ', Style: ' || COALESCE(:LSTYLE, '') || ', article number: ' || COALESCE(:LARTICLENUMBER, '') || ASCII_CHAR(13) || ASCII_CHAR(10);
          :LHEADITEMCREATE = 1;
          :LNUMBEROFHEADITEMS = 1;
        END
        ELSE
        BEGIN
          /*Head item does not exist. Lets create it*/

          SELECT
              O_ARTICLENUMBER,
              O_LEVERID,
              O_VAREGRPID
          FROM P_CREATEHEADITEM(:LARTICLENUMBER, '', :LDESCRIPTION, :LDESCRIPTION2, :LDESCRIPTION3, :LSTYLE, :LCATEGORY1, :LCATEGORY2, :LCATEGORY3, :LCATEGORY4, :LCATEGORY5, :LLONGDESCRIPTION, :LBRAND, :LBRANDID, :LITEMGROUP, :LITEMGROUPID, 0, :LSERVICE, 0, :LLABELQUANTITY, 0, '', :LWEBITEM, 1, 1, :LSPERRET, 0, 0, :LTARRIF_INTRASTAT, :LWEBITEM, :LGENDER, :LCOUNTRYOFORIGION, :LSEASON, :LQUALITY, 0, :LWEIGHT, :LNORMALSOLDQUANTITY)
          INTO :LARTICLENUMBER,
               :LBRAND,
               :LITEMGROUP;

          :LITEMLOG = 'Head item created. ' || ASCII_CHAR(13) || ASCII_CHAR(10);
          :LPROCESSLOG = :LPROCESSLOG || '  Head item created. Combination Brand: ' || COALESCE(:LBRAND, '') || ', Itemgroup: ' || COALESCE(:LITEMGROUP, '') || ', Style: ' || COALESCE(:LSTYLE, '') || ', article number: ' || COALESCE(:LARTICLENUMBER, '') || ASCII_CHAR(13) || ASCII_CHAR(10);

          /*Lets build a list of new article numbers */
          SELECT
              O_LIST
          FROM P_ADDTOLIST(:LARTICLENUMBER, :LNEWARTICLENUMBER)
          INTO :LNEWARTICLENUMBER;

          /*Create head iktem */
          :LHEADITEMCREATE = 1;
          :LNUMBEROFHEADITEMS = 1;
        END
      END
    END
    /* ******************************************* */
    /* *************** HEAD ITEM ***************** */
    /* ******************************************* */



    /* ******************************************* */
    /* *************** VARIANT ***************** */
    /* ******************************************* */

    /*Lets handle variants*/
    IF ((:LHEADITEMCREATE = 1) AND
        (:LNUMBEROFHEADITEMS = 1)) THEN
    BEGIN
      /*Head item has been created or we have found exactly 1
       Continues creating variants
       If color, size and length are not set - we will set them to empty strings. Later upon create we need to be sure, default values are set.
       if barcode is not set, we will create on.
       */

      /*Lets set null variables to empty strings*/
      /*Lets set empty color, size and length to "default no color/size/length"*/
      IF (COALESCE(:LCOLOR, '') = '') THEN
      BEGIN
        SELECT
            FARVER.NAVN
        FROM FARVER
        WHERE
            FARVER.V509INDEX = '99999'
        INTO :LCOLOR;
      END
      IF (COALESCE(:LSIZE_, '') = '') THEN
      BEGIN
        SELECT
            STOERRELSER.NAVN
        FROM STOERRELSER
        WHERE
            STOERRELSER.V509INDEX = '99999'
        INTO :LSIZE_;
      END
      IF (COALESCE(:LLENGTH_, '') = '') THEN
      BEGIN
        SELECT
            LAENGDE.NAVN
        FROM LAENGDE
        WHERE
            LAENGDE.V509INDEX = '99999'
        INTO :LLENGTH_;
      END
      IF (COALESCE(:LBARCODE, '') = '') THEN
        :LBARCODE = '';
      IF (COALESCE(:LEANNUMBER, '') = '') THEN
        :LEANNUMBER = '';

      IF ((:LCOLOR = '') AND
          (:LSIZE_ = '') AND
          (:LLENGTH_ = '') AND
          (:LBARCODE = '') AND
          (:LEANNUMBER = '')) THEN
      BEGIN
        /*We have no way of identifying this variant, so we cannot create anything new*/
        :LITEMLOG = :LITEMLOG || 'Skipped variant. ' || ASCII_CHAR(13) || ASCII_CHAR(10);
        :LPROCESSLOG = :LPROCESSLOG || '  Skipped variant. Color: ' || COALESCE(:LCOLOR, '') || ', Size: ' || COALESCE(:LSIZE_, '') || ', Length: ' || COALESCE(:LLENGTH_, '') || ', barcode: ' || COALESCE(:LBARCODE, '') || ', EANNumber: ' || COALESCE(:LEANNUMBER, '') || ' not set correctly.' || ASCII_CHAR(13) || ASCII_CHAR(10);
        :LVARIANTCREATE = 255;
      END
      ELSE
      BEGIN

        /*Lets count if we can find any variants with this combination*/
        SELECT
            COUNT(*)
        FROM VAREFRVSTR
        WHERE
            ((VAREFRVSTR.V509INDEX = :LBARCODE) OR (VAREFRVSTR.EANNUMMER = :LEANNUMBER) OR ((UPPER(VAREFRVSTR.FARVE_NAVN) = UPPER(:LCOLOR))
            AND (UPPER(VAREFRVSTR.STOERRELSE_NAVN) = UPPER(:LSIZE_))
            AND (UPPER(VAREFRVSTR.LAENGDE_NAVN) = UPPER(:LLENGTH_))))
            AND (VAREFRVSTR.VAREPLU_ID = :LARTICLENUMBER)
        INTO :LNUMBEROFVARIANTS;

        IF (:LNUMBEROFVARIANTS > 1) THEN
        BEGIN
          /*We found a match of more than one. Skip*/
          :LITEMLOG = 'Skipped variant. ' || ASCII_CHAR(13) || ASCII_CHAR(10);
          :LPROCESSLOG = :LPROCESSLOG || '  Skipped variant. Combination already in data several times. Color: ' || COALESCE(:LCOLOR, '') || ', Size: ' || COALESCE(:LSIZE_, '') || ', Length: ' || COALESCE(:LLENGTH_, '') || ', barcode: ' || COALESCE(:LBARCODE, '') || ', EANNumber: ' || COALESCE(:LEANNUMBER, '') || ' not set correctly.' || ASCII_CHAR(13) || ASCII_CHAR(10);
          :LVARIANTCREATE = 254;
        END
        ELSE
        IF (:LNUMBEROFVARIANTS = 1) THEN
        BEGIN
          /*We found excactly one. Fetch*/
          SELECT
              VAREFRVSTR.FARVE_NAVN,
              VAREFRVSTR.LAENGDE_NAVN,
              VAREFRVSTR.STOERRELSE_NAVN,
              VAREFRVSTR.V509INDEX,
              VAREFRVSTR.EANNUMMER
          FROM VAREFRVSTR
          WHERE
              (VAREFRVSTR.V509INDEX = :LBARCODE) OR (VAREFRVSTR.EANNUMMER = :LEANNUMBER) OR ((UPPER(VAREFRVSTR.FARVE_NAVN) = UPPER(:LCOLOR))
              AND (UPPER(VAREFRVSTR.STOERRELSE_NAVN) = UPPER(:LSIZE_))
              AND (UPPER(VAREFRVSTR.LAENGDE_NAVN) = UPPER(:LLENGTH_)))
          INTO :LCOLOR,
               :LLENGTH_,
               :LSIZE_,
               :LBARCODE,
               :LEANNUMBER;

          :LITEMLOG = :LITEMLOG || 'Skipped variant. ' || ASCII_CHAR(13) || ASCII_CHAR(10);
          :LPROCESSLOG = :LPROCESSLOG || '  Skipped variant. Combination already in data. Color: ' || COALESCE(:LCOLOR, '') || ', Size: ' || COALESCE(:LSIZE_, '') || ', Length: ' || COALESCE(:LLENGTH_, '') || ', barcode: ' || COALESCE(:LBARCODE, '') || ', EANNumber: ' || COALESCE(:LEANNUMBER, '') || ' not set correctly.' || ASCII_CHAR(13) || ASCII_CHAR(10);
          :LVARIANTCREATE = 1;
        END
        ELSE
        BEGIN
          /*We found none. Create*/

          SELECT
              O_BARCODE
          FROM P_CREATEVARIANT(:LARTICLENUMBER, :LCOLOR, :LLENGTH_, :LSIZE_, 0, 0, :LBARCODE, :LEANNUMBER, :LDESCRIPTION, :LDESCRIPTION2, :LDESCRIPTION3, :LBRAND, :LSUPPLIERSARTICLENUMBER, :LITEMGROUP, :LSTYLE, 0, :LSALESPRICEITEM, '', 0, :LSPERRET, 0, 0, :LNORMALSOLDQUANTITY, NULL, 0, 0, NULL)
          INTO :LBARCODE;


          /*Lets build a list of new article numbers */
          SELECT
              O_LIST
          FROM P_ADDTOLIST(:LBARCODE, :LNEWBARCODES)
          INTO :LNEWBARCODES;


          /* *********************************** */
          /* ******* DEPARTMENT VARIANT UPDATE  ******** */
          /* *********************************** */
          /*NOW - Prices etc */
          SELECT
              O_SUCCES,
              O_MESSAGE
          FROM P_UPDATEDEPARTMENTVARIANT(:LID, :LBARCODE)
          INTO :LINT,
               :LMESSAGE;
          :LPROCESSLOG = :LPROCESSLOG || :LMESSAGE;
          /* *********************************** */
          /* ******* DEPARTMENT VARIANT UPDATE  ******** */
          /* *********************************** */
        END
      END
    END

    /* ******************************************* */
    /* *************** VARIANT ***************** */
    /* ******************************************* */


    :LHANDLED = 0;
    IF (:LHEADITEMCREATE > 0) THEN
      :LHANDLED = :LHANDLED + 100;
    IF (:LVARIANTCREATE > 0) THEN
      :LHANDLED = :LHANDLED + 1000;
    UPDATE CREATEUPDATE_ITEMS SET
        CREATEUPDATE_ITEMS.ACTION_ = :LITEMLOG,
        CREATEUPDATE_ITEMS.HANDLED = :LHANDLED
    WHERE
        CREATEUPDATE_ITEMS.ID = :LID;
  END

  /*Now we have properbly created some items.*/
  /*We can then fetch new head article via LNEWARTICLENUMBER. If nmot empty we have create something */
  /*We can fetch new variants via LNEWVARIANTS. If not empty we have create something*/

  IF ((:LNEWARTICLENUMBER <> '') OR
      (LNEWBARCODES <> '')) THEN
  BEGIN
    /*Something has been created*/
    LSQLSTRING = 'SELECT';
    LSQLSTRING = LSQLSTRING || '  VARER.PLU_NR,';
    LSQLSTRING = LSQLSTRING || '  VAREFRVSTR.V509INDEX ';
    LSQLSTRING = LSQLSTRING || 'FROM VARER ';
    LSQLSTRING = LSQLSTRING || '  INNER JOIN VAREFRVSTR ON ';
    LSQLSTRING = LSQLSTRING || '    VAREFRVSTR.VAREPLU_ID = VARER.PLU_NR ';
    LSQLSTRING = LSQLSTRING || 'WHERE ';
    IF (:LNEWARTICLENUMBER = '') THEN
    BEGIN
      :LNEWARTICLENUMBER = '''!"#!"#!"#!"#!"#!"#!"#!"#!"#!"#''';
    END
    IF (:LNEWBARCODES = '') THEN
    BEGIN
      :LNEWBARCODES = '''!"#!"#!"#!"#!"#!"#!"#!"#!"#!"#''';
    END
    LSQLSTRING = LSQLSTRING || '  VARER.PLU_NR IN (' || :LNEWARTICLENUMBER || ') OR VAREFRVSTR.V509INDEX IN (' || :LNEWBARCODES || ') ';
    LSQLSTRING = LSQLSTRING || 'ORDER BY ';
    LSQLSTRING = LSQLSTRING || '  VARER.PLU_NR, ';
    LSQLSTRING = LSQLSTRING || '  VAREFRVSTR.V509INDEX ';

    :LPROCESSLOG = :LPROCESSLOG || 'New items and variants: ' || ASCII_CHAR(13) || ASCII_CHAR(10);
    FOR EXECUTE STATEMENT :LSQLSTRING
            INTO
            :LARTICLENUMBER,
            :LBARCODE
    DO
    BEGIN
      :LPROCESSLOG = :LPROCESSLOG || '  Articlenumber: ' || :LARTICLENUMBER || '   Barcode: ' || :LBARCODE || ASCII_CHAR(13) || ASCII_CHAR(10);
    END
  END
  /*We also need to see, if we have created new head items but without variants. */
  /*This should not happen - If we have created a new item, as a minimum we should have 1 variant */
  /*Log action*/
  :LPROCESSLOG = :LPROCESSLOG || 'Done';

  /*Set done*/
  SELECT
      O_END_TIME
  FROM P_CREATEUPDATE_ITEMS_STOP(:I_UUID, :LPROCESS, :LPROCESSLOG)
  INTO :L_UPDATE_TIME;

  :O_MESSAGE = :LPROCESSLOG;
  :O_SUCCES = 1;
  SUSPEND;
END^


CREATE OR ALTER PROCEDURE P_CREATELENGTH (
    I_LENGTH TYPE OF COLUMN LAENGDE.NAVN,
    I_SHORTKEY TYPE OF COLUMN LAENGDE.V509INDEX,
    I_WEBNAME TYPE OF COLUMN LAENGDE.WEB_NAVN,
    I_STDMOMS TYPE OF COLUMN LAENGDE.MOMSSATS)
RETURNS (
    O_LENGTH TYPE OF COLUMN LAENGDE.NAVN)
AS
DECLARE VARIABLE LRANDOM_NUMBER TYPE OF COLUMN LAENGDE.V509INDEX;
DECLARE VARIABLE LRANDOM_NUMBER_INT INTEGER;
BEGIN
  /* ************************
    Created: 07-03-2025
    Author: MIvi
    Purpose:
    This procedure will insert 1 new length should it not already exists
    if the short code is not set, then this routine will create a random 5 digtis code
    The routine will check for the name UPPER. It will return the correct name, if it is just a matter of casing
   ************************ */

  SELECT
      LAENGDE.NAVN
  FROM LAENGDE
  WHERE
      UPPER(LAENGDE.NAVN) = UPPER(:I_LENGTH)
  INTO :O_LENGTH;

  /*Check length exists*/
  IF (:O_LENGTH IS NULL) THEN
  BEGIN
    IF (COALESCE(:I_STDMOMS, '') = '') THEN
    BEGIN
      /*We have not yet fetched default data*/
      SELECT
          STAMDATA_PRG_EXT.STDMOMS
      FROM STAMDATA_PRG_EXT
      WHERE
          STAMDATA_PRG_EXT.AFDELING_ID = (SELECT
                                              AFDELING.AFDELINGSNUMMER
                                          FROM AFDELING
                                          WHERE
                                              AFDELING.HOVEDAFDELING = 1)
      INTO :I_STDMOMS;
    END
    /*
     The short number for this is in EasyPOS v1 smartimport set to the first 5 letters of the itemgroups name
     if this already exists,  a randomm 5 digit number is assigned.
     Here we just  assign a random 5 digit number
    */
    /*
     If I_SHORTKEY is provided (not null or empty), use it.
     Otherwise generate random 5-digit shortkey.
    */
    IF (I_SHORTKEY IS NOT NULL AND
        TRIM(I_SHORTKEY) <> '') THEN
    BEGIN
      :LRANDOM_NUMBER = :I_SHORTKEY;
    END
    ELSE
    BEGIN
      :LRANDOM_NUMBER_INT = (10000 + CAST(RAND() * 90000 AS INTEGER));
      :LRANDOM_NUMBER = CAST(:LRANDOM_NUMBER_INT AS VARCHAR(5));
      -- lets keep doing this until we get a unique number
      WHILE (EXISTS(SELECT 1 FROM LAENGDE WHERE V509INDEX = :LRANDOM_NUMBER)) DO
      BEGIN
        -- Generate new number
        :LRANDOM_NUMBER_INT = (10000 + CAST(RAND() * 90000 AS INTEGER));
        :LRANDOM_NUMBER = CAST(:LRANDOM_NUMBER_INT AS VARCHAR(5));
      END
    END

    --    /*length does not exists - create new */
    INSERT INTO LAENGDE (
        NAVN,
        UNIKTNAVN,
        V509INDEX,
        V509INDEXUNIKT,
        DTUNR,
        PROVSATS,
        STD_AV,
        LAENGDEVISES,
        MOMSSATS,
        FLEURID,
        TR_ID,
        FRANCHISE,
        WEB_NAVN)
    VALUES (
        :I_LENGTH,
        UPPER(:I_LENGTH),
        :LRANDOM_NUMBER,
        UPPER(:LRANDOM_NUMBER),
        0,
        0,
        0,
        1,
        :I_STDMOMS,
        '',
        '',
        0,
        COALESCE(:I_WEBNAME,''));

    :O_LENGTH = :I_LENGTH;
    EXECUTE PROCEDURE OPRET_AFDLAENGDER(:I_LENGTH,
                                        UPPER(:I_LENGTH));
  END
  SUSPEND;
END^


