CREATE OR ALTER PROCEDURE P_CREATESUPPLIER (
    I_BRAND TYPE OF COLUMN LEVERANDOERER.NAVN,
    I_BRANDID TYPE OF COLUMN LEVERANDOERER.V509INDEX,
    I_STDMOMS TYPE OF COLUMN LEVERANDOERER.MOMSSATS,
    I_STDVALUTA TYPE OF COLUMN LEVERANDOERER.STDVALUTA,
    I_STDAVANCE TYPE OF COLUMN LEVERANDOERER.STD_AV,
    I_PROVSATS TYPE OF COLUMN LEVERANDOERER.PROVSATS,
    I_WEBNAME TYPE OF COLUMN LEVERANDOERER.WEB_NAVN,
    I_BTXEXPORTABLE TYPE OF COLUMN LEVERANDOERER.BTX_EKSPORT,
    I_CUSTOMERNUMER TYPE OF COLUMN LEVERANDOERER.CUSTOMERNUMBER,
    I_ALWAYSWEBITEM TYPE OF COLUMN LEVERANDOERER.ALTIDWEBVARE,
    I_EXPORTABLE TYPE OF COLUMN LEVERANDOERER.EXPORTABLE)
RETURNS (
    O_BRAND TYPE OF COLUMN LEVERANDOERER.NAVN)
AS
DECLARE VARIABLE LRANDOM_NUMBER TYPE OF COLUMN LEVERANDOERER.V509INDEX;
DECLARE VARIABLE LRANDOM_NUMBER_INT INTEGER;
DECLARE VARIABLE L_CHECKNAME TYPE OF COLUMN LEVERANDOERER.NAVN;
DECLARE VARIABLE L_COUNTER INTEGER;
DECLARE VARIABLE LCOUNTRYCODE TYPE OF COLUMN LANDE.LANDEKODE;
BEGIN
  /* ************************
    Created: 06-03-2025
    Author: MIvi
    Purpose:
    This procedure will insert 1 new supplier, should it not already exists
    if the short code is not set, then this routine will create a random 5 digtis code
    if I_STDMOMS, I_STDVALUTA or i_stdavance is null, they will be fetched from head department

    Created: 18-03-2025
    Author: MIvi
    Will check if Upper(NAme) exists, and return what is found. Otherwise create

    Changed: 07-05-2025
    Author: Mivi
    Added: BrandID has been added as an input. If this is set, this will be used. If not a random value will be set

    Changed: 11-08-2025
    Author; Mivi
    Added: More input variables.
   ************************ */
  /*Initialize*/
  O_BRAND = NULL;
  LRANDOM_NUMBER = '';
  LRANDOM_NUMBER_INT = 0;
  L_CHECKNAME = '';
  L_COUNTER = 0;

  /*If brandID is set, this value is more important and will be used.*/
  /*Brand will be set to a unique value by adding a number after if not already unique*/
  IF (COALESCE(:I_BRANDID, '') <> '') THEN
  BEGIN
    /* Check if BRANDID already exists */
    SELECT
        NAVN
    FROM LEVERANDOERER
    WHERE
        UPPER(V509INDEX) = UPPER(:I_BRANDID)
    INTO :O_BRAND;

    IF (:O_BRAND IS NOT NULL) THEN
    BEGIN
      /*It was already there - lets just leave*/
      SUSPEND;
      EXIT;
    END

    /* Create new using I_BRANDID as V509INDEX */
    :LRANDOM_NUMBER = :I_BRANDID;

    /* Check if brand name exists, add number suffix if needed */
    :L_CHECKNAME = :I_BRAND;
    :L_COUNTER = 1;

    WHILE (EXISTS(SELECT 1 FROM LEVERANDOERER WHERE UPPER(NAVN) = UPPER(:L_CHECKNAME))) DO
    BEGIN
      :L_CHECKNAME = :I_BRAND || ' ' || :L_COUNTER;
      :L_COUNTER = :L_COUNTER + 1;
    END

    /* If VAT/Currency/Avance not provided, fetch defaults */
    IF ((:I_STDMOMS IS NULL) OR
        (:I_STDVALUTA IS NULL) OR
        (:I_STDAVANCE IS NULL)) THEN
    BEGIN
      SELECT
          STDMOMS,
          STDVALUTA,
          STDAVANCE
      FROM STAMDATA_PRG_EXT
      WHERE
          AFDELING_ID = (SELECT
                             AFDELINGSNUMMER
                         FROM AFDELING
                         WHERE
                             HOVEDAFDELING = 1)
      INTO :I_STDMOMS,
           :I_STDVALUTA,
           :I_STDAVANCE;
    END

    /*Lets fetch country code for head department. If not set/found set it to 008*/
    /*Can always be changed*/
    SELECT
        S.STDLAND
    FROM STAMDATA_PRG_EXT S
    WHERE
        S.AFDELING_ID = (SELECT
                             AFDELINGSNUMMER
                         FROM AFDELING
                         WHERE
                             AFDELING.HOVEDAFDELING = 1)
    INTO :LCOUNTRYCODE;
    IF (COALESCE(:LCOUNTRYCODE, '') = '') THEN
    BEGIN
      :LCOUNTRYCODE = '008';
    END

    /*Insert new Brand*/
    INSERT INTO LEVERANDOERER (
        NAVN,
        UNIKTNAVN,
        V509INDEX,
        V509INDEXUNIKT,
        MOMSSATS,
        STDVALUTA,
        STD_AV,
        PROVSATS,
        ALTIDWEBVARE,
        BTX_EKSPORT,
        EXPORTABLE,
        CUSTOMERNUMBER,
        WEB_NAVN,
        LAND_ID)
    VALUES (
        :L_CHECKNAME,
        UPPER(:L_CHECKNAME),
        :LRANDOM_NUMBER,
        UPPER(:LRANDOM_NUMBER),
        :I_STDMOMS,
        :I_STDVALUTA,
        :I_STDAVANCE,
        COALESCE(:I_PROVSATS, 0),
        COALESCE(:I_ALWAYSWEBITEM, 0),
        COALESCE(:I_BTXEXPORTABLE, 0),
        COALESCE(:I_EXPORTABLE, 0),
        COALESCE(:I_CUSTOMERNUMER, ''),
        COALESCE(:I_WEBNAME, ''),
        :LCOUNTRYCODE);

    /*Set output*/
    O_BRAND = :L_CHECKNAME;

    /*Create department brands records*/
    EXECUTE PROCEDURE OPRET_AFDLEVER(:L_CHECKNAME,
                                     UPPER(:L_CHECKNAME),
                                     '', --This is unsed, but is left for legacy calls from EasyPOS Kontor v1
                                     0, --This is unsed, but is left for legacy calls from EasyPOS Kontor v1
                                     ''); --This is unsed, but is left for legacy calls from EasyPOS Kontor v1

    /*And now we leave the routine*/
    SUSPEND;
    EXIT;
  END

  /*Brand ID was not set - we will continue with the name alone*/
  SELECT
      LEVERANDOERER.NAVN
  FROM LEVERANDOERER
  WHERE
      UPPER(LEVERANDOERER.NAVN) = UPPER(:I_BRAND)
  INTO :O_BRAND;

  /*Check supplier exists*/
  IF (:O_BRAND IS NULL) THEN
  BEGIN
    /*Brand did not exist. Lets create it*/
    IF ((:I_STDMOMS IS NULL) OR
        (:I_STDVALUTA IS NULL) OR
        (:I_STDAVANCE IS NULL)) THEN
    BEGIN
      /*We have not yet fetched default data*/
      SELECT
          STAMDATA_PRG_EXT.STDMOMS,
          STAMDATA_PRG_EXT.STDVALUTA,
          STAMDATA_PRG_EXT.STDAVANCE
      FROM STAMDATA_PRG_EXT
      WHERE
          STAMDATA_PRG_EXT.AFDELING_ID = (SELECT
                                              AFDELING.AFDELINGSNUMMER
                                          FROM AFDELING
                                          WHERE
                                              AFDELING.HOVEDAFDELING = 1)
      INTO :I_STDMOMS,
           :I_STDVALUTA,
           :I_STDAVANCE;
    END

    /*
     The short number for this is in EasyPOS v1 smartimport set to the first 5 letters of the brands name
     if this already exists,  a randomm 5 digit number is assigned.
     Here we just  assign a random 5 digit number
     */
    :LRANDOM_NUMBER_INT = (10000 + CAST(RAND() * 90000 AS INTEGER));
    :LRANDOM_NUMBER = CAST(:LRANDOM_NUMBER_INT AS VARCHAR(5));
    -- lets keep doing this until we get a unique number
    WHILE (EXISTS(SELECT 1 FROM LEVERANDOERER WHERE V509INDEX = :LRANDOM_NUMBER)) DO
    BEGIN
      -- Generate new number
      :LRANDOM_NUMBER_INT = (10000 + CAST(RAND() * 90000 AS INTEGER));
      :LRANDOM_NUMBER = CAST(:LRANDOM_NUMBER_INT AS VARCHAR(5));
    END

    /*Lets fetch country code for head department. If not set/found set it to 008*/
    /*Can always be changed*/
    SELECT
        S.STDLAND
    FROM STAMDATA_PRG_EXT S
    WHERE
        S.AFDELING_ID = (SELECT
                             AFDELINGSNUMMER
                         FROM AFDELING
                         WHERE
                             AFDELING.HOVEDAFDELING = 1)
    INTO :LCOUNTRYCODE;
    IF (COALESCE(:LCOUNTRYCODE, '') = '') THEN
    BEGIN
      :LCOUNTRYCODE = '008';
    END


    /*Supplier does not exists - create new */
    INSERT INTO LEVERANDOERER (
        NAVN,
        UNIKTNAVN,
        V509INDEX,
        V509INDEXUNIKT,
        MOMSSATS,
        STDVALUTA,
        STD_AV,
