CREATE OR ALTER PROCEDURE P_CREATEVARIANT (
    I_VAREPLU_ID TYPE OF COLUMN VAREFRVSTR.VAREPLU_ID,
    I_FARVE_NAVN TYPE OF COLUMN VAREFRVSTR.FARVE_NAVN,
    I_LAENGDE_NAVN TYPE OF COLUMN VAREFRVSTR.LAENGDE_NAVN,
    I_STOERRELSE_NAVN TYPE OF COLUMN VAREFRVSTR.STOERRELSE_NAVN,
    I_SORTGRUPPE TYPE OF COLUMN VAREFRVSTR.SORTGRUPPE,
    I_SORTINDEX TYPE OF COLUMN VAREFRVSTR.SORTINDEX,
    I_V509INDEX TYPE OF COLUMN VAREFRVSTR.V509INDEX,
    I_EANNUMMER TYPE OF COLUMN VAREFRVSTR.EANNUMMER,
    I_VARENAVN1 TYPE OF COLUMN VAREFRVSTR.VARENAVN1,
    I_VARENAVN2 TYPE OF COLUMN VAREFRVSTR.VARENAVN2,
    I_VARENAVN3 TYPE OF COLUMN VAREFRVSTR.VARENAVN3,
    I_LEVERID TYPE OF COLUMN VAREFRVSTR.LEVERID,
    I_LEVVARENR TYPE OF COLUMN VAREFRVSTR.LEVVARENR,
    I_VAREGRPID TYPE OF COLUMN VAREFRVSTR.VAREGRPID,
    I_MODEL TYPE OF COLUMN VAREFRVSTR.MODEL,
    I_GARANTIKODE TYPE OF COLUMN VAREFRVSTR.GARANTIKODE,
    I_SALGSPRISVARE TYPE OF COLUMN VAREFRVSTR.SALGSPRISVARE,
    I_KORTNUMMER TYPE OF COLUMN VAREFRVSTR.KORTNUMMER,
    I_STOPALLEFELTER TYPE OF COLUMN VAREFRVSTR.STOPALLEFELTER,
    I_SPERRET TYPE OF COLUMN VAREFRVSTR.SPERRET,
    I_MINBEHOLDNING TYPE OF COLUMN VAREFRVSTR_DETAIL.MINBEHOLDNING,
    I_GENBESTILLING TYPE OF COLUMN VAREFRVSTR_DETAIL.GENBESTILLING,
    I_NORMALSTKANTAL TYPE OF COLUMN VAREFRVSTR_DETAIL.NORMALSTKANTAL,
    I_MOMSSATS TYPE OF COLUMN VAREFRVSTR_DETAIL.MOMSSATS,
    I_VEJETKOSTPRISSTK TYPE OF COLUMN VAREFRVSTR_DETAIL.VEJETKOSTPRISSTK,
    I_SALGSPRISSTK TYPE OF COLUMN VAREFRVSTR_DETAIL.SALGSPRISSTK,
    I_ENHEDSNOGLE TYPE OF COLUMN VAREFRVSTR_DETAIL.ENHEDSNOGLE)
RETURNS (
    O_BARCODE TYPE OF COLUMN VAREFRVSTR.V509INDEX)
AS
DECLARE VARIABLE LLOBENUMMER INTEGER;
DECLARE VARIABLE LSTDGARANTIKODE TYPE OF COLUMN VAREFRVSTR.GARANTIKODE;
BEGIN
  /* ************************
    Created: 07-03-2025
    Author: MIvi
    Purpose:
    This procedure will create 1 new variant in VAREFRVSTR. This is often referrred to as 1 variant.
    After it has been created succesfully, we call procedure to create department specific records in VAREFRVSTR_DETAIL
    These will be created with some default values, and will afterwards be updated (not in this procedure)

    if I_V509INDEX is null or empty, we will create a new barcode using I_VAREPLU_ID
  ************************ */

  IF (COALESCE(:I_V509INDEX, '') = '') THEN
  BEGIN
    /*No barcode set - set one now*/
    SELECT
        O_BARCODE,
        O_SEQUENCENUMBER
    FROM P_GENERATE_BARCODE(:I_VAREPLU_ID)
    INTO :I_V509INDEX,
         :LLOBENUMMER;
  END

  SELECT
      O_COLOR
  FROM P_CREATECOLOR(:I_FARVE_NAVN, NULL, :I_FARVE_NAVN, NULL)
  INTO :I_FARVE_NAVN;

  SELECT
      O_SIZE
  FROM P_CREATESIZE(:I_STOERRELSE_NAVN, NULL, :I_STOERRELSE_NAVN, 0, 0, NULL)
  INTO :I_STOERRELSE_NAVN;

  SELECT
      O_LENGTH
  FROM P_CREATELENGTH(:I_LAENGDE_NAVN, NULL, :I_LAENGDE_NAVN, NULL)
  INTO :I_LAENGDE_NAVN;


  SELECT
      S.STDGARANTIKODE
  FROM AFDELING A
      INNER JOIN STAMDATA_PRG_EXT S ON
            S.AFDELING_ID = A.AFDELINGSNUMMER
  WHERE
      A.HOVEDAFDELING = 1
  INTO :LSTDGARANTIKODE;

  /*EANNumber must not be empty or null - if so set to barcode (this is legacy way of doing stuff)*/
  IF (COALESCE(:I_EANNUMMER, '') = '') THEN
    I_EANNUMMER = :I_V509INDEX;

  IF (COALESCE(:LLOBENUMMER, 0) = 0) THEN
  BEGIN
    SELECT
        MAX(VAREFRVSTR.LOBENUMMER)
    FROM VAREFRVSTR
    WHERE
        VAREFRVSTR.VAREPLU_ID = :I_VAREPLU_ID
    INTO :LLOBENUMMER;
    IF (COALESCE(:LLOBENUMMER, 0) = 0) THEN
    BEGIN
      SELECT
          COUNT(*) + 1
      FROM VAREFRVSTR
      WHERE
          VAREFRVSTR.VAREPLU_ID = :I_VAREPLU_ID
      INTO :LLOBENUMMER;
    END
    ELSE
    BEGIN
      :LLOBENUMMER = :LLOBENUMMER + 1;
    END
  END

  INSERT INTO VAREFRVSTR (
      VAREPLU_ID,
      FARVE_NAVN,
      LAENGDE_NAVN,
      STOERRELSE_NAVN,
      SORTGRUPPE,
      SORTINDEX,
      V509INDEX,
      LOBENUMMER,
      EANNUMMER,
      VARENAVN1,
      VARENAVN2,
      VARENAVN3,
      LEVERID,
      LEVVARENR,
      VAREGRPID,
      MODEL,
      GARANTIKODE,
      SALGSPRISVARE,
      KORTNUMMER,
      STOPALLEFELTER,
      SPERRET)
  VALUES (
      :I_VAREPLU_ID,
      :I_FARVE_NAVN,
      :I_LAENGDE_NAVN,
      :I_STOERRELSE_NAVN,
      COALESCE(:I_SORTGRUPPE, 0),
      COALESCE(:I_SORTINDEX, 0),
      :I_V509INDEX,
      :LLOBENUMMER,
      COALESCE(:I_EANNUMMER, :I_V509INDEX), --If EANNUmber is null logic decitates we need to set it to barcode
      COALESCE(:I_VARENAVN1, ''),
      COALESCE(:I_VARENAVN2, ''),
      COALESCE(:I_VARENAVN3, ''),
      :I_LEVERID,
      COALESCE(:I_LEVVARENR, ''),
      :I_VAREGRPID,
      :I_MODEL,
      COALESCE(:I_GARANTIKODE, :LSTDGARANTIKODE),
      COALESCE(:I_SALGSPRISVARE, 0),
      COALESCE(:I_KORTNUMMER, ''),
      COALESCE(:I_STOPALLEFELTER, 0),
      COALESCE(:I_SPERRET, 0));

  EXECUTE PROCEDURE OPRET_VARERDETALJEDETAIL3(0,
                                              :I_VAREPLU_ID,
                                              :I_FARVE_NAVN,
                                              :I_LAENGDE_NAVN,
                                              :I_STOERRELSE_NAVN,
                                              :I_V509INDEX,
                                              :I_MINBEHOLDNING,
                                              :I_GENBESTILLING,
                                              COALESCE(:I_NORMALSTKANTAL, 1),
                                              NULL, --Currency - will be fetched from department default value
                                              0, --provision
                                              :I_MOMSSATS,
                                              :I_VEJETKOSTPRISSTK,
                                              :I_SALGSPRISSTK,
                                              :I_ENHEDSNOGLE,
                                              :I_LEVERID,
                                              :I_VAREGRPID,
                                              'NOW',
                                              0);

  :O_BARCODE = I_V509INDEX;
  SUSPEND;
END^


