CREATE OR ALTER PROCEDURE P_DOCALCVALUTAFIELDS (
    I_AFDELING_ID VARCHAR(15),
    I_VALKURS NUMERIC(15,2),
    I_AFRUNDFAKTOR INTEGER,
    I_RETNING INTEGER,
    I_MOMSSATS NUMERIC(15,2),
    I_MOMSSATSNAVN VARCHAR(25),
    I_VALUTANAVN VARCHAR(10))
RETURNS (
    U_TEKST VARCHAR(30),
    U_BS NUMERIC(15,2),
    U_NYBS NUMERIC(15,2),
    U_NYBSMOMS NUMERIC(15,2))
AS
Declare Variable lV509INDEX VARCHAR(30);
Declare Variable BS Numeric(15,2);
Declare Variable NyBS Numeric(15,2);
Declare Variable NyBSMoms Numeric(15,2);
Declare Variable lPrisVal1 Numeric(15,2);
Declare Variable lPrisval2 Numeric(15,2);
Declare Variable lPrisNavn1 Varchar(10);
Declare Variable lPrisNavn2 Varchar(10);
Declare Variable lNySpr Numeric(18,2);
Declare Variable lNuSpr Numeric(18,2);
begin
   NyBS     = 0;
   NyBSMoms = 0; 
   BS       = 0;
   /*Genneml??ber alle varianter  */
   For SELECT
      VAREFRVSTR_DETAIL.V509INDEX,
      VAREFRVSTR_DETAIL.BEH_SALGSPRIS,
      (Select U_Belob from P_DOAFRUND((VAREFRVSTR_DETAIL.BEH_SALGSPRIS / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)),
      VAREFRVSTR_DETAIL.SALGSPRISSTK
    FROM
      VAREFRVSTR_DETAIL 
    WHERE
      VAREFRVSTR_DETAIL.AFDELING_ID=:I_AFDELING_ID
   into 
      :lV509INDEX,
      :BS,
      :NyBS,
      :lNuSpr
   do 
   Begin                
   
      SELECT
        PRISOMREGNER.PRISIVAL1,
        PRISOMREGNER.PRISIVAL2,
        PRISOMREGNER.PRISVALUTA2,
        PRISOMREGNER.PRISVALUTA3
      FROM
        PRISOMREGNER
      WHERE
        PRISOMREGNER.PRISIDKR = :lNuSpr
      INTO
        :lPrisVal1,
        :lPrisval2,
        :lPrisNavn1,
        :lPrisNavn2;
                               
      lNySpr = 0;                          
      
      if (:lPrisVal1 is null) then
      begin
        lNySpr = (Select U_Belob from P_DOAFRUND((:lNuSpr / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)); 
      end
      else
      begin
        if (:lPrisNavn1 = :I_VALUTANAVN) then
        begin
          lNySpr = :lPrisVal1; 
        end
        if (:lPrisNavn2=:I_VALUTANAVN) then
        begin
          lNySpr = :lPrisVal2; 
        end      
        if (:lNySpr = 0) then
        begin
          lNySpr = (Select U_Belob from P_DOAFRUND((:lNuSpr / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)); 
        end
      end
     
   
      NyBSMoms = (NyBS - (NyBS*100/(100+:I_MomsSats)));
      /*Beregniner nye "tal" felter for den indkommende afdeling. */
      Update VareFrvStr_Detail set
        BEH_KOSTPRIS = (Select U_Belob from P_DOAFRUND((VareFrvStr_Detail.BEH_KOSTPRIS / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)),
        BEH_SALGSPRIS = :NyBS,
        BEH_SALGSPRISMOMS = :NyBSMoms,
        VEJETKOSTPRISSTK = (Select U_Belob from P_DOAFRUND((VareFrvStr_Detail.VEJETKOSTPRISSTK / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)),
        SALGSPRISSTK = :lNySpr,
        SIDSTEKOSTPRSTK = (Select U_Belob from P_DOAFRUND((VareFrvStr_Detail.SIDSTEKOSTPRSTK / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)),
        IORDREKPR = (Select U_Belob from P_DOAFRUND((VareFrvStr_Detail.IORDREKPR / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)),
        IORDRESPR = (Select U_Belob from P_DOAFRUND((VareFrvStr_Detail.IORDRESPR / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)),
        IORDRESPR_MOMS = (Select U_Belob from P_DOAFRUND((VareFrvStr_Detail.IORDRESPR_MOMS / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)),
        STATUSKKR = (Select U_Belob from P_DOAFRUND((VareFrvStr_Detail.STATUSKKR / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)),
        STATUSSKR = (Select U_Belob from P_DOAFRUND((VareFrvStr_Detail.STATUSSKR / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)),
