CREATE OR ALTER PROCEDURE P_DOCALCVALUTAFIELDS2 (
    I_AFDELING_ID_ORI VARCHAR(15),
    I_AFDELING_ID VARCHAR(15),
    I_VALKURS NUMERIC(15,2),
    I_AFRUNDFAKTOR INTEGER,
    I_RETNING INTEGER,
    I_MOMSSATS NUMERIC(15,2),
    I_MOMSSATSNAVN VARCHAR(25),
    I_VALUTANAVN VARCHAR(10))
RETURNS (
    U_TEKST VARCHAR(30),
    U_BS NUMERIC(15,2),
    U_NYBS NUMERIC(15,2),
    U_NYBSMOMS NUMERIC(15,2))
AS
Declare Variable lV509INDEX VARCHAR(30);
Declare Variable BS Numeric(15,2);
Declare Variable NyBS Numeric(15,2);
Declare Variable NyBSMoms Numeric(15,2);
Declare Variable lPrisVal1 Numeric(15,2);
Declare Variable lPrisval2 Numeric(15,2);
Declare Variable lPrisNavn1 Varchar(10);
Declare Variable lPrisNavn2 Varchar(10);
Declare Variable lNySpr Numeric(18,2);
Declare Variable lNuSpr Numeric(18,2);
Declare Variable lNuKpr Numeric(18,2);
begin
   NyBS     = 0;
   NyBSMoms = 0; 
   BS       = 0;
   lNuSpr        = 0;
   lNuKpr        = 0;
   /*Genneml??ber alle varianter  */
   For SELECT
      VAREFRVSTR_DETAIL.V509INDEX,
      VAREFRVSTR_DETAIL.BEH_SALGSPRIS,
      VAREFRVSTR_DETAIL.VEJETKOSTPRISSTK,
      VAREFRVSTR_DETAIL.SALGSPRISSTK
    FROM
      VAREFRVSTR_DETAIL 
    WHERE
      VAREFRVSTR_DETAIL.AFDELING_ID=:I_AFDELING_ID_ORI
   into 
      :lV509INDEX,
      :BS,
      :lNuKpr,
      :lNuSpr
   do 
   Begin                
   
      SELECT
        PRISOMREGNER.PRISIVAL1,
        PRISOMREGNER.PRISIVAL2,
        PRISOMREGNER.PRISVALUTA2,
        PRISOMREGNER.PRISVALUTA3
      FROM
        PRISOMREGNER
      WHERE
        PRISOMREGNER.PRISIDKR = :lNuSpr
      INTO
        :lPrisVal1,
        :lPrisval2,
        :lPrisNavn1,
        :lPrisNavn2;
                               
      lNySpr = 0;                          
      
      if (:lPrisVal1 is null) then
      begin
        lNySpr = (Select U_Belob from P_DOAFRUND((:lNuSpr / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)); 
      end
      else
      begin
        if (:lPrisNavn1 = :I_VALUTANAVN) then
        begin
          lNySpr = :lPrisVal1; 
        end
        if (:lPrisNavn2=:I_VALUTANAVN) then
        begin
          lNySpr = :lPrisVal2; 
        end      
        if (:lNySpr = 0) then
        begin
          lNySpr = (Select U_Belob from P_DOAFRUND((:lNuSpr / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)); 
        end
      end
     
   
      NyBSMoms = (NyBS - (NyBS*100/(100+:I_MomsSats)));
      /*Beregniner nye "tal" felter for den indkommende afdeling. */
      Update VareFrvStr_Detail set
        BEH_KOSTPRIS = 0,
        BEH_SALGSPRIS = :NyBS,
        BEH_SALGSPRISMOMS = :NyBSMoms,
        VEJETKOSTPRISSTK = (Select U_Belob from P_DOAFRUND((:lNuKpr / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)),
        SALGSPRISSTK = :lNySpr,
        SIDSTEKOSTPRSTK = 0,
        IORDREKPR = 0,
        IORDRESPR = 0,
        IORDRESPR_MOMS = 0,
        STATUSKKR = 0,
        STATUSSKR = 0,
        STATUSSKR_MOMS = 0,
        GEM_KKR = 0,
        GEM_SKR = 0,
        GEM_SKR_MOMS =0,
        ENGROSPRIS = (Select U_Belob from P_DOAFRUND((VareFrvStr_Detail.ENGROSPRIS / :I_ValKurs * 100),:I_AfrundFaktor,:I_Retning)),
        MomsSats = :I_MomsSatsNavn,
        Valuta_Navn = :I_ValutaNavn
      Where
        Afdeling_ID = :I_Afdeling_ID and
        V509Index = :lV509Index;
      
      U_Tekst = :lV509INDEX;   
      U_BS = :BS;
      U_NYBS = :NYBS;
      U_NYBSMoms = :NYBSMoms;
      suspend; 
   end 
end^


