CREATE OR ALTER PROCEDURE P_GENERATE_BARCODE (
    I_ARTICLENUMBER VARCHAR(10))
RETURNS (
    O_BARCODE VARCHAR(30),
    O_SEQUENCENUMBER INTEGER)
AS
DECLARE VARIABLE TSTR VARCHAR(30);
DECLARE VARIABLE TSTR2 VARCHAR(6);
DECLARE VARIABLE CHECKDIGIT CHAR(1);
DECLARE VARIABLE C1 INTEGER;
DECLARE VARIABLE C2 INTEGER;
DECLARE VARIABLE N INTEGER;
DECLARE VARIABLE S VARCHAR(30);
DECLARE VARIABLE LEN_S INTEGER;
DECLARE VARIABLE DIGIT INTEGER;
DECLARE VARIABLE CHECKSUM INTEGER;
DECLARE VARIABLE TEMP_MOD INTEGER;
DECLARE VARIABLE LSEQUENCENUMBER INTEGER;
BEGIN
  /* ************************
    Created: 07-03-2025
    Author: MIvi
    Purpose:
    This procedure will create a barcode. The barcode is I_Articlenumber + 5 digits + check digit.
    This is how its made in v1 EasyPOS KOntor except we use 3 digit
  ************ */
  /*Let fetch tom highest current number from variants to this item*/
  SELECT
      MAX(VAREFRVSTR.LOBENUMMER)
  FROM VAREFRVSTR
  WHERE
      VAREFRVSTR.VAREPLU_ID = :I_ARTICLENUMBER
  INTO :LSEQUENCENUMBER;

  IF (:LSEQUENCENUMBER IS NULL) THEN
  BEGIN
    /*no variants present*/
    :LSEQUENCENUMBER = 1;
  END
  ELSE
  BEGIN
    /*Variants present - increment*/
    :LSEQUENCENUMBER = :LSEQUENCENUMBER + 1;
  END

  :O_SEQUENCENUMBER = :LSEQUENCENUMBER;

  -- Lets ensure we do not have to many
  IF (:LSEQUENCENUMBER > 99999) THEN
  BEGIN
    :O_BARCODE = 'FOR MANGE';
    SUSPEND;
    EXIT;
  END

  -- Set sequence to 5 digits
  :TSTR2 = LPAD(CAST(:LSEQUENCENUMBER AS VARCHAR(5)), 5, '0');

  -- Combine article number and seqence
  :TSTR = :I_ARTICLENUMBER || :TSTR2;

  -- Calculate check digit
  -- I got help from CHATGPT to make the code below.
  :C1 = 0;
  :C2 = 0;
  :S = :TSTR;
  :LEN_S = CHAR_LENGTH(:S);
  :N = 1;

  WHILE (:N <= :LEN_S) DO
  BEGIN
    -- Hent det aktuelle ciffer
    :DIGIT = CAST(SUBSTRING(S FROM :N FOR 1) AS INTEGER);

    -- Beregn summer baseret p? position
    IF ((MOD(:N, 2) = 1 AND
        MOD(:LEN_S, 2) = 0) OR
        (MOD(:N, 2) = 0 AND
        MOD(:LEN_S, 2) = 1)) THEN
      :C1 = :C1 + :DIGIT;
    ELSE
      :C2 = :C2 + :DIGIT;

    -- Increment loop-counter
    :N = :N + 1;
  END

  :TEMP_MOD = MOD((:C1 + (:C2 * 3)), 10);
  :CHECKSUM = MOD((10 - :TEMP_MOD), 10);
  :CHECKDIGIT = CAST(:CHECKSUM AS VARCHAR(1));

  -- Complete barcode
  :O_BARCODE = :TSTR || :CHECKDIGIT;

  SUSPEND;
END^


