CREATE OR ALTER PROCEDURE P_GETMAXANDMINVALUE (
    I_VALUES VARCHAR(4000))
RETURNS (
    O_MAXVALUE FLOAT,
    O_MINVALUE FLOAT)
AS
DECLARE VARIABLE max_value FLOAT; 
DECLARE VARIABLE min_value FLOAT; 
DECLARE VARIABLE current_value FLOAT; 
DECLARE VARIABLE delimiter CHAR(1) = ','; 
DECLARE VARIABLE token VARCHAR(100); 
DECLARE VARIABLE ldelimiterposition INTEGER; 
BEGIN 
  max_value = NULL; 
  min_value = NULL; 
                                       
  WHILE (CHAR_LENGTH(I_Values) > 0) DO 
  BEGIN 
    lDelimiterPosition = POSITION(delimiter IN I_Values); 
    IF (ldelimiterposition > 0) THEN 
    BEGIN 
      token = SUBSTRING(I_Values FROM 1 FOR POSITION(delimiter IN I_Values) - 1);  
      I_Values = SUBSTRING(I_Values FROM POSITION(delimiter IN I_Values) + 1); 
    END 
    else 
    begin 
      token = I_Values; 
      I_Values = ''; 
    end 
 
    current_value = CAST(token AS FLOAT); 
 
    IF (max_value IS NULL OR current_value > max_value) THEN 
      max_value = current_value; 
    IF (min_value IS NULL OR current_value < min_value) THEN 
      min_value = current_value; 
  END 
 
  O_MaxValue = max_value; 
  O_MInValue = min_value; 
  Suspend; 
END^


