       tSpr   = 0; 
       tMoms  = 0; 
       select first 1 
         vg.lantal, 
         vg.LKOSTPRIS, 
         vg.LSalgspris, 
         vg.LMoms 
       from VARERGRUPPER_OMSETNING vg 
       where 
         vg.afdeling_ID = :AfdNr and 
         ((vg.aar*100)+vg.mdr)<=((:LAar * 100)+:LMdr) and 
         vg.navn=:LNavn 
       order by aar desc, mdr desc 
       into 
         :tAntal, 
         :tKpr, 
         :TSpr, 
         :TMoms; 
 
       llAntal = llAntal + :tAntal; 
       llKpr   = llKpr + :tKpr; 
       llSpr   = llSpr + :tSpr; 
       llMoms  = llMoms + :tMoms; 
     end 
     LAntal = :llAntal; 
