CREATE OR ALTER PROCEDURE P_NEXTHEADITEMARTICLENUMBER
RETURNS (
    O_ARTICLENUMBER TYPE OF COLUMN VARER.PLU_NR)
AS
DECLARE VARIABLE LNEXTARTICLENUMBER INTEGER;
DECLARE VARIABLE LVARENUMMERLAENGDE INTEGER;
DECLARE VARIABLE LTEMPSTR TYPE OF COLUMN VARER.PLU_NR;
DECLARE VARIABLE LTEMPSTR2 TYPE OF COLUMN VARER.PLU_NR;
BEGIN
  /* ************************
    Created: 06-03-2025
    Author: MIvi
    Purpose:
    This will fetch the current number, make this 4, 6, 8 or 10 digits long according to database and increment and save back yo database
  ************ */

  /*Lets select the next Articlenumber*/
  IN AUTONOMOUS TRANSACTION DO
  BEGIN
    SELECT
        AUTOVARELOBENR,
        VARENUMMERLAENGDE
    FROM GLOBALSTAMDATA
    WHERE
        POSTNR = 2
    INTO :LNEXTARTICLENUMBER,
         :LVARENUMMERLAENGDE;
    :LTEMPSTR = CAST(:LNEXTARTICLENUMBER AS VARCHAR(30));

    IF (:LVARENUMMERLAENGDE = 4) THEN
    BEGIN
      :LTEMPSTR2 = LPAD(:LTEMPSTR, 4, '0');
    END
    ELSE
    IF (:LVARENUMMERLAENGDE = 6) THEN
    BEGIN
      :LTEMPSTR2 = LPAD(:LTEMPSTR, 6, '0');
    END
    ELSE
    IF (:LVARENUMMERLAENGDE = 8) THEN
    BEGIN
      :LTEMPSTR2 = LPAD(:LTEMPSTR, 8, '0');
    END
    ELSE
    IF (:LVARENUMMERLAENGDE = 10) THEN
    BEGIN
      :LTEMPSTR2 = LPAD(:LTEMPSTR, 10, '0');
    END
    ELSE
    BEGIN
      :LTEMPSTR2 = LPAD(:LTEMPSTR, 6, '0');
    END


    WHILE (EXISTS(SELECT 1 FROM VARER WHERE VARER.PLU_NR = :LTEMPSTR2)) DO
    BEGIN
      :LNEXTARTICLENUMBER = :LNEXTARTICLENUMBER + 1;
      :LTEMPSTR = CAST(:LNEXTARTICLENUMBER AS VARCHAR(30));
      IF (:LVARENUMMERLAENGDE = 4) THEN
      BEGIN
        :LTEMPSTR2 = LPAD(:LTEMPSTR, 4, '0');
      END
      ELSE
      IF (:LVARENUMMERLAENGDE = 6) THEN
      BEGIN
        :LTEMPSTR2 = LPAD(:LTEMPSTR, 6, '0');
      END
      ELSE
      IF (:LVARENUMMERLAENGDE = 8) THEN
      BEGIN
        :LTEMPSTR2 = LPAD(:LTEMPSTR, 8, '0');
      END
      ELSE
      IF (:LVARENUMMERLAENGDE = 10) THEN
      BEGIN
        :LTEMPSTR2 = LPAD(:LTEMPSTR, 10, '0');
      END
      ELSE
      BEGIN
        :LTEMPSTR2 = LPAD(:LTEMPSTR, 6, '0');
      END
    END

    UPDATE GLOBALSTAMDATA SET
        AUTOVARELOBENR = :LNEXTARTICLENUMBER + 1
    WHERE
        POSTNR = 2;

    :O_ARTICLENUMBER = :LTEMPSTR2;
  END
  SUSPEND;
END^


CREATE OR ALTER PROCEDURE P_SALEREPORT_TINDERBOX_MONTHLY (
    FROMDATE DATE,
    TODATE DATE,
    DELIMITER VARCHAR(1),
    RAPPVALUTA VARCHAR(10))
RETURNS (
    SUPPLIER D_LEVERNAVN,
    DESSIN D_MODEL,
    ITEMNAME D_VARENAVN,
    BARCODE D_STREGKODE,
    ITEMGROUP D_VAREGRPNAVN,
    ITEMCOLOR D_FARVENAVN,
    SEASON VARCHAR(30),
    ITEMSIZE D_STORNAVN,
    DEPARTMENTSALES VARCHAR(1200),
    EANNUMMER D_EANNUMMER,
    TOTALLINIE VARCHAR(4000),
    REPORT_CURRENCY NUMERIC(15,2),
    REPORT_CURRENCYNAME VARCHAR(10))
AS
declare variable AfdNr Varchar(30);

declare variable SumSaleStk Numeric(18,2);
declare variable SumSaleSpr Numeric(18,2);
declare variable SumWEBStk Numeric(18,2);
declare variable SumWEBSpr Numeric(18,2);

declare variable Vnr Varchar(30); 
declare variable Laengde Varchar(30);
declare variable RappValutaKurs Numeric (18,2);
declare variable RappValutaNavn Varchar(10); 
begin
  /* Fetch report currency and name */
  SELECT
    VALUTA.ISOKODE,
    VALUTALINIER.KURS
  FROM
    VALUTA
    INNER JOIN VALUTALINIER ON VALUTALINIER.VALUTA_TEKST = VALUTA.TEKST  
  WHERE
    VALUTA.TEKST = :RAPPVALUTA
  Into
    :RappValutaNavn,
    :RappValutaKurs; 

  /* Set currency and rate to be returned */   
  REPORT_CURRENCYNAME = RappValutaNavn; 
  REPORT_CURRENCY = RappValutaKurs; 
                                            
  
  /*Select sale from af given periode and iterate through result */
  for SELECT DISTINCT
    TransSale.LEVNAVN,  
    TransSale.VAREGRPID,
    VARER.MODEL,
    VAREFRVSTR.V509INDEX,
    case when (VAREFRVSTR.EANNUMMER=VAREFRVSTR.V509INDEX) then
      ''
    else       
      VAREFRVSTR.EANNUMMER
    end EANNUMMER,
    TransSale.FARVE_NAVN,
    TransSale.STOERRELSE_NAVN,
    VARER.KATEGORI1 AS saeson,
    VARER.VARENAVN1,
    TransSale.VAREFRVSTRNR,
    TransSale.LAENGDE_NAVN
  FROM
    TRANSAKTIONER TransSale
    INNER JOIN VARER ON TransSale.VAREFRVSTRNR = VARER.PLU_NR
    INNER JOIN VAREFRVSTR ON TransSale.VAREFRVSTRNR = VAREFRVSTR.VAREPLU_ID AND TransSale.FARVE_NAVN = VAREFRVSTR.FARVE_NAVN AND TransSale.LAENGDE_NAVN = VAREFRVSTR.LAENGDE_NAVN AND TransSale.STOERRELSE_NAVN = VAREFRVSTR.STOERRELSE_NAVN
    INNER JOIN VAREFRVSTR_DETAIL ON TransSale.VAREFRVSTRNR = VAREFRVSTR_DETAIL.VAREPLU_ID AND TransSale.FARVE_NAVN = VAREFRVSTR_DETAIL.FARVE_NAVN AND TransSale.LAENGDE_NAVN = VAREFRVSTR_DETAIL.LAENGDE_NAVN AND TransSale.STOERRELSE_NAVN = VAREFRVSTR_DETAIL.STOERRELSE_NAVN AND TransSale.AFDELING_ID = VAREFRVSTR_DETAIL.AFDELING_ID
  WHERE
    TransSale.ART IN (0,1)
    AND TransSale.DATO>=:FROMDATE
    AND TransSale.DATO<=:TODATE
  ORDER BY TransSale.LEVNAVN asc  
  INTO
    :SUPPLIER,
    :ITEMGROUP,
    :DESSIN,
    :BARCODE,
    :EANNUMMER,
    :ITEMCOLOR,
    :ITEMSIZE,
    :SEASON,
    :ITEMNAME,
    :Vnr,
    :Laengde
  do begin
    /* Reset line */
    DEPARTMENTSALES = '';
    
    /*Select all departments and iterate through all of them */
    for select Afdelingsnummer from Afdeling order by Afdelingsnummer Into :AfdNr 
    do begin                                                
      /*For each department fecth ordinary sale to current supplier and item */
      SELECT
        Sum(TransAfd.SALGSTK) AS SStk,
        Sum(TransAfd.SALGKR - TransAfd.MOMSKR) as SSpr 
      FROM
        TRANSAKTIONER TransAfd
      WHERE
        TransAfd.ART IN (0, 1)
        and TransAfd.KUNDESALG=0 
        AND TransAfd.DATO>=:FROMDATE
        AND TransAfd.DATO<=:TODATE
        and TransAfd.AFDELING_ID = :AfdNr
        and TransAfd.LEVNAVN = :Supplier
        and TransAfd.FARVE_NAVN = :ITEMCOLOR
        and TransAfd.STOERRELSE_NAVN = :ITEMSIZE
        and TransAfd.LAENGDE_NAVN = :Laengde
        and TransAfd.VAREFRVSTRNR = :Vnr
      Into
        :SumSaleStk,
        :SumSaleSpr;
        
      if ((SumSaleStk is null)) then                         
        SumSaleStk = 0;
      if ((SumSaleSpr is null)) then                         
        SumSaleSpr = 0;
        
        
      /*For each department fecth WEB sale to current supplier and item */
      SELECT
        Sum(TransAfd.SALGSTK) AS SStk,
        Sum(TransAfd.SALGKR - TransAfd.MOMSKR) as SSpr 
      FROM
        TRANSAKTIONER TransAfd
      WHERE
        TransAfd.ART IN (0, 1)
        and TransAfd.KUNDESALG IN (100,101) 
        AND TransAfd.DATO>=:FROMDATE
        AND TransAfd.DATO<=:TODATE
        and TransAfd.AFDELING_ID = :AfdNr
        and TransAfd.LEVNAVN = :Supplier
        and TransAfd.FARVE_NAVN = :ITEMCOLOR
        and TransAfd.STOERRELSE_NAVN = :ITEMSIZE
        and TransAfd.LAENGDE_NAVN = :Laengde
        and TransAfd.VAREFRVSTRNR = :Vnr
      Into
        :SumWEBStk,
        :SumWEBSpr;
        
      if ((SumWEBStk is null)) then                         
        SumWEBStk = 0;
      if ((SumWEBSpr is null)) then                         
        SumWEBSpr = 0;
        
        
      /*Department had sale and web sale of this item in the periode */
      DEPARTMENTSALES = :DEPARTMENTSALES || :AfdNr || :Delimiter 
                                         || ROUND(:SumSaleSpr,2) || :Delimiter 
                                         || ROUND(:SumSaleStk,2) || :Delimiter 
                                         || ROUND(:SumWEBSpr,2) || :Delimiter 
                                         || ROUND(:SumWEBStk,2) || :Delimiter; 
    end                      
    /*Remove last delimiter*/
    DepartmentSales = LEFT(:DEPARTMENTSALES, CHAR_LENGTH(:DEPARTMENTSALES)-1);
    /*Create export line */  
    TotalLinie = :Dessin || :Delimiter ||
                 :ItemName || :Delimiter ||
                 :EANNummer || :Delimiter ||
                 :ItemGroup || :Delimiter ||
                 :ItemColor || :Delimiter ||
                 :Season || :Delimiter ||
                 :ItemSize || :Delimiter ||
                 :DEPARTMENTSALES;
    suspend;
  end
end^


