    TRANSAKTIONER TransSale
    INNER JOIN VARER ON TransSale.VAREFRVSTRNR = VARER.PLU_NR
    INNER JOIN VAREFRVSTR ON TransSale.VAREFRVSTRNR = VAREFRVSTR.VAREPLU_ID AND TransSale.FARVE_NAVN = VAREFRVSTR.FARVE_NAVN AND TransSale.LAENGDE_NAVN = VAREFRVSTR.LAENGDE_NAVN AND TransSale.STOERRELSE_NAVN = VAREFRVSTR.STOERRELSE_NAVN
    INNER JOIN VAREFRVSTR_DETAIL ON TransSale.VAREFRVSTRNR = VAREFRVSTR_DETAIL.VAREPLU_ID AND TransSale.FARVE_NAVN = VAREFRVSTR_DETAIL.FARVE_NAVN AND TransSale.LAENGDE_NAVN = VAREFRVSTR_DETAIL.LAENGDE_NAVN AND TransSale.STOERRELSE_NAVN = VAREFRVSTR_DETAIL.STOERRELSE_NAVN AND TransSale.AFDELING_ID = VAREFRVSTR_DETAIL.AFDELING_ID
  WHERE
    TransSale.ART IN (0,1)
    AND TransSale.DATO>=:FROMDATE
    AND TransSale.DATO<=:TODATE
  ORDER BY TransSale.LEVNAVN asc  
  INTO
    :SUPPLIER,
    :ITEMGROUP,
    :DESSIN,
    :BARCODE,
    :EANNUMMER,
    :ITEMCOLOR,
    :ITEMSIZE,
    :SEASON,
    :ITEMNAME,
    :Vnr,
    :Laengde
  do begin
    DEPARTMENTSALES = '';
    /*Select all departments and iterate through all of them */
    for select Afdelingsnummer from Afdeling order by Afdelingsnummer Into :AfdNr 
    do begin                                                
      /*For each department fecth ordinary sale to current supplier and item */
      SELECT
        Sum(TransAfd.SALGSTK) AS SStk,
        Sum(TransAfd.SALGKR - TransAfd.MOMSKR) as SSpr, 
        Sum(TransAfd.RABAT - TransAfd.RABAT_MOMS) as SRabat 
      FROM
        TRANSAKTIONER TransAfd
      WHERE
        TransAfd.ART IN (0, 1) 
        AND TransAfd.DATO>=:FROMDATE
        AND TransAfd.DATO<=:TODATE
        and TransAfd.AFDELING_ID = :AfdNr
        and TransAfd.LEVNAVN = :Supplier
        and TransAfd.FARVE_NAVN = :ITEMCOLOR
        and TransAfd.STOERRELSE_NAVN = :ITEMSIZE
        and TransAfd.LAENGDE_NAVN = :Laengde
        and TransAfd.VAREFRVSTRNR = :Vnr   
        and TransAfd.KUNDESALG=0
      Into
        :SumSaleStk,
        :SumSaleSpr,
        :SumSaleRabat;
        
      if ((SumSaleStk is null)) then                         
      begin
        SumSaleStk = 0;
      end                                                      
      if ((SumSaleSpr is null)) then                         
      begin
        SumSaleSpr = 0;
      end                                                      
      if ((SumSaleRabat is null)) then                         
      begin
        SumSaleRabat = 0;
      end                                                      
      
      
      /*For each department WEB fecth sale to current supplier and item */
      SELECT
        Sum(TransAfdWEB.SALGSTK) AS SStk,
        Sum(TransAfdWEB.SALGKR - TransAfdWEB.MOMSKR) as SSpr, 
        Sum(TransAfdWEB.RABAT - TransAfdWEB.RABAT_MOMS) as SRabat 
      FROM
        TRANSAKTIONER TransAfdWEB
      WHERE
        TransAfdWEB.ART IN (0, 1) 
        AND TransAfdWEB.DATO>=:FROMDATE
        AND TransAfdWEB.DATO<=:TODATE
        and TransAfdWEB.AFDELING_ID = :AfdNr
        and TransAfdWEB.LEVNAVN = :Supplier
        and TransAfdWEB.FARVE_NAVN = :ITEMCOLOR
        and TransAfdWEB.STOERRELSE_NAVN = :ITEMSIZE
        and TransAfdWEB.LAENGDE_NAVN = :Laengde
        and TransAfdWEB.VAREFRVSTRNR = :Vnr
        and TransAfdWEB.KundeSalg IN (100,101)
      Into
        :SumWEBStk,
        :SumWEBSpr,
        :SumWEBRabat;
        
      if ((SumWEBStk is null)) then                         
      begin
        SumWEBStk = 0;
      end                                                      
      if ((SumWEBSpr is null)) then                         
      begin
        SumWEBSpr = 0;
      end                                                      
      if ((SumWEBRabat is null)) then                         
      begin
        SumWEBRabat = 0;
      end                                                      
      

      SumTotalStk = :SumSaleStk + :SumWEBStk;
      
      
      /*Fetch stock to current item */
      itemLeveTid = 0;
      Select VAREFRVSTR_DETAIL.ANTALSTK from VAREFRVSTR_DETAIL 
      Where
        VAREFRVSTR_DETAIL.V509INDEX = :BARCODE AND
        VAREFRVSTR_DETAIL.AFDELING_ID = :AfdNr
      INTO
        :ItemStock;


      /*Calculate levetid */
      if (ItemStock<=0) then
      begin 
        /*Stock negative - Levetid is 0 */
        itemLeveTid = 0;
      end
      else
      begin
        /*stock is positive*/      
        if (SumTotalStk<=0) then
        begin                     
          /* Sale is negative and stock is positive - Levetid is 999 */
          itemLeveTid = 999;
        end
        else
        begin             
          itemLeveTid = :ItemStock / :SumTotalStk;
        end
      end       
               
      /*Department had sale of this item in the periode */
      DEPARTMENTSALES = :DEPARTMENTSALES || :AfdNr || :Delimiter 
                                         || ROUND(:SumSaleSpr,2) || :Delimiter 
                                         || ROUND(:SumSaleRabat,2) || :Delimiter 
                                         || ROUND(:SumSaleStk,2) || :Delimiter 
                                         || ROUND(:SumWEBSpr,2) || :Delimiter 
                                         || ROUND(:SumWEBRabat,2) || :Delimiter 
                                         || ROUND(:SumWEBStk,2) || :Delimiter 
                                         || ROUND(:ItemStock,0) || :Delimiter
                                         || ROUND(:ItemLeveTid,2) || :Delimiter;
    end                      
    /*Remove last delimiter*/
    DepartmentSales = LEFT(:DEPARTMENTSALES, CHAR_LENGTH(:DEPARTMENTSALES)-1);
    /*Create export line */  
    TotalLinie = :Dessin || :Delimiter ||
                 :ItemName || :Delimiter ||
                 :EANNummer || :Delimiter ||
                 :ItemGroup || :Delimiter ||
                 :ItemColor || :Delimiter ||
                 :Season || :Delimiter ||
                 :ItemSize || :Delimiter ||
                 :DEPARTMENTSALES;
    suspend;
  end
end^


