CREATE OR ALTER PROCEDURE P_SPLIT_STRING (
    INPUT_STRING VARCHAR(255),
    DELIMITER CHAR(1) = ',' -- Separator (som standard ",")
)
RETURNS (
    SPLIT_VALUE VARCHAR(100))
AS
DECLARE VARIABLE POS INTEGER;
DECLARE VARIABLE TEMP_STRING VARCHAR(255);
BEGIN
  /*
      Author: Mivi
      Date: 04-03-2025
      Purpose:
      This will iterate through a string where we find values (department numbers) divided by a delimiter (,) and return them individually
    */
  TEMP_STRING = INPUT_STRING;

  WHILE (CHAR_LENGTH(TEMP_STRING) > 0) DO
  BEGIN
    POS = POSITION(DELIMITER IN TEMP_STRING);

    IF (POS = 0) THEN
    BEGIN
      -- Hvis der ikke er flere delimiter, er resten sidste v?rdi
      SPLIT_VALUE = TEMP_STRING;
      TEMP_STRING = '';
    END
    ELSE
    BEGIN
      -- Tag v?rdien f?r delimiter
      SPLIT_VALUE = LEFT(TEMP_STRING, POS - 1);
      -- Fjern den udtrukne v?rdi + delimiter fra TEMP_STRING
      TEMP_STRING = SUBSTRING(TEMP_STRING FROM POS + 1);
    END

    -- Returner den fundne v?rdi
    SUSPEND;
  END
END^


